css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("http://forums.shoryuken.com/(?!dashboard/).*") {

/* site background */
html, body {
background-color: #758288 !important;
background-image: none !important;
}

/* white logo */
.Logo {
background: url(http://srktt.com/skin/srk_logo.png) !important;
text-indent: -9999px !important;
}

/* background colors */
div * { background-color: transparent !important; }
.Announcement { background-color: #c0c6cd !important; }
.ButtonBar, .cleditorToolbar, .cleditorMain { background-color: #ffffff !important; }
.Breadcrumbs, .UserQuote, .UserSpoiler, .HasNew, pre { background-color: #9ca4ae !important; }
.Menu .Row { background: url(http://srktt.com/skin/gray_twotone.png) !important; }
.Row, .TextBox, .InputBox, .PopList, .Dropdown, .Flyout, .tipsy { background-color: #d4d7db !important; }

/* smaller fonts */
body, div, h3, .Item, .Title, .Number, .TextBox, .InputBox, .NewCommentCount, .Pager .Highlight {
font-family: "arial", "helvetica", "clean", sans-serif !important;
font-size: 10pt !important;
color: #000000 !important;
}

/* link colors */
a:link, a:hover, .MenuItems a:link { color: #31649B !important; }
a:visited, .MenuItems a:visited, a.Button, a.NavButton { color: #074574 !important; }

/* misc fixes */

#Body .Breadcrumbs span a:after, #Body .Breadcrumbs span a:hover:after { border-left-color: #9ca4ae !important; }
#Header .Navigation > li a, #Header .MeBox .Username { color: #000000 !important; }
h1, h2, .Breadcrumbs a, .Tag, .DataTable thead tr { color: #000000 !important; }
.PostEdited { padding: 0px 10px !important; }
.Promoted { box-shadow: none !important; }
.Signature, .PostEdited { color: #444444 !important; }
.TextBox, .InputBox { border-color: #eeeecc !important; }
.UserSignature { border-top: 1px solid rgba(255, 255, 255, 0.52) !important; }
}
