css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("trademe.co.nz") {

/** A compact Trade Me theme optimised for netbooks **/
/** & tablets with screen resolutions of 1024x600px **/
/** Please note that this CSS is a work in progress **/
/** Restyled by zANavAShi, email: zanavashi@gmx.net **/

/** Step 1: typography resets **/
html {font-size:100% !important;}
html,body {
    font:13px/115% sans-serif !important;
    padding:0 !important;
    margin:0 !important;}
h1,h2,h3 {
    color:rgba(204,102,0,1) !important;
    padding:0 !important;
    margin:3px 0px 6px !important;}
h1 {font:bold 18px/18px monospace !important;
    text-transform:lowercase;}
h1#ListingTitle_title {
    font-family: sans-serif !important;
    text-transform:none;}
h2 {font:bold 15px/15px monospace !important;
    text-transform:lowercase;}
h2#ListingTitle_subtitle {
    font:normal 14px/18px sans-serif !important;
    text-transform:none;}
h3 {font-size:14px !important;}
small {font-size:12px !important;}
img,img a,td img {
    border:none !important;
    padding:0 !important;
    margin:0 !important;}
td,select,input,select.drop-select {
    font:13px/14px sans-serif !important;}
td {padding:1px 4px !important;}
td nowrap {padding:0 !important;}

/** Step 2: nuke big bloated blocks **/
div.sat-nav/*amputate entire top block*/, 
div.site-header h1/*bye-de-bye to kevin*/,
nav.site-header-links,/*navbloat for noddies*/
ul#SiteHeader_SiteTabs_BarOfSearch_watchlistAndFavouritesButtons,
div#QuickBid_Auction/*scroll down you lazy sod*/, 
div#BuyerChecklistDiv/*nfo for newbies only*/,
div#advertSection,div#google-ads/*we already pay you too much*/,
div#SaveToWatchlistRight/*networking for nolifers*/,
div.login-area/*temp till block css completed*/,
div.site-footer/*amputate entire bottom block*/
    {display:none!important;/*die!die!die!*/}

/** Step 3: de-arrange blocks position **/
div.page-body {
    background:#ffffff url("http://www.trademe.co.nz/images/3/common/retina-kev.gif") 
        left -1260px top 70px / 1800px 348px no-repeat fixed !important;
    border-radius:0 !important;
    margin:0 !important;}
div.wrapper {
    width:auto !important;
    background:linear-gradient(90deg,rgba(255,255,255,0.85)5%,rgba(255,255,255,0.92)30%,rgba(255,255,255,1)75%) !important;
    padding:3px !important;
    margin:0 !important;}
div.site-header {
    height:0 !important;
    padding:0 !important;
    margin:0 !important;}
div.site-navigation {
    height:auto !important;
    padding:0 !important;
    margin:0 0 0 150px !important;}
div.main {
    padding:0 !important;
    margin:0 0 0 150px !important;} 
div#SiteHeader_SidebarColumn {
    display:block !important;
    position:fixed !important;
    top:33px; left:4px;
    width:145px !important;
    border:none !important;
    padding:0 !important;
    margin:-7px 0 0 !important;
    overflow:hidden !important;
    z-index:999 !important;}
div.sidebar-column {
    height:auto !important;
    background-color:transparent !important;
    padding:0 !important;
    margin:0 !important;}
div.content {
    padding:0 0 0 4px !important;
    margin:0 !important;}
div.category-listings-breadcrumbs,
div.listingBreadCrumbs {
    margin:6px 0 !important;}

/**    Step 4: compress top search block **/
div.main-search-control input {
    height:18px !important;
    font:normal 13px/13px monospace !important;
    padding:1px 0 0 8px !important;
    margin:0 !important;}
span.drop-label {
    font:normal 12px/20px monospace !important;
    padding: 4px 24px 4px 8px !important;
    margin:0 !important;}
div.main-search-control .main-search-left,
div.main-search-control .main-search-right,
div.main-search-control .drop-container {
    height:18px !important;
    padding:0 !important;
    margin:0 !important;}
div.search-bar {
    height:27px !important;
    background:linear-gradient(180deg, #ffe787, #ffcd07) !important;
    border:1px solid #ffcc00 !important;
    border-radius:5px !important;
    box-shadow:1px 2px 3px gray !important;
    padding:0 !important;
    margin:0 2px 0 0 !important;} 
.search-bar-form {
    line-height:100% !important;
    padding:0 !important;}
form#generalSearch {
    padding:2px !important;
    margin: 0 !important;}
form#generalSearch .btn {
    padding:11px 0 11px !important;
    margin:0 !important;}
span.icon-search-large {
    margin:-10px 10px 10px 10px !important;}
.search-bar-form .field-right button {}
div#search-options-dropdown .btn {
    background:linear-gradient(180deg,#f9c118,#d97200) !important;
    border:1px solid #a85400 !important;
    border-radius:5px !important;
    box-shadow: 0 1px 0 #FFFFFF inset, 1px 2px 3px rgba(0,0,0, 0.5);
    padding:6px 7px 4px !important;
    margin:2px 4px !important;}
input#searchString.default-input.input-main-search.text.ac_input {
    width:300px !important;}

/** Step 5: debloat and float sidebar menu **/
strong#SiteHeader_SideBar_Quicklinks_BuyingText,
strong#SiteHeader_SideBar_Quicklinks_SellingText {
    display:block;
    font:bold 20px/20px monospace !important;
    text-align:center;
    text-transform:lowercase;
    color:#CC6600 !important;
    padding:0 !important;
    margin:2px auto !important;}
div#SiteHeader_SideBar_Quicklinks_QuickLinks {
    background:linear-gradient(180deg,rgba(255,255,193,1)50%,rgba(255,255,193,.1)) !important;
    border:1px solid #ffcc00 !important;
    border-radius:9px;
    padding:0 2px 0 1px !important;}
li.Selling/*reset*/{
    margin:0 !important;}
div.QuickLinks ul/*reset*/,
ul.QuickLinks ul li /*reset*/{
    border:none !important;
    padding:0 !important;
    margin: 0 !important;}
div.QuickLinks li li a {
    display:block !important;
    opacity: 0.9;
    width:auto;height:auto;
    background: linear-gradient(to bottom, #ffcc00, #d97200 80%) !important;
    border:1px solid #A85400 !important;
    border-radius:7px !important;
    box-shadow:1px 2px 3px gray;
    font: bold 12px/12px sans-serif !important;
    text-align:center;
    text-transform:lowercase;
    text-decoration:none !important;
    text-shadow: 0 -1px 0 #8C4600;
    color:white !important;
    white-space: nowrap !important;
    padding:3px 3px 4px !important;
    margin:3px 0 !important;}
div.QuickLinks li li a:hover {
    opacity: 1.0;}

/** Restyle sidebar search block **/
div.breakerBottom {
    padding:0 0 3px 0 !important;}
a.attribute-search-header {
    width:144px !important;
    border-right:1px solid gray !important;
    border-radius: 0 7px 0 0 !important;}
div.attributeSearchInner {
    padding: 1px 0 0 0 !important;
    margin:0 !important;}
div.attribute-search .text {
    font: 11px/14px monospace !important;
    width: 133px !important;
    margin: 4px 1px;} 
div.attribute-search select, 
div.attribute-search input {
    max-width:140px !important;} 
li#SiteHeader_SideBar_AttributeSearch_SearchTabOne, 
li#SiteHeader_SideBar_AttributeSearch_SearchTabTwo {
    width: 70px !important;
    height: 18px !important;}
.search-tabs-holder {
    margin:0 0 -5px !important;
    padding:0 !important;}
.search-tabs li a {
    padding: 4px 0 !important;
    margin:0 !important;}

/** A bitta betta button frillery **/
.btn {/*reset*/
    max-height:18px !important;
    background:linear-gradient(0deg,#f9c118,#d97200);
    border:1px solid #a85400 !important;
    border-radius:5px !important;
    box-shadow:0 1px 0 #FFFFFF inset, 1px 2px 3px rgba(0,0,0, 0.5);
    color:#ffffff !important;
    font:normal 12px/12px monospace !important;
    text-align:center !important;
    text-shadow:0px -1px 0px #8C4600;
    white-space:nowrap;
    padding:0px 2px !important;
    margin:0 !important;}
a.button {
    display:block !important;
/*  background: linear-gradient(to bottom, #ffcc00, #d97200 80%) !important;*/
    background-size:100% !important;
    border:1px solid #A85400 !important;
    border-radius:7px !important;
    font: bold 12px/12px sans-serif !important;
    text-align:center;
    text-transform:lowercase;
    text-decoration:none !important;
    text-shadow:0 -1px 0 #8C4600;
    color:#ffffff !important;
    white-space: nowrap !important;
    padding:3px 3px 4px !important;}
.attribute-search .search-button {
    width:100px !important;
    height:21px !important;
    background-color:yellow;
    background-size:84px 63px;
    background-repeat:no-repeat;
    background-position:50% -1px !important;
    border:1px solid #a85400 !important;
    border-radius:7px !important;
/*  box-shadow: -4px -6px 8px rgba(0,0,0, 0.75) inset, 1px 2px 4px rgba(0,0,0, 0.75); */
    padding:0 !important;
    margin:0 auto 2px !important; }
.attribute-search .search-button:hover {
    background-position:50% -22px !important;}
div.ResetLink {
    display:none !important}
/*button.search-button {
    font:bold 11px/11px monospace !important;
    padding:0 !important;
    margin:0 !important;}*/
button#placeBidButton,
button#buyNowButton {
    width:100px !important;
    height:23px !important;
    background-size: 100px 69px;
    background-position:-1px -2px !important;
    border:1px solid navy !important;
    border-radius:11px !important;
    box-shadow:0 -3px 6px navy inset, 1px 2px 3px rgba(0,0,0, 0.75);
    padding:0 !important;
    margin:0 auto !important; }
button#placeBidButton:hover,
button#buyNowButton:hover {
    background-position:-1px -25px !important;}

/** Move these buttons to top search bar **/
.SaveToWatchlistButton {
    position:fixed !important;
    top:3px !important;
    right:3px !important;
    width:160px !important;
    height:22px !important;
    background-size: 162px 120px !important;
    background-position:-1px -1px !important;
    border:1px solid #a85400 !important;
    border-radius:7px !important;
    box-shadow:0 -8px 15px #f9c118 inset, 0 1px 2px rgba(0,0,0, 0.5);
    padding:0 !important;
    margin:3px 4px !important;
    z-index:6;}
.SaveToWatchlistButton:hover {
    background-position:-1px -25px !important;}
.SaveToWatchlistButton.Saved {
    background-position: 0 -97px !important;}
a#browseRegionLink {
    position:fixed !important;
    top:3px !important;
    right:3px !important;
    width:120px !important;
    height:22px !important;
    background-size: 122px 72px !important;
    background-position:-1px -1px !important;
    border:1px solid #a85400 !important;
    border-radius:7px !important;
    box-shadow:0 -8px 15px #f9c118 inset, 0 1px 2px rgba(0,0,0, 0.5);
    padding:0 !important;
    margin:3px 4px !important;
    z-index:6;}
a#browseRegionink:hover {
    background-position:-1px -25px !important;}
div.region-chooser {
    left:auto !important;
    right:0 !important;
    top:5px !important;}

/**    De-arrange the time-date element **/
div.time { 
    display:block !important;
    position:fixed !important;
    top:0; left:5px;
    width:135px !important;
    opacity: 0.9;
    background: linear-gradient(to bottom, #F9C118, #D97200 85%) !important;
    border:1px solid #A85400 !important;
    border-radius:7px !important;
    box-shadow:1px 2px 3px gray;
    font: bold 12px/12px sans-serif !important;
    text-align:center !important;
    text-transform:lowercase;
    text-decoration:none !important;
    text-shadow: 0 -1px 0 #8C4600;
    color:white !important;
    white-space: nowrap !important;
    padding:3px 3px 4px !important;
    margin:18px 0 0 !important;
    z-index:1000 !important;}

/** Relocate login block - still under construction
div.login-area {
    display:block !important;
    position:fixed !important;
    left:3px;
    bottom:138px;
    width:112px !important;
    height:112px !important;
    border:1px solid rgb(204, 204, 204) !important;
    border-radius: 8px;
    padding:4px 6px 8px !important;
    margin:0 !important;
    z-index:500 !important;}  **/

/**    De-arrange details footer block **/
div#DetailsFooterOptions {
    display:block !important;
    position:fixed !important;
    left:4px; bottom:5px; 
    width:113px !important;
    height:auto !important;
    border-radius:9px;
    padding:2px 14px 4px !important;
    margin:0 !important;
    overflow:hidden !important;
    z-index:500 !important;}
a#DetailsFooter_BuzzyBee:before {
    display:block !important;
    content:url(http://www.trademe.co.nz/Images/Browse/RelatedListingsLarge.gif);
    padding:0 !important;
    margin:0 20px -5px !important;}
a#DetailsFooter_BuzzyBee {
    display:block !important;
    width:80px !important;
    height:40px !important;
    padding:3px 6px !important;
    margin:0 0 0 10px !important;
    overflow:hidden !important;}
a.BuzzyBee {
    font:bold 14px/12px monospace !important;
    text-transform:lowercase !important;
    color:#CC6600 !important;
    white-space:nowrap !important;
    width:auto !important;} 
a.BuzzyBee:hover {
    border:1px solid #CC6600 !important;
    border-radius:7px;
    text-decoration:none !important;
    padding:2px 0 !important;
    margin:-1px !important;}
a.BuzzyBee img {
    display:none !important;}
div.PageCounterContainer {
    font-size:0 !important;
    line-height:0 !important;
    padding:0 !important;
    margin:0 auto !important;}
div#DetailsFooter_PageViewsPanel img {
    width:20px !important;
    height:25px !important;}
div.CommunityWatchLink {
    background:none !important;
    font:13px/13px sans-serif !important;
    padding:0 !important;
    margin:-9px 0 0 -15px !important;} 
div.CommunityWatchLink strong {
    display:none !important;} 

/** Step 6: compress listing whitespace **/
div#ListingTitle_container {
    padding:0 !important;}
li#ListingTitle_listingNumberContainer:before {
    content:"Auction ";}
li#ListingTitle_listingNumberContainer {
    display:block !important;
    position:absolute !important;
    top:-44px;
    right:2px;
    width:282px;
    font:bold 13px/13px monospace !important;
    text-align:center;
    padding:0 !important;
    margin:0 !important;}
ul#ListingTitle_status {
    overflow:visible !important;
    padding:0 !important;
    margin:6px 0 !important;}
ul#ListingTitle_status li {
    border-right:0 !important;}
li.auction-title-bids {
    font-size:14px !important;}
li#ListingTitle_auctionTitleReserve {
    font-size:0 !important;
    margin-left:-10px !important;}
li#ListingTitle_auctionTitleLeading {
    background: linear-gradient(to bottom, #ffe787, #ffcd07) !important;
    border:1px solid #ffcc00 !important;
    border-radius:10px !important;
    box-shadow:1px 2px 4px gray;
    text-shadow: 0 -1px 0 #ffffff;
    font:bold 13px/13px sans-serif !important;
    color:#ff0000 !important;
    text-transform:uppercase;
    padding:2px 10px 0 5px !important;
    margin-top:-2px !important;}
div#ListingMainDetails {
    overflow:visible !important;
    padding:0 !important;}
div.ListingDescription {
    font-size:13px !important;
    padding:0 !important;
    margin:6px 0 0 0 !important;}
p.DescriptionSeeQnA:before {
    content:url("http://www.trademe.co.nz/Images/Listing/bullet.gif");}
p.DescriptionSeeQnA {
    font-size:12px !important;
    margin:13px 0 0 !important;}
div#SaveToWatchlist_SaveToWatchlistArea {
    padding:0 !important;
    margin:3px auto !important;}
div#SaveToWatchlist_EmailReminder,
div#SaveToWatchlist_TextReminder {
    display:none !important;}
.NotificationBox.Highlight {
    border-radius:8px !important;
    padding:6px 8px !important;
    margin:8px auto !important;}
.NotificationBox.Highlight li {
    font:normal 12px/15px monospace !important;}

/** Rework photo display block **/
div#ListingPhotoAndAd,
#advertAndPhoto/*dupe?*/ {/*position*/
    width:auto !important;
    padding:0 !important;
    margin:-52px 2px -4px 12px !important;}
#mainImageTable {/*reset*/
    border:none !important;
    padding:0 !important;
    margin:0 !important;}
td#mainImg {/*resize for small screens*/
    max-width:282px !important;
    height:212px !important;
    vertical-align:top !important;}
td#mainImg a,img#mainImage {
    max-width:282px !important;
    max-height:212px !important;}
td#mainImg,td#mainImg a,img#mainImage {
    background-color:transparent !important;
    border:none !important;
    padding:0 !important;
    margin:0 !important;}
div#pCounter:after {
    content:" (click to view full size)";
    font:normal 10px/11px monospace !important;}
div#pCounter {
    font:bold 11px/11px monospace !important;
    margin:2px auto 0 !important;}
div#contentArea,
div#scroller div { /*reset*/ 
    width:282px !important;
    height:70px !important;
    margin:1px auto 0 !important;}
div#leftGradient,
div#rightGradient {
    height:50px !important;
    width:24px !important;}
div#leftGradient {
    background:linear-gradient(90deg,rgba(255,255,255,1)18px,rgba(255,255,255,0)) !important;;
    padding:18px 8px 0 0 !important;}
div#rightGradient {
    background:linear-gradient(-90deg,rgba(255,255,255,1)18px,rgba(255,255,255,0)) !important;;
    padding:18px 0 0 8px !important;}
ul.thumbs {
    margin:0 auto !important;}
li.thumb {
    padding:0 !important;
    margin:1px 3px !important;}
li.thumb a {
    border:none !important;}
li.selectedThumb {
    box-shadow:1px 2px 4px gray;
    border: 2px solid #FFCC00 !important;}
div.marginTop15{
    display:none !important;}
div#lbOuter {
    left:auto !important;
    right:4px !important;
    width:auto !important;
    height:auto !important;
    margin:-40px 0 0 !important;}
div#lbCounterContainer.Widget {
    display:block !important;}

/**    Compress auction details block **/
div.AuctionSaleDetails {
    width:700px !important;
    border:1px solid #ffcc00 !important;
    border-radius:8px !important;
    line-height:100% !important;
    padding:4px 0px !important;
    margin:0 auto !important;}
div.AuctionSaleDetailsTop,
div.AuctionSaleDetailsRight,
div.AuctionSaleDetailsBottom,
div.AuctionSaleDetailsBottomRight {
    height:auto !important;
    padding:0 !important;
    margin:0 !important;}
div.LeftPadding ul li {
    margin:0 auto 3px !important;}
div#AuctionSaleDetail_MemberText img,
a#AuctionSaleDetail_SellersOtherDetailsLink,
li#AuctionSaleDetail_SafeBuyingOptions {
    display:none !important;}

/**    Compress bid actions block **/
div#bidBuyNowContainer {
    max-width:700px !important;
    background:none !important;
    padding:0 !important;
    margin:8px auto !important;}
div#BidBuyNow_bidBuyNowOr {
    height:34px !important;width:34px !important;
    border:1px solid #ffcc00 !important;
    border-radius:17px !important;}
div#BidBuyNow_bidContainer.actionCol,
div#BidBuyNow_buyNowContainer.actionCol {
    height:128px !important;
    width:340px !important;
    background-color: #ffffc1 !important;
    border:1px solid #ffcc00 !important;
    border-radius:64px;
    padding:0 !important;
    margin:0 !important;}
div.bidBuyNowBody {
    padding:0 !important;}
div.bidBuyNowBody input {
    width:50px !important;
    font:bold 16px/14px sans-serif !important;
    color:rgb(205, 0, 0) !important;
    text-align:right !important;
    padding:0 3px !important;}
div.bidBuyNowBody form ul li div,
p#BidBuyNow_bidNoReserve,
p#BidBuyNow_bidReserveMet {
    font-size:13px !important;
    line-height:13px !important;
    padding:0 !important;
    margin:1px auto !important;}
p#BidBuyNow_bidReserveNotMet {
    padding:0 !important;
    margin:4px 0 !important;}
p#BidBuyNow_closingContainer {
    font-size: 11px !important;
    margin:2px auto 0 !important;}
div.buyNowText {
    width:128px;
    font:bold 20px/26px sans-serif !important;
    margin:14px auto !important;}

/**    Compress bid history block **/
div#bidHistory {
    padding:0 !important;
    margin:8px auto !important;}
p#BidHistory_bidCountMsg {
    font-size: 11px !important;
    margin: 0 auto !important;}
table#BidHistory_bidTable {
    width:480px !important;
    border:1px solid rgb(204, 204, 204) !important;
    border-radius: 8px;
       padding:0 !important;
    margin:4px auto !important;}
table#BidHistory_bidTable td {
    white-space:nowrap !important;
    padding:0px 16px !important;
    border-top:1px dotted #C7C7C7 !important;
    vertical-align:middle !important;
    text-align:right !important;
    font-family:monospace !important;
    font-size:11px !important;} 
table#BidHistory_bidTable tr.topBidRow td {
    border-top:0 !important;
    padding:1px 16px !important;}
table#BidHistory_bidTable td img {
    display:none !important;}

/**    Compress questions block **/
div.AddQuestion,
div.QuestionBlock {
    width:99% !important;
    padding:3px 0px !important;
    margin:0 !important;}
div.QuestionBlock img {
    display:none !important} 

/** General debloating of whitespace **/
/*td[colspan="4"] {
    display:none !important;}*/
table.tabs {
    margin:0 0 -4px !important; }
table.tabs td {
    font:bold 14px/14px monospace !important;
    text-transform:lowercase;
    white-space:nowrap;
    padding:0 !important;}
td.Current span:before {
    content:"Auctions ";}
table.FilterBar {
    margin:-4px 0 -2px !important;}
table.FilterBar tr {
    height:22px !important;}
/*table.auction_item_details td[colspan="3"] img {
    height:5px !important;}*/
div.galleryCard {
    width:32% !important;
    height:320px !important;}
div.galleryCard div {
    line-height:100% !important;
    white-space:nowrap;
    padding:2px 0 !important;
    margin:0 !important;}
td.mtm-item-separator {
    height:1px !important;
    line-height:1px !important;
    background:linear-gradient(180deg,white,white,silver,white,white ) !important;
    border:none !important;
    padding:0 !important;}
div.note_spacer_class,
div.sub-nav-property,
td center small,
tr.padding-for-table-seperators-surronding-rows {
    display:none !important;}

/** Other miscellaneous annoyances **/
div.Widget,
div.actionInnerTLCnr,
div.actionInnerTRCnr,
div.breakerTop, div.icon-key,
div#actionTop, div#actionBottom
    {display:none !important;}
div#GoogleLinks {text-align:center;}
/*****TRADE ME MOTORS*****/
div#SuperFeaturesContainer {
    display:none !important;}

/*TEMP STYLE FOR BLOCK DESIGN*/
/*div#design-mode {
    background:repeating-linear-gradient(45deg,white,white,cyan 5px) !important;
    background:repeating-linear-gradient(135deg,tomato,white,white 5px) !important;}*/
}
