css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(imgur.com) {

/* background */
html, .textbox
{ background: #7A8373 !important; }

body, .arrows, .info.textbox.nobottom, .combobox.inline, .combobox:hover .selection
{ background-color: transparent !important; }

*
{ color: CCFFCC !important;
border-color: #58674B !important; }

/* links color */
a, a:link
{ color: #0B58C0 !important; }

a:not([href="//imgur.com"]):hover
{ background: transparent !important;
text-shadow: 1px 0px 0px !important; }

#topbar, .header.textbox.bold.small-margin-bottom, .textbox.small-margin-bottom, .title-n
{ background: -moz-linear-gradient(25%, #88977A, #C1CAB3, #88977A) !important;
border-bottom-color: #88977A !important; }

.panel
{ background: rgba(140,151,135, .6) !important; }

#topbar li a, #secondary-nav, .sort-options ul li a
{ border-color: #505948 !important; }

input
{ background-color: white !important;
color: black !important; }

.search.placeholder, textarea, INPUT[type="button"]:not([title="Paste URLs from the web"])
{ -moz-appearance: none !important;
background: transparent !important;
box-shadow: inset rgba(0,0,0, .2) 0px 1px 4px 2px!important;
border: 1px solid #937B83 !important; }

#upload-global-buttons span, #upload-global-file-wrapper, #upload-global-clipboard, #upload-global-dragdrop, .button-big.green, #upload-global-file-list td, #upload-global-file-list-header h2, #upload-global-file-list-header span
{ color: 51574C !important; } }
