css:
@-moz-document domain("*.kekomundo.com"), domain("kekomundo.com") {
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(http://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
body{
  	font:13px 'Open Sans' !important;
}
div .inner{
	border-top: none;
}
.forumtitle a img{
  	height: 0 !important;
	width: 0 !important;
	
	padding-left: 273px !important;
	padding-top: 38px !important;
	background: url(http://i.imgur.com/1go5YXd.png) no-repeat !important;
    margin-top: 15px;
}
a.unreadlink{
	font: 13px 'Open Sans' !important;
  	margin-top: 0;
}
a.collapse{
  	margin-top: -8px !important;
}
img#minilogo{
   	height: 0 !important;
    width: 0 !important;
    padding-left: 80px !important;
    padding-top: 34px !important;
    background: url(http://i.imgur.com/UXdtpLg.png) no-repeat !important;
    background-size: contain;
    margin-top: -4px !important;
}
.dropmenu li a{
    border-radius: 3px;
}
h3.catbg{
  	font:15px 'Open Sans' !important;
  	text-transform: uppercase;
  	text-shadow: 0 1px 0 rgba(000,000,000,0.8);
  	padding-top: 8px;
  	padding-left: 3px;
}
h4.titlebg{
  	font:15px 'Open Sans' !important;
  	text-shadow: 0 1px 0 rgba(000,000,000,0.8);
  	padding-top: 8px;
}
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th{
  	font-family:'Open Sans' !important;
    font-size: 13px !important;
  	text-shadow: 0 1px 0 rgba(000,000,000,0.8);
}
h4.titlebg span a img{
	margin-top: 2px !important;
}
p.smalltext{
	font: 12px 'Open Sans' !important;
}
.buttonlist ul li a, .button_submit, .button_reset, input[type=button]{
	font: 12px 'Open Sans' bold !important;
    text-transform: uppercase;
}
div.reply_date{
  text-transform: uppercase;
  text-shadow: 0 1px 0 rgba(255,255,255,0.5);
}
a{
  font-family:'Open Sans' !important;
  text-decoration: none;
  color: #569DB4;
}
a:hover{
  text-decoration: underline;
}
.bbc_standard_quote {
    background: rgba(000,000,000,0.07);
    box-shadow: inset 0 2px 10px rgba(000,000,000,0.3);
    font-size: 11px !important;
  	border-top: none;
  	border-bottom: none;
  	border: 1px solid rgba(000,000,000,0.4) !important;
}
}
