css:
@-moz-document domain("forum.dobreprogramy.pl") {
#branding {min-height: 55px !important;}
#logo img {top: -4px !important;}
#header_bar {top: 10px !important;}
#content {width: 95% !important;}
.maintitle {padding: 3px 4px 3px 10px !important; border: none !important; border-radius: 0 !important; box-shadow: none !important;}
.maintitle .toggle {height: 18px !important; width: 18px !important; margin-top: -1px !important;}
#toggle_sidebar {top: 0 !important; right: -32px !important;}
.category_block > br, #content > br {display: block; margin: 2px 0;}
.breadcrumb {margin: 0 !important; padding: 2px 5px !important;}
.ipsType_pagetitle, .ipsType_subtitle {font-size: 28px !important;}
.ipsFilterbar li.active a, .ipsFilterbar.bar.altbar li.active a {padding: 3px 10px !important;}
.ipsBox, .ipb_table:not(.shoutbox_table) td {padding: 5px !important;}
.ipsPostForm.ipsLayout_withright {padding-right: 256px !important;}
.ipsBox.ipsLayout_tinyleft.ipsLayout_withleft {padding-left: 56px !important;}
.ipsBox_container, .post_block, .ipsLikeButton.ipsLikeButton_enabled, table.ipb_table .preview td, div.bbc_spoiler_wrapper {border: none !important;}
.ipb_table td, .shoutbox_table td {border-bottom: none !important;}
.topic_buttons li.important a, .topic_buttons li.important span,
.ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton, .input_submit {border: none !important; box-shadow: none !important;}
.ipsSideMenu h4 {padding: 6px !important; margin: 0 !important;}

#ajax_loading {left: 47% !important; padding: 0 0 4px !important; width: 6% !important;}
.message {padding: 3px !important;}

div[id^="sigClose_"] {margin-top: 5px !important; right: 25px !important; left: auto !important;}

/*Kolorystyka*/
/*Ogólna*/
/*Font*/
a {color: #94E859 !important;}
.ipsHeaderMenu>.ipsType_sectiontitle {color: #595959 !important;}
.ipsHeaderMenu a {color: #3A7A0B !important;}
#user_link.menu_active, .ipsVerticalTabbed_tabs li.active a {color: #323232 !important;}
body .ipb_table .header a, body .topic_options a {color: #F7F7F7 !important;}
.ipsType_pagedesc, .warn_panel a, .no_messages, .search_msg, #search-box li {color: #cacaca !important;}
.ipsType_pagetitle, .ipsType_subtitle {color: rgba(0, 0, 0, 0.8) !important; text-shadow: 0 1px 0 #797979 !important;}
.desc.lighter.blend_links a:not(.name):not([hovercard-ref="member"]) {color: #858585 !important;}
.pagination .pages a, .pagejump, .desc, .desc.blend_links a:not(.name):not([hovercard-ref="member"]), p.posted_info, .poll_question .votes {color: #999 !important;}
#user_navigation a, #primary_nav a, .maintitle a, .pagination .back a:hover, .pagination .forward a:hover,
 .ipsLikeButton, #primary_nav .active a, .ipsBox_container, .ipsVerticalTabbed_tabs li:not(.active) a:hover,
 .ipsFilterbar li.active a, .col_c_forum>h4>a, .col_f_content>h4>a, .topic_buttons li a, .topic_buttons li span, .ipsButton, .ipsSideMenu ul li.active a {color: #fff !important;}
table.ipb_table td:nth-last-child(2) li, p.posted_info, .breadcrumb, .breadcrumb a, .ipsButton_extra, .topic_buttons li.non_button a,
 .ipsType_sectiontitle, .ipsSideMenu ul li:not(.active) a, #board_stats .value:not(a) {color: #c5c5c5 !important;}
.edit {color: #969696 !important; text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.5) !important;}
/*Tło*/
#content, .topic_buttons li.non_button a, #primary_nav .active a, .ipsBox_container, .ipsBox_container tr:nth-child(2n+1):not(.preview),
 .row1, .post_block.row1, .ipsVerticalTabbed_tabs li {background-color: #535353 !important;}
.ipsBox_container tr:nth-child(2n):not([role="presentation"]):not(.preview), .row2, .post_block.row2, .general_box {background-color: #494949 !important;}
.ipsBox_container>table:not(.shoutbox_table) tr:not(.preview):hover {background-color: #393939 !important;}
.topic_buttons li a, .topic_buttons li span, .ipsButton {background: #292929 !important;}
.topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important {background: #812200 !important;}
 #board_stats .value {background-color: #1a1a1a !important; border: 1px solid #919191 !important;}
.unread .altrow, .unread.altrow, .highlighted, .highlighted .altrow, #conversation .post_block, .ipsSideBlock,
 #folder_list .total, .status_update, .edit_folders, .no_messages, .post span:not(.cke_wrapper)/*, .post_block*/, #attachments li, .post_body>.post>p:not(.citation) {background-color: transparent !important;}
.maintitle, .popupInner h3 {background-color: #3A7A0B !important;}
.ipsBox {background-color: #292929 !important;}
ul.mini_pagination li a {background-color: #2D2D2D !important;}
#more_topics, body#ipboard_body fieldset.submit, body#ipboard_body p.submit {background-color: #2F2F2F !important;}
/*Inne*/
.general_box h3, .bar, .ipsSideMenu h4, .ipsSideBlock h3, .popupInner {background: rgb(65, 65, 65) !important; color: #f7f7f7 !important;}
#content, #profile_background, .maintitle, .popupInner h3 {background-image: none !important;}
#footer_utilities>.left>li>a:not(#mark_all_read), #footer_utilities>.left>li>ul:not(#mark_all_read_menucontent) a {color: #3A7A0B !important;}
#copyright a {color: #848484 !important;}
.ipsVerticalTabbed_tabs > ul, .ipsVerticalTabbed_tabs li {border: 1px solid transparent !important;}
table.ipb_table .preview td {box-shadow: 0 6px 5px -3px rgba(0, 0, 0, 0.4) inset, 0 -6px 5px -3px rgba(0, 0, 0, 0.4) inset !important;}
.post_block.feature_box {background-color: #424741 !important;}
.search_msg {border-color: #777777 !important;}
.input_submit.alt {background: #212121 !important; color: #aaa !important;}
.cke_skin_ips .cke_wrapper.minimized.std {border: none !important;}
/*Posty*/
.post_block h3 {background: rgb(65, 65, 65) !important;}
#ips_Posts {background-color: #595959 !important;}
.ipsBox_container>.post_block:nth-child(2n+1), #topic_summary>div:nth-child(2n+1) {background-color: #535353 !important;}
.ipsBox_container>.post_block:nth-child(2n), #topic_summary>div:nth-child(2n) {background-color: #494949 !important;}
div[id^="newContent"]>.post_block, .poll_question ol, .poll_question h4 {background: transparent !important;}
.post_body .post, #content {color: rgba(243, 243, 243, 0.8) !important;}
.author_info .group_title, .custom_fields .ft, .ipsList_data .row_title {color: #8E8E8E !important;}
.custom_fields, .ipsList_data .row_data {color: #ccc !important;}
ul.post_controls a {color: #fff !important; line-height: 21px !important;}
.ipsButton_secondary:not(#statusSubmitGlobal) {
 -moz-appearance: none !important;
 -webkit-appearance: none !important;
 background: #393939 !important;
 border: none !important;
 border-radius: 2px !important;
 box-shadow: none !important;
 color: #c4c4c4 !important;
}
.ipsButton_secondary:not(#statusSubmitGlobal):hover {color: #fff !important;}
ul.post_controls a:hover {color: #B3FF17 !important;}
.ipsLikeBar_info {background: #818181 !important;}

/*Komentarz*/
.cke_show_borders>blockquote.ipsBlockquote {padding: 0 10px 10px !important;}
cite.ipb {
  border: none !important;
  margin: 0 -10px 8px !important;
  padding: 8px 10px !important;
}

/*Cytaty*/
p.citation, blockquote {border: 1px solid #c0c0c0 !important;}
p.citation {
 border-bottom: none !important;
 background: #444 !important;
 padding: 0 10px !important;
 border-radius: 3px 3px 0 0 !important;
}
blockquote {
 background-color: #ccc !important;
 color: rgba(11, 11, 11, 0.8) !important;
 border-radius: 0 0 3px 3px !important;
 box-shadow: 2px 2px 6px #3A3A3A inset !important;
}
blockquote a {color: #009900 !important;}

/*Ramki kodu*/
pre.prettyprint, code.prettyprint {
 background-color: #c9c9c9 !important;
 color: #000 !important;
 border-radius: 3px !important;
 box-shadow: 2px 2px 6px #3A3A3A inset !important;
}
.post.entry-content pre ol, .post_body pre ol, .as_content pre ol, .comment_content pre ol, ol.linenums {color: #808080 !important;}
pre.prettyprint ol>li:nth-child(2n) {background-color: #c2c2c2 !important;}

/*Shoutbox*/
table[id^="shoutbox"] .row2 td:first-child>a:first-child {color: #9E9DFF !important;}
.shoutbox_text, .shoutbox_text *:not(a) {color: rgba(243, 243, 243, 0.8) !important; background: transparent !important;}


/*Administratorzy*/
a[href$="/2-lisek/"], a[href$="/2-lisek/"]>span,
a[href$="/81907-tomick/"], a[href$="/81907-tomick/"]>span,
span[style*="#FF0000"],
span[style*="#AA0000"] {color: #FF7B7B !important;}

/*Moderatorzy  globalni*/
a[href$="/23-fiesta/"], a[href$="/23-fiesta/"]>span,
a[href$="/5821-areh/"], a[href$="/5821-areh/"]>span,
a[href$="/36263-agaton/"], a[href$="/36263-agaton/"]>span,
a[href$="/40922-rgabrysiak/"], a[href$="/40922-rgabrysiak/"]>span,
a[href$="/47690-roobal/"], a[href$="/47690-roobal/"]>span,
a[href$="/73082-spandaupol/"], a[href$="/73082-spandaupol/"]>span,
a[href$="/121324-semtex/"], a[href$="/121324-semtex/"]>span,
a[href$="/32818-dimatheus/"], a[href$="/32818-dimatheus/"]>span,
span[style*="#008000"],
span[style*="#e4680d"],
span[style*="rgb(0,128,0)"],
span[style*="#006400"],
span[style*="green"] {color: #54A9A4 !important;}

/*Redakcja*/
a[href$="/3-krogulec/"], a[href$="/3-krogulec/"]>span,
a[href$="/4-cebula/"], a[href$="/4-cebula/"]>span,
a[href$="/1654-/"], a[href$="/1654-/"]>span,
a[href$="/29680-docent/"], a[href$="/29680-docent/"]>span,
a[href$="/42304-tomekb/"], a[href$="/42304-tomekb/"]>span,
a[href$="/62454-tomish/"], a[href$="/62454-tomish/"]>span,
span[style*="#5a8d00"] {color: #F8FF00 !important;}

/*Ikony*/
img[src$="icon_users.png"] {background-image: url(data:image/png;base64,) !important;}

/*Oznacz jako przeczytane*/
#mark_all_read {
 transform: rotate(90deg) !important;
 -webkit-transform: rotate(90deg) !important;
 -o-transform: rotate(90deg) !important;
 background-color: #535353 !important;
 position: fixed !important;
 top: 200px !important;
 right: -61px !important;
 margin: 0 !important;
 color: #fff !important;
 padding: 3px 5px !important;
}
/* AGENT_SHEET */
#mark_all_read.menu_active {
 background-color: #393939 !important;
 border: none !important;
 padding: 3px 5px !important;
}
#mark_all_read_menucontent {
 background-color: #494949 !important;
 position: fixed !important;
 top: 154px !important;
 right: 16px !important;
 left: auto !important;
}
#mark_all_read_menucontent a {color: #fff !important;}
#mark_all_read_menucontent, #mark_all_read_menucontent li {border: none !important;}
#mark_all_read_menucontent li:nth-child(2n+1) {background-color: #535353 !important;}
#mark_all_read_menucontent a:hover {background-color: #393939 !important;}

/*tło menusów*/
#forum_filter_menucontent, #filter_answered_menucontent, #search_options_menucontent {background-color: #535353 !important; border: 1px solid #595959 !important;}
#forum_filter_menucontent, #filter_answered_menucontent a:hover {background-color: #393939 !important;}
/*fix'y*/
a[id^="cke_"], a[class^="cke_"] {color: #000 !important;}
.content[style="text-align: center;margin:20px 0;"] {margin: 0 !important; padding: 20px 0 !important;}
.content[style="text-align: center;margin:20px 0;"]>div {margin: 0 !important;}

/*test*/
.cke_show_borders {height: 96% !important; margin: 2px !important;}
}
