css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.maximumpc.com/forums/index.php),

               domain(maximumpc.com) { 

body {

	background-color: #C7C7C7  ! important;

	color: #000000 ! important;

}


font,th,td,p { font-family: Verdana, Arial, Helvetica, sans-serif }


p, td { font-size: 11px ! important; }


hr	{ height: 0px ! important; border: solid #4D4D4D 0px ! important; border-top-width: 1px ! important;}


.bodyline {

	background-color: #00FF00 ! important;

}


.forumline {

	background-color: #FFFFFF ! important;

}


td.row1	{ background-color: #E0E0E0 ! important; }

td.row2	{ background-color: #B0B0B0 ! important; }

td.row3, td.row3Right	{ background-color: #E0E0E0 ! important; }


th {

	color: #CDC9C9 ! important; font-size: 11px ! important; font-weight : bold ! important;

	background-color: #660000 ! important;

}

td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom {

	background-color:#B22222 ! important; font-weight: bold ! important;

}

td.spaceRow {

	background-color: #B22222 ! important; }

.maintitle,h1,h2	{

	font-weight: bold ! important; font-size: 22px ! important; font-family: "Trebuchet MS", Verdana, Arial, Helvetica, sans-serif ! important;

	text-decoration: none ! important; line-height : 120% ! important; color : #45688B ! important;

}


.gen { font-size : 12px ! important; }

.genmed { font-size : 11px ! important; }

.gensmall { font-size : 10px ! important; }

.gen,.genmed,.gensmall { color : #2B4157 ! important; }

a.gen,a.genmed,a.gensmall { color: #2B4157 ! important; text-decoration: none ! important; }

a.gen:hover,a.genmed:hover,a.gensmall:hover	{ color: #45688B ! important; text-decoration: underline ! important; }



.mainmenu	{ font-size : 11px ! important; color : #2B4157 }

a.mainmenu	{ text-decoration: none ! important; color : #2B4157 ! important; }

a.mainmenu:hover{ text-decoration: underline ! important; color : #45688B ! important; }



.cattitle		{ font-weight: bold ! important; font-size: 12px  ! important; letter-spacing: 1px ! important; color : #8B7D7B}

a.cattitle		{ text-decoration: none ! important; color : #141414 ! important; }

a.cattitle:hover{ text-decoration: underline ! important; }



.forumlink		{ font-weight: bold ! important; font-size: 12px ! important; color : #2B4157 ! important; }

a.forumlink 	{ text-decoration: none ! important; color : #2B4157 ! important; }

a.forumlink:hover{ text-decoration: underline ! important; color : #45688B ! important; }



.nav		{ font-weight: bold ! important; font-size: 11px ! important; color : #2B4157 ! important;}

a.nav		{ text-decoration: none ! important; color : #2B4157 ! important; }

a.nav:hover	{ text-decoration: underline ! important; }



.topictitle	{ font-weight: bold ! important; font-size: 11px ! important; color : #2B4157 ! important; }

a.topictitle:link   { text-decoration: none ! important; color : #2B4157 ! important; }

a.topictitle:visited { text-decoration: none ! important; color : #739AC5 ! important; }

a.topictitle:hover	{ text-decoration: underline ! important; color : #45688B ! important; }


.name	{ font-size : 11px ! important; color : #2B4157 ! important;}



.postdetails	{ font-size : 10px ! important; color : #2B4157 ! important; }



.postbody { font-size : 12px ! important; line-height: 150% ! important; }

a.postlink:link	{ text-decoration: none ! important; color : #800000 ! important; }

a.postlink:visited { text-decoration: none ! important; color : #C67171 ! important; }

a.postlink:hover { text-decoration: underline ! important; color : #45688B ! important; }


.signature { font-size : 11px ! important; color : #45688B ! important; }


.code {

	font-family: 'Courier New', Courier, sans-serif ! important; font-size: 11px ! important; color: #45688B ! important;

	background-color: #CDBA96 ! important; border: #45688B ! important; border-style: solid ! important; border-width: 1px ! important;

}



.quote {

	font-family: Verdana, Arial, Helvetica, sans-serif ! important; font-size: 11px ! important; color: #000000 ! important; line-height: 125% ! important;

	background-color: #CBCAB6 ! important; border: #A67D3D ! important; border-style: solid ! important; border-width: 1px ! important;

}


.copyright	{ font-size: 10px ! important; font-family: Verdana, Arial, Helvetica, sans-serif ! important; color: #2B4157 ! important; letter-spacing: -1px ! important;}

a.copyright,a.copyright:visited,a.copyright:active	{ color: #2B4157 ! important; text-decoration: none ! important;}

a.copyright:hover { color: #45688B ! important; text-decoration: underline ! important;}


input,textarea,select {

	color : #2B4157 ! important;

	font: normal 12px Verdana, Arial, Helvetica, sans-serif ! important;

	border-color : #AEC9E4 ! important;

}


input.post, textarea.post {

	background-color : #F5F8FB ! important;

}


select {

	background-color : #F5F8FB ! important;

}


input { text-indent : 2px ! important; }


input.button {

	background-color : #F5F8FB ! important;

	color : #2B4157 ! important;

	font-size: 11px ! important; font-family: Verdana, Arial, Helvetica, sans-serif ! important;

}


input.mainoption {

	background-color : #F5F8FB ! important;

	font-weight : bold ! important;

}

input.liteoption {

	background-color : #F5F8FB ! important;

	font-weight : normal ! important;
}

.helpline { background-color: #E8EFF7 ! important; border-style: none ! important; }

}
