css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("battle.fourmizzz.fr"), regexp("^http://.*\\.fourmizzz\\..*"), regexp("http:\\/\\/\\.[a-z]+\\.fourmizzz\\.fr\\/.*"), regexp("^http://s.*\\.antzzz\\..*"), regexp("http:\\/\\/s\\.[a-z]+\\.antzzz\\.org\\/.*") {

  a[href*="abyssus.games"]
  {
    display: none !important;
  }

  .message hr, .messageForum hr
  {
    -webkit-border-image: -webkit-linear-gradient(right center , transparent 5%, rgba(255, 0, 0, 0.8) 50%, transparent 95%) 10 !important;
    -moz-border-image: -moz-linear-gradient(right center , transparent 5%, rgba(255, 0, 0, 0.8) 50%, transparent 95%) 10 !important;
    -o-border-image: -o-linear-gradient(right center , transparent 5%, rgba(255, 0, 0, 0.8) 50%, transparent 95%) 10 !important;
    border-image: linear-gradient(right center , transparent 5%, rgba(255, 0, 0, 0.8) 50%, transparent 95%) 10 !important;
    border-color: rgba(0, 0, 0, 0.7)  !important;
    background-image: linear-gradient(right center , transparent 5%, rgba(255, 0, 0, 0.8) 50%, transparent 95%) !important;
    width: 90%;
    border-width: 1px !important;
  }
  
  
/* --- changement de grade IG --- */
  #rang label input
  {
    vertical-align: middle !important;
    top: -2px !important;
  }

  #rang label:nth-of-type(2n+1)
  {
    background: #9b9d9c !important;
    border: 1px solid black;
    box-shadow: 1px 1px 5px rgba(0,0,0,0.5)!important;
  }

  #rang label
  {
    width: 200px!important;
    padding: 2px 50px 2px 10px !important;
  }

  #rang label:hover
  {
    background: rgba(200,100,50,0.6) !important;
  }

  #rang label:nth-of-type(2n+1):hover
  {
    background: rgba(50,100,200,0.6) !important;
  }
     
/* --- changement de compte IG --- */ 
  #titre_boite_info.titre_colonne_cliquable
  {
    display: block !important;
    background: transparent !important;
    top: -80px !important;
    z-index: 999 !important;
  }

  #titre_boite_info.titre_colonne_cliquable .titre_ressource
  {
    display: inline-block !important;
    color: transparent !important;
    font-size: 0px !important;
    width: 150px !important;
  }

  #boiteInfo.boite_info div.titre_colonne_cliquable a.titre_ressource
  {
    color: transparent !important;
    font-size: 0px !important;
  }

  #boiteInfo.boite_info div.titre_colonne_cliquable
  {
    background-image: none !important;
  }
/* --- Info général --- */
  html
  {
    background-color: #E7E7E7 !important;
  }

  .bleu a, .bleu
  {
    color: blue !important;
  }

  ::selection
  {
    background-color: #444;
    color: white;
  }

/* Firefox */
  ::-moz-selection
  {
    background-color: #444;
    color: white;
  }

  body
  {
    Margin: 0px !important;
    Padding: 0px !important;
    font-family: comic sans ms !important;
    font-size: 0.9em !important;
    background-position: top center !important;
    background-repeat: repeat !important;
    padding-bottom: 12px!important;
    padding-top: 20px;
    background-color: #E7E7E7 !important;
  }

/* --- Gestion des 3 colonnes --- */
  #centre
  {
    background-color: #E7E7E7 !important;
    -webkit-border-radius: 5px 5px 5px 5px !important;
    border-radius: 5px 5px 5px 5px !important;
    padding: 0 20px 20px 20px;
  }

  #centre img
  {
    max-width: 100%;
 /* les images seront limitées à la largeur de leur parent */;
  }

  #gauche,#droite
  {
    position: fixed;
    top: 0px;
    bottom: 0px;
    width: 250px;
    background-repeat: no-repeat;
    background-color: black;
  }

  #gauche
  {
    left: 0px;
    background-position: top right;
    border-right: solid 2px black;
    background-image: url('http://silas5988.free.fr/fourmizzz/images_skin/background.jpg') !important;
    background-position: top center !important;
    background-repeat: repeat !important;
    left: 0px !important;
    border: none !important;
    background-size: auto !important;
    top: 0px!important;
    background-color: transparent !important;
    -webkit-box-shadow: inset -5px 0px 25px 0px #000;
    -o-box-shadow: inset -5px 0px 25px 0px #000;
    box-shadow: inset -5px 0px 25px 0px #000;
  }

  #droite
  {
    right: 0px;
    display: none;
    background-position: top left;
    border-left: solid 2px black;
    background-image: url('http://silas5988.free.fr/fourmizzz/images_skin/background.jpg') !important;
    background-position: top center !important;
    background-repeat: repeat !important;
    position: fixed;
    top: 0px!important;
    bottom: 0px;
    right: 0px;
    border-left: solid 0px black !important;
    background-color: transparent !important;
    -webkit-box-shadow: inset 5px 0px 25px 0px #000;
    -o-box-shadow: inset 5px 0px 25px 0px #000;
    box-shadow: inset 5px 0px 25px 0px #000;
  }

  #droite:before
  {
    content: "Skin Black-Carbon by Silas88" !important;
    color: white !important;
    text-decoration: none;
    text-align: center!important;
    font-size: 0.9em !important;
    -webkit-border-radius: 5px 5px 5px 5px !important;
    border-radius: 5px 5px 5px 5px !important;
    border: 1px solid #BDBDBD !important;
    box-shadow: 3px 2px 3px black !important;
    padding: 5px;
    background-image: url('http://silas5988.free.fr/images/fond.jpg') !important;
    background-position: top center !important;
    background-repeat: repeat !important;
    margin-left: 10%;
    opacity: 0.85 !important;
  }
	
@media screen and (max-width: 61em)
  {
 /*980px*/
    #centre {margin: 0px 5px 0px 5px;
    padding-top: 53px;
  }

  #gauche
  {
    display: none;
    background-image: none;
  }
}

@media screen and (min-width: 84em)
{
/*1340px*/  #centre
  {
    margin-right: 260px;
  }

  #droite
  {
    display: block;
  }
}

@media screen and (min-width: 97em)
{
/*1550px*/  #centre
  {
    margin: 0px 310px 0px 310px;
  }

  #gauche,#droite
  {
    width: 300px;
  }
}

@media screen and (min-width: 112em)
{
/*1800px*/  #centre
  {
    margin: 0px 410px 0px 410px;
  }

  #gauche,#droite
  {
    width: 400px;
  }
}

/* --- MENU --- */
#menu
{
  position: fixed !important;
  left: 25%!important;
  right: 25%!important;
  background-image: url('http://silas5988.free.fr/images/fond.jpg') !important;
  background-position: top center !important;
  background-repeat: repeat !important;
  -webkit-border-radius: 0px 0px 5px 5px !important;
  border-radius: 0px 0px 5px 5px !important;
  box-shadow: 3px 2px 3px black  !important;
  border-top: none !important;
  margin-top: -2.5px !important;
  color: black !important;
  border: 1px solid #BDBDBD !important;
}

.boutonActif
{
  background: #9B9D9C !important;
  box-shadow: -3px -3px 4px 0px #25261e inset,3px 3px 4px 0px #25261e inset;
}

.boutonActifSansColonne
{
  background: #9B9D9C !important;
  box-shadow: -3px -3px 4px 0px #25261e inset,3px 3px 4px 0px #25261e inset;
}

.menu_colonne li
{
  margin: 2px !important;
  margin-bottom: 8px !important;
}

ul#menuAide.menu_colonne, ul#menuDeconnexion
{
  margin-left: -13px!important;
}

a.boutonChat strong, a.boutonMessagerie strong
{
  color: red !important;
}

.menu_colonne li a:hover
{
  background: #9B9D9C !important;
  -webkit-box-shadow: inset 0 0 1em black;
  box-shadow: inset 0 0 1em black;
  -webkit-border-radius: 5px 5px 5px 5px !important;
  border-radius: 5px 5px 5px 5px !important;
  border: 2px solid #000 !important;
  color: rgba(0 ,0 ,0 ,0.8) !important;
}

ul.menu_colonne
{
  background-image: url('http://silas5988.free.fr/images/fond.jpg') !important;
  background-position: top center !important;
  background-repeat: repeat !important;
  -webkit-border-radius: 0px 0px 5px 5px !important;
  border-radius: 0px 0px 5px 5px !important;
  box-shadow: 3px 2px 3px black  !important;
  border-top: none !important;
  margin-top: 0px !important;
  color: black !important;
  border: 1px solid #BDBDBD !important;
  border-top: 0px solid #BDBDBD !important;
  padding-right: 5px !important;
  padding-left: 5px !important;
  padding-bottom: 3px !important;
}

div#nb_ouvrieres, div#nb_materiaux, div#nb_nourriture, div#quantite_tdc
{
  display: none !important;
}

.menuAccueil
{
  position: absolute !important;
  width: 100%;
  left: 0px !important;
}

@media screen and (min-width: 84em)
{
/*1340px*/  .menuAccueil
  {
    width: 1200px;
    left: 50% !important;
    margin-left: -600px;
  }
}

/* --- boite info --- */
#boiteComptePlus:not(.cadre_zzzelp), #boiteInfo, #menuBoite div#data
{
  left: 15px!important;
  text-align: center!important;
}

@media screen and (max-width: 41em)
{
 /*656px*/  #menuBoite, div#data
  {
    position: absolute !important;
  }
}

@media screen and (max-width: 61em)
{
 /*980px*/  #menuBoite, div#data
  {
    top: -1000px !important;
  }
}

@media screen and (min-width: 97em)
{
/*1550px*/  #boiteComptePlus:not(.cadre_zzzelp), #boiteInfo, #menuBoite div#data
  {
    left: 65px!important;
  }
}

@media screen and (min-width: 112em)
{
/*1800px*/  #boiteComptePlus:not(.cadre_zzzelp), #boiteInfo, #menuBoite div#data
  {
    left: 160px!important;
  }
}

.menu_colonne_volant_contenu
{
  background-image: url('http://silas5988.free.fr/images/fond.jpg') !important;
  background-position: top center !important;
  background-repeat: repeat !important;
  margin-top: 7.8px !important;
  -webkit-border-radius: 0px 0px 5px 5px !important;
  border-radius: 0px 0px 5px 5px !important;
  border: 1px solid #BDBDBD !important;
  border-top: none !important;
  box-shadow: 3px 2px 3px black !important;
}

.contenu_boite_compte_plus
{
  font-size: 0.9em;
  position: absolute;
  top: 40px;
  padding-left: 10px!important;
  padding-bottom: 2px !important;
  padding-top: 2px!important;
  width: 210px !important;
  background-image: url('http://silas5988.free.fr/images/fond.jpg') !important;
  background-position: top center !important;
  background-repeat: repeat !important;
  -webkit-border-radius: 5px 5px 5px 5px !important;
  border-radius: 5px 5px 5px 5px !important;
  border: 1px solid #BDBDBD !important;
  box-shadow: 3px 2px 3px black !important;
  color: #E7E7E7 !important;
  margin-top: -55px !important;
  opacity: 0.85 !important;
}

.titre_compte_plus, .titre_ressource
{
  padding-top: 5px !important;
}

.contenu_boite_info
{
  padding-top: 10px;
  font-size: 0.9em;
  position: absolute;
  top: 25px;
  padding-bottom: 10px;
  background-image: url('http://silas5988.free.fr/images/fond.jpg') !important;
  background-position: top center !important;
  background-repeat: repeat !important;
  width: 220px !important;
  -webkit-border-radius: 5px 5px 5px 5px !important;
  border-radius: 5px 5px 5px 5px !important;
  border: 1px solid #BDBDBD !important;
  box-shadow: 3px 2px 3px black !important;
  margin-top: -20px !important;
  opacity: 0.85 !important;
  color: #BDBDBD !important;
  font-weight: normal;
}

.boite_presentation_contenu
{
  background-image: url('http://silas5988.free.fr/images/fond.jpg') !important;
  background-position: top center !important;
  background-repeat: repeat !important;
  -webkit-border-radius: 0px 0px 5px 5px !important;
  border-radius: 0px 0px 5px 5px !important;
  border: 1px solid #BDBDBD !important;
  border-top: none !important;
  box-shadow: 3px 2px 3px black !important;
}

#boiteComptePlus.boite_compte_plus div.effet_boite div.titre_colonne_cliquable,#boiteComptePlus.boite_compte_plus div.effet_boite div.titre_colonne_cliquable:hover, .boite_connexion_titre, .titre_colonne, #boiteComptePlus.boite_compte_plus.cadre_zzzelp div.titre_colonne_cliquable
{
  display: none !important;
 /*ajout*/;
}

div#menuBoite div#boiteComptePlus.boite_compte_plus div.titre_colonne_cliquable
{
  display: none !important;
}

div#menuBoite > div#boiteComptePlus.boite_compte_plus > div.titre_colonne_cliquable
{
  display: none !important;
}

div.titre_colonne_cliquable
{
  padding: 0px!important;
}

.contenu_boite_compte_plus
{
  padding-right: 5px !important;
  padding-left: 5px !important;
}

.contenu_boite_compte_plus table tbody tr.lien, .contenu_boite_info table tbody tr.lien
{
  height: 20px !important;
}

html body div#centre div#entete div#boite_info.boite_info div.contenu_boite_info table tbody tr.lien td, html body div#centre div#entete div#boite_compte_plus.boite_compte_plus div.contenu_boite_compte_plus table tbody tr.lien td, html body div#boiteInfo.boite_info div.contenu_boite_info table tbody tr.lien td
{
  background-color: transparent !important;
}

.boite_info a, .boite_compte_plus a
{
  color: #BDBDBD !important;
  font-weight: normal;
}

.boite_info a:hover, .boite_compte_plus a:hover
{
  color: black !important;
  text-decoration: none;
}

.lien:hover
{
  color: black !important;
  background-color: #6D6E6E !important;
}

.super_lien_colonne
{
  color: black !important;
  background-color: #6D6E6E !important;
}

.super_lien
{
  background: transparent !important;
  color: #9B9D9C !important;
}

/*  --- taille recherche C+  --- */
div.contenu_boite_compte_plus form input#recherche
{
  width: 150px!important;
  margin-top: 3px !important;
}

/* --- messagerie --- */
.boiteMessagerie
{
  background-color: transparent !important;
  border: none !important;
}

.boiteMessagerie .ligneMessagerie:nth-of-type(2n+1)
{
  background-color: #9B9D9C !important;
}

.messageMessagerie
{
  margin-left: 20px !important;
}

.checkboxMessagerie input
{
  margin-left: -1px!important;
}

.boiteMessagerie form table#TableMessagerie tbody tr th
{
  background-color: #9B9D9C !important;
}

#TableMessagerie tbody tr th
{
  background-color: #9B9D9C !important;
}

#lecture,#importance,#deplacement
{
  border-right: 1px solid rgba(0,0,0,0.25) !important;
}

/* --- page d'armée --- */
.beige4, .ligne_impaire{background-color: #9B9D9C !important;}

#TableMessagerie tr:nth-of-type(4n+4), #tabChoixArmee tr:nth-of-type(2n+1), #TableMessagerie tr:nth-of-type(4n+5){background-color: #9B9D9C !important;}

/* --- importation --- */
.message_voile{background-color: #9B9D9C !important;-webkit-border-radius: 10px 10px 10px 10px !important; border-radius: 10px 10px 10px 10px !important;-webkit-box-shadow:  0px 0px 15px 0px #000 !important;-o-box-shadow:  0px 0px 15px 0px #000 !important;box-shadow:  0px 0px 15px 0px #000 !important;
}

/* --- page colonie --- */
.caseColonie{background-color: #9B9D9C !important;}

/* --- membre alliance c+ --- */
.tab_triable th.sortable, .tab_triable th.sortable-text, .tab_triable th.sortable-date, .tab_triable th.sortable-keep, .tab_triable th.sortable-date-dmy, .tab_triable th.sortable-numeric, .tab_triable th.sortable-currency, .tab_triable th.sortable-sortByTwelveHourTimestamp, .tab_triable th.sortable-sortIPAddress, .tab_triable th.sortable-sortEnglishLonghandDateFormat, .tab_triable th.sortable-sortScientificNotation, .tab_triable th.sortable-sortImage, .tab_triable th.sortable-sortFileSize, .tab_triable th.sortable-sortAlphaNumeric, .tab_triable th.sortable-sortEnglishDateTime{
   background-color: #9B9D9C !important;
   background: transparent !important;
}

/* --- ajout descriptif joueur --- */
div#data:before{content: "Information sur le joueur" !important;color: white !important;text-decoration:underline;text-align: center!important;font-size:0.9em !important;}
#pseudo:before{content: "Pseudo : " !important;color: white !important;font-size:0.9em !important;}
#tag_alliance:before{content: "Alliance : " !important;color: white !important;font-size:0.9em !important;}

div#data{
   width:220px!important;
   height:55px !important;
   text-decoration:none !important;
   z-index: 20 !important;
   position : absolute !important;
   background-image: url('http://silas5988.free.fr/images/fond.jpg') !important;
   background-position: top center !important;
   background-repeat: repeat !important;
   -webkit-border-radius: 5px 5px 5px 5px !important; 
   border-radius: 5px 5px 5px 5px !important; 
   border: 1px solid #BDBDBD !important;
   box-shadow: 3px 2px 3px black !important;
   top: 15px !important;
   text-align: center!important;
   color : #CEFAC5 !important;
   opacity: 0.85 !important; 
   display: block !important;
   left: 0 !important;
   position: fixed !important;
   z-index: 100 !important;
   font-size:0.9em !important;
   height: auto !important;
   padding:2px 0 2px 0 !important;
}
    
#data div#NonLuMess{display : none !important}
#menuBoite {position: fixed;top: 75px !important;}

/* --- page reine-labo-batiment --- */
.ligneAmelioration{background-color: #9B9D9C !important;}
.ligneTableau, .ligneIcone{display: block !important;}

/* --- page ressources --- */

#boite_ouvriere, #boite_tdc{background-color: #9B9D9C !important;border: 1px solid black !important;}
table#boite_ouvriere.boite_amelioration tbody tr td center table{background-color: #9B9D9C !important;border: 1px solid black !important;}

/* --- page envoie de chasse --- */
table#t.simulateur{background-color: transparent !important;border: 0px solid black !important;}

/* --- page alliance --- */
#tabMembresAlliance, .tab_triable, #fd-table-1{background-color: transparent !important;border: 0px solid black !important;}
.diplomatie tbody tr.odd:not([data-rang_zzzelp="1"]){background-color: transparent !important;}
.diplomatie tbody tr[data-rang_zzzelp="1"] td, .diplomatie tbody tr[data-rang_zzzelp="0"] td {text-align: center !important; padding : 0 4px !important;}

/* --- diverse page --- */
.simulateur{background-color: transparent !important;border: 0px solid black !important;}

/* --- page armée --- */
.beige4, .ligne_paire, .alt:not([data-rang_zzzelp="1"]), .tableau_detail_carte, .tableau_diplomatie_carte, .tableau_fourmiliere_carte{background: none  !important;background-color: #9B9D9C !important;}
.simulateur, .simulateur_combat{background-color: #E7E7E7 !important;border: none !important;}

/* --- page fourmilliere --- */

table#message1.boite_amelioration, table#message2.boite_amelioration, table#message3.boite_amelioration, table#message4.boite_amelioration, table#message5.boite_amelioration, table#message6.boite_amelioration, table#message7.boite_amelioration, table#message8.boite_amelioration, table#message9.boite_amelioration, table#message10.boite_amelioration, table#message11.boite_amelioration, table#message12.boite_amelioration, table#message13.boite_amelioration{background-color: #9B9D9C !important;border: 1px solid black !important;}

/* --- page ennemie --- */
#formulairePageEnnemie fieldset{background-color: #9B9D9C !important;}
#tabEnnemie tr:nth-of-type(2n+1), .diplomatie tr:nth-of-type(2n+1):not([data-rang_zzzelp="1"]){background-color: #9B9D9C !important;}
#fieldset_recherche_fourmiliere legend h2.cliquable3:hover, #fieldset_recherche_alliance legend.cliquable3:hover, #fieldset_recherche_interaction legend h2.cliquable3:hover{background: none !important;}
#fieldset_recherche_interaction_bis, #fieldset_recherche_alliance_bis, #fieldset_recherche_fourmiliere_bis{background: #9B9D9C !important;}
#fieldset_recherche_interaction, #fieldset_recherche_alliance, #fieldset_recherche_fourmiliere{background-color: #9B9D9C !important;}


/* --- page commerce --- */
.boite_membre, .TableauxArmees{background: none !important;border: 1px solid black !important;background-color: #9B9D9C !important;}

/* --- taille largeur des zones d'écriture --- */
textarea#message, textarea#message_collectif
{
  min-width: 100% !important;
  box-shadow: 3px 2px 3px black !important;
}

table#TableMessagerie textarea
{
  min-width: 98.5% !important;
  box-shadow: 3px 2px 3px black !important;
}

/* --- boite connexion et info --- */
.boite_connexion_contenu, .boite_presentation_contenu
{
  padding-left: 10px!important;
  padding-bottom: 2px !important;
  padding-top: 2px!important;
  width: 210px !important;
  background-image: url('http://silas5988.free.fr/images/fond.jpg') !important;
  background-position: top center !important;
  background-repeat: repeat !important;
  -webkit-border-radius: 5px 5px 5px 5px !important;
  border-radius: 5px 5px 5px 5px !important;
  border: 1px solid #BDBDBD !important;
  box-shadow: 3px 2px 3px black !important;
  color: #E7E7E7 !important;
  opacity: 0.85 !important;
}

.boite_connexion_titre
{
  display: none !important;
}

.boite_presentation_contenu
{
  margin-top: -20px !important;
}

#boite_statistique
{
  margin-top: -30px !important;
}

/* --- page note c+ --- */
.tableau_ami
{
  border: 1px solid black !important;
  background-color: #9B9D9C !important;
  min-width: 100% !important;
}

.tableau_ami textarea
{
  min-width: 100% !important;
  box-shadow: 3px 2px 3px black !important;
}

#tab_blocnote.beige4
{
