#messagerie #table_liste_conversations tr.contenu_conversation > td{
    background-color :#9b9d9c !important;
    border : 1px solid rgba(0,0,0,0.4) !important;
    /*border-top : none!important;*/
    box-shadow: 2px 5px 5px rgba(0,0,0,0.3) !important;
    transform: scale(1.005) !important;
    animation: 0.4s ease 0s normal forwards 1 running animationFrames;
    transform-origin: 50% 0 ;
   animation-fill-mode:forwards; /*when the spec is finished*/
  -webkit-animation: animationFrames ease 0.4s;
  -webkit-animation-iteration-count: 1;
  -webkit-transform-origin: 50% 0;
  -webkit-animation-fill-mode:forwards; /*Chrome 16+, Safari 4+*/
  -moz-animation: animationFrames ease 0.4s;
  -moz-animation-iteration-count: 1;
  -moz-transform-origin: 50% 0;
  -moz-animation-fill-mode:forwards; /*FF 5+*/
  -o-animation: animationFrames ease 0.4s;
