css:
@namespace url(http://www.w3.org/1999/xhtml);

/* RPOL.net Dark Rainbow Patchwork Style */
/* Style by: adrasteia1 */
/* Web: http://www.roleplayersarchive.net */

@-moz-document regexp("https?://((www\.?)?rpol\.net|rpol.hyphenit.com.au).*") {

    body, th, tr, td, a {
           font-family: Verdana, Geneva, sans-serif !important;
    }
    
    body {
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-dark-rbow-patchwk/smallrainbowbg.jpg') !important;
           background-repeat: repeat !important; 
           color: #ffffff !important;
    }
    
    span.normal_text {
           color: #ffffff !important;
    }
    
    th.center.border_0010 {
           border: none !important;
    }
         
    td.left.message {
           background-color: #2d152e !important;
           border: 1px solid #ffffff !important;
    }
    
    td.left.message td {
           background-color: #2d152e !important;
    }
    
    td.left.message td.quote {
           background-color: #424241 !important;
    }

    
    td.left.message_alt {
           background-color: #301643 !important;
           border: 1px solid #ffffff !important;
    }    
    
    th.left, th.right, th.center {
           border: none !important;
    }
    
    tbody th {
           background-color: #514747 !important;
           border: none !important;
           color: #ffffff !important;
    }
    
    tbody td {
           color: #ffffff !important;
    }
    
     td.left, td.right, td.center {
           color: #ffffff !important;
    }
    
    table.selection_table {
           margin-top: 15px !important;
    }
    
    table.selection_table th {
           background-color: #514747 !important;
    }
    
    table.selection_table th.left, th.center {
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-dark-rbow-patchwk/thback2.jpg') !important;
    }
  
    table.selection_table td {
           background-color: #2d152e !important;   
    }
  
    table.selection_table.round_top th {
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-dark-rbow-patchwk/thback2.jpg') !important;
    }
    
    a, a:link, a:visited, .link-colour, .msg a:hover:before, .msg a:hover:after {
           color: #11e2e4 !important;    
    }
    
    a:hover, a:active, .alink-colour, .msg a:before, .msg a:after, a.button_on  {
           color: #FF3333 !important;
    }
 
    .border {
	   border-color: #000000 !important;
    }
    .info_table {		
	   border: 1px solid #000000 !important;
	   background-color: #272726 !important;
    }
    table.info_box {
	   border: 1px solid #272726 !important;
	   background-color: #424241 !important;
    }
	
    div.info_box {
	   background-color: #424241 !important;
	   color: #ffffff !important;
    }
	
    table.info_table td {
	   background-color: #2d152e !important;
	   color: #ffffff !important;
    }
	
    input.invis.med {
	   color: #ffffff !important;
    }
	
    table.info_table label {
	   color: #ffffff !important;
    }
	
    #header, #footer {
	   border: 2px solid #000000 !important;
    }
   
    #header td {
           background-color: #2d152e !important;
    }
   
    #footer td {
           background-color: #2d152e !important;
    }
   
    .red, .red th, .red td {
           color: #FF3333 !important;
    }
    .blue, .blue th, .blue td {
           color: #3f7ba9 !important;
    }
    
    span.alink-colour {
           background-color: #272726 !important;
    }
        
    .stickyblock {
           background-color: #2d152e !important;
    }
    
    td#stickylist.stickylist {
           background-color: #854188 !important;
    } 
   
   #wrapper:after {
           content:"RPOL.net Dark Rainbow Patchwork Style by adrasteia1" !important;
           font-size: 9px !important;
           float: right !important;
           width: 300px !important;
    }   
    
    legend {
           color: #ffffff !important;
    }  

    #composediv.niftybox table#composetable td.right {
           color: #ffffff !important;
    }

    #composediv.niftybox  table#composetable td.left {
           color: #ffffff !important;
    }

    tbody.info_table td {
           color: #ffffff !important;
           background-color: #272726 !important;
    }

    table.med.center tr.info_table td {
           color: #ffffff !important;
           background-color: #514747 !important;
    }

    tr.info_table td {
           color: #ffffff !important;
           background-color: #514747 !important;
    }

    tbody tr label {
           color: #ffffff !important;
    }
    
    tbody tr td.smc.sm.left {
           color: #ffffff !important;
    }
    
    table.message_table td {
           background-color: #000000 !important;
    }

    td.left.message_alt td.quote {
           background-color: #000000 !important;
    }
    
    #form-menu-container.right #form-menu.message a {
           background-color: #000000 !important;
    }
    
    .niftybox {
           background-color: #514747 !important;
           color: #ffffff !important;
    }

    .niftybox label {
           color: #ffffff !important;
    }
    
    table.sm label {
           color: #ffffff !important;
    }

    select.link-colour {
           background-color: #000000 !important;
    }
    
    select.link-colour option {
           background-color: #000000 !important;
           color: #ffffff !important;
    }
    
    #contents form.form {
           color: #ffffff !important;
    }
}
