css:
@-moz-document domain("pandora.com") {
body {
    font-family: "lucida grande","Open Sans",tahoma,verdana,arial,sans-serif !important;
    background-color: #ffffff;
    color: #89949b;
}



#brandingBar {
    z-index: 9999;
    height: 67px;
    background-position: bottom;
    background-repeat: repeat-x;
    color: white;
    background-image: url(data:image/png;base64,);
}

.middlecolumn > .stationChangeSelectorNoMenu {
    display: none;
}

#brandingBar .leftcolumn {
    position: fixed;
    left: 20px;
    top: 20px;
}

.searchBox {
    position: fixed;
    left: 150px;
    top: 20px;
    z-index: 9999;
    background-color: #ebeff1;
    border-radius: 13px !important;
    padding-left: 30px !important;
    box-shadow: inset 0 0 4px 0 rgba(0,0,0,0.1);
    background-image: url(data:image/png;base64,) !important;
    background-position: 9px 50% !important;
    background-repeat: no-repeat !important;
}

#brandingBar .rightcolumn {
    top: 20px;
    color: #89949b;
    text-shadow: none;
}

#playerBar {
    height: 0;
}

#topnav {
    height: 67px !important;
}

.contentnav {
    display: none;
}



#main {
    margin-top: -50px !important;
}

#mainContainer {
    background: none;
}

.adSupported-layout #adLayout, #mainContentContainer {
    width: 100%;
}

.top {
    display: none;
}

.slidesBackground > img, .slidesBackground > div > img {
    display: none;
}

.home {
    background: none;
}

.stationContent {
    background: #fff;
    position: fixed;
    border-right: 1px solid #ebeff1;
}

#stationList:before {
    content: "Stations";
    position: fixed;
    left: 10px;
    text-transform: uppercase;
    color: #b8c3c9;
    top: 75px;
    font-size: 11px;
}

#stationList {
    padding-top: 20px;
}

.shuffleStationLabel {
    color: #4b5c66;
    text-shadow: none;
    font-size: 13px;
}

.shuffleStationLabelCurrent {
    color: #008fd5;
}

.stationListItem.selected {
    background: none;
    color: #008fd5;
    border-top: none;
}

.stationListItem.selected .stationNameText {
    color: #008fd5;
}

.stationListItem .stationNameText {
    font-family: "lucida grande","Open Sans",tahoma,verdana,arial,sans-serif !important;
    font-size: 13px !important;
}

.stationListItem.selected > #shuffleContainer .stationName {
    padding-top: 0 !important;
}

.stationListItem.selected .options .option {
    background-color: #ebeff1 !important;
}

.stationListItem .stationName {
    text-shadow: none !important;
    padding: 0 !important;
}

.stationName_hover {
    background-color: #ebeff1 !important;
}

#shuffleContainer > div.options, #shuffleTypeLabel {
    background: #ebeff1 !important;
}

#stationSortButtons, #promobox, .platformPromo, #footer {
    display: none !important;
}

#trackInfo .buttons {
    display: none !important;
}

#trackInfo {
    background-color: transparent !important;
    border-bottom: 1px solid #ebeff1 !important;
}

div.heading {
    font-family: "Open Sans",Arial,Helvetica,sans-serif !important;
    text-shadow: 0 1px rgba(255,255,255, 0.5) !important;
    line-height: 26px !important;
    font-size: 20px !important;
    color: #4b5c66 !important;
    font-weight: normal !important;
    -webkit-font-smoothing: antialiased !important;
}

.itemContent {
    color: #4b5c66 !important;
    font-size: 12px !important;
}

#detailContainer {
    background:  !important
    border-left: 1px solid #ebeff1 !important;
}

.trackData a {
    font-family: "Open Sans",Arial,Helvetica,sans-serif !important;
    color: #4b5c66 !important;
}

.trackData .songTitle {
    font-size: 20px !important;
    font-weight: normal !important;
}

.trackData .artistSummary, .trackData .albumTitle {
    font-size: 14px !important;
}



#playbackControl {
    position: fixed;
    bottom: 0px;
    padding: 0;
    margin: 0;
    left: 0;
    width: 100%;
    height: 91px;
    background-image: url(data:image/png;base64,);
}

#playbackControl .buttons {
    left: 66px !important;
    position: fixed !important;
    bottom: 58px !important;
    height: 0px !important;
}

div.progress {
    position: fixed;
    bottom: 26px;
    left: 175px;
}

div.volumePosition {
    position: fixed;
    left: 490px;
}

div.pauseButton, div.skipButton, div.playButton {
    background: none !important;
}

div.thumbUpButton a, div.thumbDownButton a {
    display: none !important;
}

div.thumbUpButton {
    background-image: url(http://www.pandora.com/img/content-area/thumbup-watermark.png) !important;
    width: 40px;
    height: 40px;
    background-size: 20px 25px !important;
    background-repeat: no-repeat !important;
    background-position: 50% 50% !important;
    position: fixed;
    right: 80px;
    bottom: 20px;
}

#playerBar .thumbUpButton.indicator, #playerBar .thumbDownButton.indicator {
    background-color: #4b5c66;
    border-radius: 10px;
}

div.thumbDownButton {
    background-image: url(http://www.pandora.com/img/content-area/thumbdown-watermark.png) !important;
    width: 40px;
    height: 40px;
    background-size: 20px 25px !important;
    background-repeat: no-repeat !important;
    background-position: 50% 50% !important;
    position: fixed;
    right: 30px;
    bottom: 20px;
}

div.playButton a {
    background-image: url(data:image/png;base64,) !important;
    background-position: 50% 50% !important;
}

div.pauseButton a {
    background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACMAAAAjAQMAAAAkFyEaAAAABGdBTUEAALGPC/xhBQAAAAZQTFRFAAAAiZSbOXOcEgAAAAF0Uk5TAEDm2GYAAAATSURBVAjXY2CgBMjzN9CIJB8AACnhCjO1CGzvAAAAAElFTkSuQmCC) !important;
    background-position: 50% 50% !important;
}

div.skipButton a {
    background-image: url(data:image/png;base64,) !important;
    background-position: 50% 50% !important;
    background-repeat: no-repeat !important;
}

div.volumeButton {
    background-image: url(data:image/png;base64,) !important;
    background-position: 50% 50% !important;
    background-repeat: no-repeat !important;
}

.albumArt {
    position: fixed;
    bottom: 20px;
    left: 25px;
    z-index: 9999;
}

#playerBar .highlight {
    height: 0;
    background: none;
}

#playerBar .rightcolumn {
    text-align: left !important;
}

#playerBar .rightcolumn .info {
    position: fixed;
    bottom: 50px;
    left: 85px;
    z-index: 9999;
    color: black;
    text-align: left;
    width: 100%;
}

#playerBar .rightcolumn .info > div {
    float: left !important;
    display: inline-block !important;
    position: relative !important;
    z-index: 9999 !important;
}

#playerBar .playerBarSong {
    display: block !important;
    margin-top: -3px !important;
    font-size: 16px !important;
}

.playerByText {
    margin-top: 0px !important;
    padding-left: 8px !important;
    font-size: 12px !important;
}

.playerBarArtist {
    display: inline-block !important;
    margin-top: -1px !important;
    font-size: 14px !important;
    padding-right: 8px !important;
}

.playerBarAlbum, #playerBar .rightcolumn .info a {
    font-size: 14px !important
    display: inline-block !important;
    margin-top: -3px !important;
}

#playerBar .rightcolumn .info span, .playerBarAlbum, .playerBarArtist, .playerBarSong {
    color: #4b5c66 !important;
    font-weight: normal !important;
    background: none !important;
    text-shadow: none !important;
}
}
