css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soymallorquinista.mforos.com") {
tbody > tr > td div > a > img,embed,iframe,td > img,object {display:none !important;}
br {line-height:0 !important;}
body { background-image:url(http://farm2.static.flickr.com/1439/1020418737_f2e49faa53_o.jpg) !important;font-family:'helvetica neue',arial,helvetica,sans-serif;}
table, table tbody, table tr td{background-color:transparent !important;}
#tabla_titulo_obj {font-family:Century Gothic !important;background-color:transparent !important;color:#500000 !important;-moz-opacity:0.83 !important;color:white !important;text-transform:uppercase !important;}
#tabla_titulo_obj a {color:white !important;font-size:14px !important;}
.tabla_categorias {background-color:white !important;-moz-border-radius-topleft:10px !important;-moz-border-radius-topright:10px !important;font-family:Century Gothic !important;-moz-opacity:0.63 !important;text-transform: uppercase !important}
tr.tabla_categorias td {background-color:transparent !important;color:#500000 !important;padding-left:10px !important;}
.tabla_texto_1, .tabla_sombra_1{ font-family:'helvetica neue',arial,helvetica,sans-serif;}
#Tabla1_obj a:link,#Tabla1_obj a:visited,#Tabla2_obj a:link,#Tabla2_obj a:visited  {color:#ffbbbb !important;font-family:Century Gothic !important;}
#tabla_categorias_obj a {color:#500000 !important;font-family:Century Gothic !important;font-size:18px !important;text-transform: uppercase !important;}
#tabla_categorias_obj {color:#900000 !important;font-family:Century Gothic !important;}
noscript img {display:block !important;color:red !important;}
.contenedor_foro {width:100% !important;}
#ma_toolbar, #ma_toolbar #ma_auxie6_bg, #ma_toolbar .matb_search_form * {background-color:transparent !important;color:red !important;}
#ma_toolbar, #ma_toolbar a.matb_boton:hover, #ma_toolbar a.matb_menuSel, #ma_toolbar div.matb_menu *, #ma_toolbar div.matb_menu div.sepSubMenu {color:red !important;border:0 !important;}
.tabla_texto_1 + .tabla_texto_1 , .tabla_texto_2 + .tabla_texto_2 {background-color:transparent !important;display:none !important;}
.tabla_texto_1 , .tabla_texto_2 {background-color:transparent !important;-moz-opacity:0.88 !important;}
.tabla_texto_1 td ,.tabla_texto_2 td {-moz-border-radius-bottomright:20px !important;background-color:#500000 !important;-moz-opacity:0.98 !important;color:#ffaaaa !important;-moz-border-radius-topright:20px !important}
.tabla_texto_1 td:first-child + td a,.tabla_texto_2 td:first-child + td a{text-transform: uppercase !important;font-size:14px !important;margin-right:15px !important;}
.tabla_texto_1 td:first-child + td a + br,.tabla_texto_2 td:first-child + td a + br {display:none !important;}
.tabla_texto_1 td span ,.tabla_texto_2 td span {font-size:8px !important;}
.tabla_texto_1 td span a,.tabla_texto_2 td span a {font-size:12px !important;font-weight:bold !important;}
.tabla_texto_1 td:first-child,.tabla_texto_2 td:first-child {padding-left:20px  !important;padding-top:10px !important;color:#440000 !important;background-color:#990000 !important;-moz-border-radius-topleft:20px !important;-moz-border-radius-bottomright:0 !important;-moz-border-radius-bottomleft:20px !important;-moz-border-radius-topright:0 !important}
.tabla_texto_1 td:first-child img,.tabla_texto_2 td:first-child img {display:none !important;}
form {padding:15px !important;}
.tabla_texto_1 div, .tabla_texto_2 div {display:none !important;}
.tabla_texto_1 .derecha, .tabla_texto_2 .derecha ,.tabla_texto_1 div:first-child, .tabla_texto_2 div:first-child{ display:block !important;}
.contenedor_foro table:first-child tbody tr:first-child td {background-color:black !important;}
.contenedor_foro table:first-child tbody tr td {-moz-border-radius:20px !important;}
.contenedor_foro table:first-child + br + table tbody tr td {-moz-border-radius:20px !important;}
}
