css:
@-moz-document url-prefix(http://www.vgchat.com/) {


tbody a, .smallfont a { color: #0066CC !important;  /*- #CC1100 !important; */}
.smallfont {color: #424242 !important;}
body {background-color: #f4f4f4 !important;}
.page {background: #ffffff !important;}

.tborder {
	background: #999999 !important;
	color: #999999 !important;
}


td.thead[colspan="8"] { display: none !important; }

tbody td.alt1, tbody td.alt2 { 
padding:2px 2px 2px 2px !important; 
}

tr td.alt2:first-child { 
border-left: 0px !important;
border-bottom:0px !important; }

tr td.alt2:first-child + td.alt1 { 
border-right: 0px !important;
border-bottom:0px !important; 
padding-left: 8px !important;
padding-top: 6px !important;}


tbody td.alt2 img { 
padding: 0px 0px 0px 2px !important; 
position:relative;
top:-1px;
}

td.alt1 div, td.alt1 div + div span:before {
display: inline;
content: " by "
}



td.tbody, tbody, tr, td, span, body {
background-color: #f4f4f4 !important;
border-color: #999999 !important;
}

td.tbody, tbody, tr, td, span, div.navbar, input, div.panel, div.imagebutton, div.vBulletin_editor, legend {
background-color: #ffffff !important;
border-color: #999999 !important;

color: #333333 !important;
}



div[id$="bold"], div[id$="italic"], div[id$="underline"]  {
background-color: #000000 !important;
}

body.wysiwyg { 
color: #333333 !important;
}





.tcat, .tcat span.normal, .tcat a, td.tcat + td, td.tcat + td a, td.tcat + td + td, td.tcat + td + td a, td.tcat + td + td, td.tcat + td + td a, td.tcat + td + td + td, td.tcat + td + td + td span, .tcat span 
{
	background: #FFFFFF url(http://www.1up.com/images/Elements/nav/hover-bg.png) repeat-x top left !important;
	color: #FFFFFF !important;
}

div.post_links font {
color: #000000 !important;
}


td.smallfont a[href^="newthread"] {
background:url("http://www.1up.com/boards/templates/default/images/button_newthread_off.gif") !important;
background-color: #000000 !important; 
padding-bottom: 8px;
position:relative;
top: -9px;
right: -1px
}

td.smallfont a[href^="newreply"] {
background:url("http://www.1up.com/boards/templates/default/images/button_post_off.gif") !important;
background-color: #000000 !important; 
padding-bottom: 8px;
position:relative;
top: -9px;
right: -1px
}


td a img[src="images/buttons/newthread.gif"] {
width: 102px !important;
visibility: hidden;
}

td a img[src="images/buttons/reply.gif"] {
width: 117px !important;
visibility: hidden;
}

td[align="left"] a[href="http://www.vgchat.com/"]:after { content: url(http://i.imgur.com/fnnESJU.png)}

td[align="left"] a[href="http://www.vgchat.com/"] {
position: relative;
left: 0px;
}

td[align="left"] a[href="http://www.vgchat.com/"] img {
width: 0px !important;
visibility: hidden;
}



td.smallfont script { 

background-color: #000000 !important; 
padding-bottom: 8px;

}


table#threadslist { border-spacing: 0px 1px !important; }

table[cellpadding="3"] { border-spacing:0px !important; }

td.thead, th.thead, div.thead, .thead span{ 
padding: 0px 5px 1px 5px !important;
background-color: #eff5ff !important}

img[title="Link"] {display:none;}

.spoiler { background-color: #333333 !important;} 
}
