css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("del.icio.us") {

BODY{
Font-family:"trebuchet ms", tahoma !important;
font-size:15px;
}

#header-l{
background-image:  url("data:image/png;base64,") !important;
background-repeat: no-repeat;
background-position: left bottom;
}

a{color:#754c24 !important}

span.numbox, .selected {border:0;background:#CCC !important}

#main a.tag:hover{background:#CCC !important}

#footer-inner li.first img {display:none !important}
#footer-inner li.first a {
background-image:  url("data:image/png;base64,") ;
background-repeat: no-repeat;
background-position: left center;
padding-left:14px
}

.post{
border-top:1px solid #DDD;
padding:10px 150px 10px 25px !important;
position:relative;
}
.post .desc a{
font-size:12pt;
color:black !important;
padding:0.2em !important;
}
.post .desc a:visited,
.hotlist h4 a:visited{
color:#666 !important;
color: #444 !important;
}
.post .desc a:hover,
.hotlist h4 a:hover{
color:black !important;
background:#CCCCCC;
text-decoration:none !important;
}



.post .meta{
margin-top:3px;
}
.post .meta a{
color:#754c24 !important;
}


.post .private{
display:none;
}
.post .commands{
position:absolute;
top:10px;
right:0;
height:20px !important;
}
.post .commands A,
.post .meta a {
height:20px !important;
padding:2px;
color: #a57c52 !important;
}


.sidebar-inner{
background:none !important;
border:none !important;
border-left:1px solid #EEE !important;
margin-left:20px;
}
#notifybar{
background:#F8FFBF;
margin:0 !important;
padding:3px !important;
color:black !important;
border:none !important;
border-bottom:1px solid black !important;
}

H1{
font-size:28px !important;
font-weight:normal;
}
H1 A{
color:black !important;
}
.pop{
background:none !important;
color:#AAA!important;
}
#header-mini{
background:#F0F0F0 !important;
}

#delForm input{
position:absolute;
top:5px;
right:5px;
background:#FFD110;
padding:3px 30px !important;
border:1px solid orange;
font-weight:bold;
}
#delForm #private{
position:relative !important;
}
#delForm #url, #delForm #tags, #delForm #description, #delForm #notes{
position:relative !important;
font-weight:normal;
border:none !important;
border-bottom:1px solid #DDD !important;
background:none;
padding:4px !important;
font-size:15px;
color:#666;
margin:0px 10px !important;
}
#delForm .rs{
font-size:15px;
vertical-align:bottom;
text-align:left !important;
text-transform: capitalize;
}
#delForm .smaller{
font-size:14px !important;
}
.postui .bundles{
background:#F0F0F0 !important;
margin:0 -15px !important;
}
.postui .bundles LI{
border-top:1px solid #DDD;
margin-top:20px !important;
padding:5px 20px !important;
}
.postui .bundles .label span{
font-weight:normal !important;
text-transform: uppercase;
}
.postui .bundles A{
font-size:15px !important;
text-transform: uppercase;
color:#666 !important;
}
.postui .bundles .selected{
color:red !important;
background:none !important;
}

.bundles .label {color: #444 !important;
letter-spacing: 1px;
font-variant: small-caps;
}

.cloud li.bundle {margin-bottom: 1em !important;}
.cloud .bundles li.options {color: #777 !important;}

div.commands a.edit, div.commands a.edit:visited, div.commands a.rm, div.commands a.rm:visited  {color: #999 !important;}

a.cur  {color: #000 !important; background: #f1f1f1;}

}
