css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kafan.cn") {

/*all*/
a:visited, a:visited *{
    color: #006400 !important;}
a:hover,a:hover *{
    color: #010101 !important;
    text-shadow: 0px 0px .2px #C0C0C0 !important;
    text-decoration: none !important;
    -moz-transition: all 0.4s ease-in-out 0.1s; !important;}
img:hover{
    box-shadow: 0px 0px 4px 4px #0099FF !important;
    -moz-transition: all 0.5s ease-out 0.1s; !important;}
/*page*/
#nv_forum,#nv,#nv li,#nv li.a a, #nv li.a a:hover,
#nv li.hover a:hover,#nv li.hover a,#nv li a{
    background: none !important;}
#nv{
    background-color: #2d2d2d !important;}
#nv a{
    color: #c4c4c4 !important;}
.ptn.xg2 p,.wp.a_t,#bdshare,.scbar_icon_td,.a_pt{
    display: none !important;}
.avatar img{
    padding: 0 !important;}
.quote{
    border: 2px dashed #009933 !important;
    color: #166ADB !important;
    font-size: 17px !important;
    margin: 20px !important;}
font[color="#999999"],.pstatus{
    color: #010101 !important;}
.authi{
    width: 65% !important;
    color: #010101 !important;}
.pattl,.po,.pi,.ratl th,.ratl td,.ratc{
    border: none !important;}
#toptb{
    opacity: 0 !important;
    height: 0px !important;}
#toptb:hover{
    opacity: 0.5 !important;
    height: 100% !important;}
#fastpostmessage{
    font-size: 20px !important;
    color: #166ADB !important;
    line-height: 120% !important;}
#subject{
    font-size: 18px !important;
    color: #166ADB !important;}
#p_btn i,.pls{
    border: none !important;}
.ts{
    font-size: 20px !important;
    font-weight: bold !important;}
.t_f{
    font-size: 18px !important;
    color: #010101 !important;
    background-color: #FAFAFA !important;}
#moderate table,.pls,.wp,.bm.bml.pbn,.bm.bmw,#fastpostmessage,
#subject,#scbar_form,.p_pop.blk.bui,.p_pop.h_pop a,
.bm_c,#typeid_fast_ctrl_menu li,#typeid_fast_ctrl,
#typeid_fast_ctrl_menu,#scbar_type_menu{
    background-color: #eef !important;}
.blockcode{
    font-family: Consolas !important;}
.tl th, .tl td{
    border: none !important;}
.plc{
    background-color: #EFEFFF !important;}
/*home*/
.xg2,#scbar_type,#scbar_txt,.chart{
    color: #010101 !important;}
.bm.lk{
    display: none !important;}
.fl_row td{
    border: none !important;}
/*fixed*/
#pt{
    margin: 0 !important;}
.p_pop, .p_pof, .sllt,.tedt .area{
    padding: 0 !important;}
#ft,.wp.header_gg{
    display: none !important;}
#um > P:last-child{
    position: relative !important;
    left: -25px !important;}
/*u.kafan*/
#nv_home{
    background: none !important;}
.wp{
    width: 99% !important;}
/*plus*/
.p_pop.blk.bui img:hover{
    -moz-transform: rotate(360deg) !important;}
.authi a[href="http://u.kafan.cn/space-uid-840021.html"]:before{
    content: "【萌】" !important;}
.authi a[href="http://u.kafan.cn/space-uid-501901.html"]:before{
    content: "【我爱NS】" !important;}
.authi{
    position: relative !important;
    bottom: 12px !important;}
    
}
