css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
body{background:url('http://tumblr.com/images/background_transparent.png?alpha') top left repeat-x #1a1a1a}h1{color:#1a1a1a}#nav .nav_item:hover{color:rgba(255,255,255,0.9)}#nav .nav_item.active,#nav .nav_item .nav_menu,#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{background:url('http://tumblr.com/images/black_30.png') top left repeat #1a1a1a}#nav .nav_item .nav_menu{border-bottom-color:rgba(0,0,0,0.85)}#nav .nav_item .nav_menu a{color:rgba(255,255,255,0.6);border-top-color:rgba(0,0,0,0.7)}#nav .nav_item .nav_menu a:hover{color:rgba(255,255,255,0.75)}#footer,#footer a{color:rgba(255,255,255,0.3)}body#main_meetups #promo #map{border:2px solid rgba(255,255,255,0.05)}body#main_meetups #promo .map_corner{background:none!important}div.l-header .tab.dragging{background-color:#393939}.overflow_gradient_right{right:0;background:-moz-linear-gradient(35px center,#323232,rgba(50,50,50,.1));background:-webkit-gradient(linear,left top,right top,color-stop(1,rgba(50,50,50)),color-stop(0,rgb(50,50,50,.1)))}.selection_nipple{border-bottom-color:#1a1a1a!important}#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background:#1a1a1a}.no_posts_found,h1.dashboard_header,h1.dashboard_header a,h1.dashboard_header span{color:rgba(255,255,255,0.65)!important}#dashboard_controls{color:rgba(255,255,255,0.55)}#dashboard_controls a,#dashboard_controls .control{color:rgba(255,255,255,0.55);text-shadow:rgba(0,0,0,0.3)}#dashboard_controls a:hover{color:rgba(255,255,255,0.65)}#dashboard_controls .dashboard_controls_arrow_first_page{background-image:url('http://tumblr.com/images/dashboard_controls_arrow_first_page_transparent.png')!important}#dashboard_controls .dashboard_controls_arrow_left{background-image:url('http://tumblr.com/images/dashboard_controls_arrow_left_transparent.png')!important}#dashboard_controls .dashboard_controls_arrow_right{background-image:url('http://tumblr.com/images/dashboard_controls_arrow_right_transparent.png')!important}#return_to_top .return_to_top_icon{background-position:-650px -190px}#footer.fixed{background:transparent;background-image:-webkit-gradient(linear,0% 0,0% 100%,from(transparent),to(rgba(0,0,0,0.6)));background-image:-moz-linear-gradient(top,transparent,rgba(0,0,0,0.6))}#pagination,#pagination a{color:rgba(255,255,255,0.8)}#pagination a{background-color:rgba(0,0,0,0.75)}#pagination a:hover{color:rgba(255,255,255,0.65)}#auto_pagination_loader{background:url('http://tumblr.com/images/auto_pagination_loader_bg_transparent.png') top repeat-x #1a1a1a;border-top-color:rgba(0,0,0,0.75);text-shadow:rgba(0,0,0,0.75) 2px 2px 3px}#left_column #posts .answer_container{background:url('http://tumblr.com/images/white_20.png') 3px 3px repeat #1a1a1a;color:rgba(255,255,255,0.7);border-bottom-color:rgba(0,0,0,0.5)}#left_column #posts .answer_container .answer_avatar{background:rgba(0,0,0,0.2)}#left_column .dashboard_options_form{background-color:rgba(255,255,255,0.046);color:rgba(255,255,255,0.6);border:1px solid #000}#left_column .dashboard_options_form a{color:rgba(255,255,255,0.6)}#left_column .dashboard_options_form .dashboard_options_form_divider{border-bottom:1px solid rgba(255,255,255,0.1);background-color:rgba(0,0,0,0.2)}#left_column .dashboard_options_form .dashboard_options_form_disclaimer,#left_column .dashboard_options_form .dashboard_options_form_disclaimer a{color:rgba(255,255,255,0.3)}.controls_section{border:1px solid #000!important}.controls_section li{border-top:1px solid #000!important}.controls_section .recessed{background:rgba(255,255,255,0.03)!important}.controls_section .recessed:hover{background:rgba(255,255,255,0.04)!important}.controls_section .with_subtitle small,.controls_section li .blog small{color:rgba(255,255,255,0.3)!important}.controls_section .description_and_title .description{color:rgba(255,255,255,0.7)!important}#right_column:after{background:-moz-linear-gradient(top,rgba(26,26,26,1) 0,rgba(26,26,26,0) 100%);background:-webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(26,26,26,1)),color-stop(100%,rgba(26,26,26,0)));background:-webkit-linear-gradient(top,rgba(26,26,26,1) 0,rgba(26,26,26,0) 100%);background:-o-linear-gradient(top,rgba(26,26,26,1) 0,rgba(26,26,26,0) 100%);background:-ms-linear-gradient(top,rgba(26,26,26,1) 0,rgba(26,26,26,0) 100%);background:linear-gradient(top,rgba(26,26,26,1) 0,rgba(26,26,26,0) 100%)}.tagged_takeover #right_column:after{display:none}#right_column .radar .content.is_media{background-position:-1398px 0}#right_column .radar .content.is_media .mask.top{background-position:-932px 0}#right_column .radar .content.is_media .mask.bottom{background-position:-932px -9px}#right_column .radar .content.is_text{background-position:-1631px 0}#right_column .radar .content.is_text .mask.top{background-position:-1165px 0}#right_column .radar .content.is_text .mask.bottom{background-position:-1165px -10px}#right_column .radar .attribution .username{color:rgba(255,255,255,0.7)}#right_column .radar .attribution .label_and_follow{color:rgba(255,255,255,0.4)}#right_column .radar .attribution .label_and_follow a{color:rgba(255,255,255,0.4)}#posts .post .post_avatar{background:#333;border-width:0}#posts .post .permalink{background:url('http://tumblr.com/images/posts/1a1a1a/dogear.png')}#auto_pagination_loader .spinner{background:url('http://tumblr.com/images/standard/1a1a1a/auto_pagination_loader.gif')}#posts .promotion_highlighted .permalink{background-image:url("http://tumblr.com/images/posts/promotion_highlight_sprite.png");background-position:0 -121px;background-repeat:no-repeat}#site_notice{background:#1a1a1a;color:#eee;text-shadow:#222 1px 1px 5px}#site_notice h3{color:#eee}#site_notice small{color:#aaa}#site_notice small a{color:#aaa}#site_notice a{color:#fff;border-bottom:solid 1px #555}#search_field .pill.notice{color:#2b2b2b}#sidebar_notifications:before{background:#1a1a1a}
#logo a { background-image: url(https://secure.assets.tumblr.com/images/logo_katakana.png?5); }
}
