css:
/* i really want this to be global */

.fanat-talks {
	background: none !important;
}

.fanat-talks .fanat-widget,
.fanat-talks .fanat-talks-status-line,
.fanat-talks .reply-form-container {
    margin: 0 0px !important;
}

.fanat-talks .fanat-pre-widget {
	display: none !important;
}
      
.fanat-talks .fanat-widget {
	height: auto !important;
    background: #ddd !important;
    border-bottom: 1px solid #ccc !important;
}
.fanat-talks .fanat-widget .snot {
	position: static !important;
  	height: auto !important;
  	text-align: left !important;
}
.fanat-talks .fanat-widget .link-to-talks {
	width: auto !important;
  	height: auto !important;
}
.fanat-talks .fanat-widget .snot .counter {
  	position: relative !important;
	display: inline-block !important;
	width: auto !important;
  	height: auto !important;
	background: none !important;
	padding: 7px 20px !important;
    color: #444 !important;
    font-size: 12px !important;
}
.fanat-talks .fanat-widget .snot .counter:before {
	position: relative !important;
	display: inline-block;
	top: 0 !important;
	left: 0 !important;
	margin-right: .3em;
	content: "Всего комментариев:" !important;
}
.fanat-talks .fanat-widget .snot .diss {
	display: none !important;
}
.fanat-talks .fanat-talks-status-line {
	padding: 10px 20px 5px !important;
}
.fanat-talks .fanat-talks-status-line p {

}
.fanat-talks .fanat-talks-status-line a.userlink .usericon img {
	margin-top: -4px !important;
	width: 18px !important;
	height: 18px !important;
}
.fanat-talks .reply-form-container {
  	padding: 0 20px !important
}
.fanat-talks .reply-form-wrapper {
	width: 100% !important;
	padding: 0 50px 0 0 !important;
    box-sizing: border-box;
    -moz-box-sizing: border-box; 
    -webkit-box-sizing: border-box;   
}
.fanat-talks .reply-form-wrapper .ta-container .ta-wrap textarea {
	width: 98% !important;
	min-height: 75px !important;
	padding: .7em !important;
  
  	font-size: 12px !important;
  
    background-color: #ffffff;
    border: 1px solid #cccccc;
}
.fanat-talks .reply-form-wrapper .ta-container .ta-wrap textarea:focus {
    border-color: rgba(82, 168, 236, 0.8);
    outline: 0;
    outline: thin dotted \9;
    
  
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 5px rgba(82, 168, 236, 0.6);
       -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 5px rgba(82, 168, 236, 0.6);
            box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 5px rgba(82, 168, 236, 0.6);
}
.fanat-talks .reply-form-wrapper .buttons-wrapper {
	text-align: left !important;
	font-family: tahoma, arial, sans-serif !important;
  	padding: 10px 0 15px !important;
}
.fanat-talks .reply-form-wrapper .buttons-wrapper .reply {
    width: auto !important;
  	font-family: tahoma, arial, sans-serif !important;
  	font-size: 11px !important;
  	background: #6E92B9 !important;
  	text-shadow: none !important;
    line-height: 18px !important;
    padding: 4px 10px !important;
}
.fanat-talks .reply-form-wrapper .buttons-wrapper .reply:hover {
	background: #195786 !important;
}
.fanat-talks .reply-form-wrapper .buttons-wrapper:after {
	content: "или Ctrl+Enter" !important;
	font-size: 11px;
	color: #999;
	margin-left: .7em;
}
.fanat-talks .reply-form-wrapper .buttons-wrapper .reply:after {
	content: "Добавить комментарий" !important;
}
.fanat-talks .reply-form-wrapper .buttons-wrapper .reply span {
	display: none !important;
}
.fanat-talks .reply-form-wrapper .buttons-wrapper .text-below-form {
    float: right !important;
}

.fanat-talks .fanat-talks-flash-message {
    color: #B88B3F !important;
    background: #FCF8E3 !important;
    border: 1px solid #FBEED5 !important;
    border-radius: 4px 4px 4px 4px !important;
    margin: 0 0 5px !important;
    padding: 5px 10px !important;
    text-shadow: 0 1px 0 rgba(255, 255, 255, 0.5) !important;
}

.fanat-talks-posts {
	margin-top: 70px !important;
}
.fanat-talks .fanat-talks-posts.no-padding {
	margin-top: 15px !important;
}
.fanat-talks-posts {
	margin-bottom: 30px !important;
}
.fanat-talks-posts .b-posts-item {
	background-color: #fbfbfb !important;
  	border-top: 0 !important;
	border-bottom: 1px solid #f2f2f2 !important;
	padding: 10px 12px !important;
	margin: 0 10px !important;
  	min-height: 0 !important;
}
.fanat-talks-posts .b-posts-item .reply-form-wrapper {
	margin-top: 10px !important;
}
.fanat-talks-posts .b-posts-item .reply-form-wrapper .buttons-wrapper {
	padding-bottom: 5px !important;
}
.fanat-talks-posts .b-posts-item.item-read {
  filter: alpha(opacity=100) !important;
  opacity: 1 !important;
}
.fanat-talks-posts .b-posts-item.b-single-comment {
	border-bottom-color: #ccc !important;
    background: #E3EFFF !important;
}
.fanat-talks-posts .b-posts-item.b-striped-item-odd {
	background-color: #fff !important;
}
.fanat-talks-posts .b-posts-item.b-striped-item-odd.b-single-comment {
    border-bottom-color: #ccc !important;
    background: #E3EFFF !important;
}
.fanat-talks-posts .b-posts-item .meta {
	margin-left: 0 !important;
        font-family: tahoma, sans-serif !important;
}
.fanat-talks-posts .b-posts-item .meta a {
    color: rgb(111, 136, 172) !important;
}
.fanat-talks-posts .b-posts-item .meta,
.fanat-talks-posts span.username,
.fanat-talks-posts span.in_reply_to_wrap {
	font-size: 0px !important;
}
.fanat-talks-posts span.username .username {
  	color: #446FA8 !important;
	font-family: tahoma, arial !important;
	font-size: 11px !important;
	font-weight: bold !important;
}
.fanat-talks-posts .b-posts-item .meta .username img {
	width: 16px !important;
	height: 16px !important;
	margin-right: 1px !important;
  	margin-top: 2px !important;
}
.fanat-talks-posts .b-posts-item .meta .timestamp {
  	font-family: tahoma, arial, sans-serif !important;
	font-size: 10px !important;
  	margin: 0 10px 0 7px !important;
  	color: #999 !important;
}
.fanat-talks-posts .in_reply_to_wrap {
    background: none !important;
    padding: 3px 0 0 0 !important;
    display: inline-block !important;
}
.fanat-talks-posts .in_reply_to_wrap:before {
	font-size: 11px !important;
	position: relative !important;
	display: inline-block;
	top: 0 !important;
	left: 0 !important;
	margin: 0 3px !important;
	width: 10px !important;
	height: 10px !important;
	content: "" !important;
  	text-indent: 100%;
	opacity: .5;
  	background: url(data:image/png;base64,) 50% 50% no-repeat !important;
}
.fanat-talks-posts .in_reply_to_wrap .in_reply_to {
	font-size: 11px !important;
}
.fanat-talks-posts .in_reply_to_wrap a.userlink {
  	margin: 0 3px !important;
}
.fanat-talks-posts .in_reply_to_wrap a.userlink .usericon img {
	width: 12px !important;
	height: 12px !important;
	margin-right: 1px !important;
  	margin-top: 1px !important;
  	opacity: .75 !important;
}
.fanat-talks-posts .in_reply_to_wrap a.userlink .username {
  	color: #7E86A2 !important;
	font-family: tahoma, arial !important;
	font-size: 11px !important;
  	font-weight: bold !important;
}

.fanat-talks-posts .b-posts-item .b-post-controls {
  	width: 150px !important;
	top: 7px !important;
  	right: 5px !important;
	text-align: right !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls li {
	float: none !important;
	display: inline-block;
	vertical-align: middle !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-icon span.icon {
	top: auto !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-icon span.icon span.text {
	text-align: left !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls a.delete {
  	position: relative !important;
    top: 25px !important;
	right: 2px !important;
	margin: 0 0 0 -20px !important;
  	vertical-align: top !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls a.complain {
	margin-left: 10px !important;
  	vertical-align: top !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls a.reply {
	
}
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-text {
    width: auto !important;
	padding: 0 7px !important;
  	margin-top: 0 !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-text,
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-text .text {
    color: #aaa !important;
  	background: #f4f4f4 !important;
    line-height: 18px !important;
    font-family: 'lucida grande',tahoma,verdana,arial,sans-serif !important;
    font-size: 11px !important;
    text-decoration: none !important;
    text-shadow: none !important;  
}
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-text:hover,
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-text:hover .text {
	color: #fff !important;
  	background: #6288AD !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-text.cancel,
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-text.cancel .text {
	color: #fff !important;
  	background: #C62E20 !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-text.cancel:hover,
.fanat-talks-posts .b-posts-item .b-post-controls li a.button-link-with-text.cancel:hover .text {
	color: #fff !important;
  	background: #A62115 !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls li.complained-post-marker {
	width: 98% !important;
	margin-top: 5px !important;
}
.fanat-talks-posts .b-posts-item .b-post-controls li.complained-post-marker span {
	text-align: center !important;
	background: #d85 !important;
	color: #fff !important;
	border-radius: 3px;
  	padding: 2px 7px !important;
}
.fanat-talks-posts .b-posts-item .body {
  	line-height: 15px !important;
  	white-space: pre-line;
	padding: 6px 40px 2px 0 !important;
	margin: 0 !important;  
}
.fanat-talks .voter {
    top: 9px !important;
    right: 110px !important;
	left: auto !important;
    height: auto !important;
    background: none !important;
    border: 0 !important;
  
  	font-family: tahoma, arial, sans-serif !important;
  	font-size: 11px !important;
}
.fanat-talks .voter .change_rating, .fanat-talks .voter .rating {
    display: inline-block !important;
}
.fanat-talks .voter .rating {
    vertical-align: middle !important;
    font: bold 11px/16px Verdana, Geneva, Arial, Helvetica, sans-serif !important;
    word-spacing: normal !important;
    letter-spacing: normal !important;
	color: #692 !important;
	background: #e8fae8 !important;
    margin: -1px 1px 0 !important;
    padding: 0 5px !important;
  	border-radius: 3px;
}
.fanat-talks .voter .rating[data-rating=""] {
	color: #bbb !important;
  	background: none !important;
}
.fanat-talks .voter .rating[data-rating^="-"] {
	color: #e65 !important;
  	background: #fae8e8 !important;
}
.fanat-talks .voter .change_rating {
	line-height: 8px !important;
  	font-size: 8px !important;
  	font-weight: bold !important;
  	background: none !important;
  	padding: 1px 3px 2px !important;
  	border-radius: 2px;
  	color: #ddd !important;
}
.fanat-talks .voter.voted .change_rating {
	visibility: hidden;
}
.fanat-talks .voter.to_be_voted .change_rating.upvote {
  	color: #390 !important;
}
.fanat-talks .voter.to_be_voted .change_rating.upvote:hover {
	background: #390 !important;
  	color: #fff !important;
}
.fanat-talks .voter.to_be_voted .change_rating.downvote {
	color: #DD1212 !important;
}
.fanat-talks .voter.to_be_voted .change_rating.downvote:hover {
	background: #DD1212 !important;
	color: #fff !important;
}

.fanat-talks-posts .b-unread-hint {
	margin: 0 10px !important;
	background: #D0DCEC !important;
  	color: #5C77A5 !important;
}

.fanat-talks .fanat-talks-pager {
	margin-bottom: 40px;
}
.fanat-talks .page__button {
    width: 60% !important;
    padding-top: 12px;
    padding-bottom: 11px;
	border: 1px solid #eeeddc !important;
	background: #fffeec !important;
	font-weight: normal !important;
	padding: 7px 0 !important;
	margin: 0 auto !important;
}  
.fanat-talks .page__button,
.fanat-talks .page__button .text {
	color: #b95 !important;
}
.fanat-talks .prev-page {
  	left: 20% !important;
	margin: 20px 0 0 !important;
}
.fanat-talks .page__button .icon {
	display: none !important;
}
.fanat-talks .page__button:hover,
.fanat-talks .page__button:hover .text {
    color: #A7833C !important;
    background: #FCF9D1 !important;
}
