css:
/* 
Bugs:
    Bug 0x01: Sidebar have margin-top error when famous youtube accounts
    
Todo:
    Remove annotation bubbles in html 5 player
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://youtube.com"), url-prefix("https://www.youtube.com"), url-prefix("https://youtube.com"), url-prefix("http://www.youtube.com") {
    
    /* Remove header toolbar */
    #yt-masthead-container,
    
    /* Annotations */
    .annotation,
    .annotation-type-image,
    .annotation-shape,
    .annotation-popup-shape,
    .annotation-type-text,
    
    /* Removes logged into google acount 'ticker' */
    #ticker,
    #ticker-inner,
    
    /* Remove subscription box */
    .yt-uix-button-subscription-container,
    
    /* Remove "Buy %s on" */
    #watch-description-extra-info,
    
    /* Remove VEVO banner */
    #player-branded-banner,
    #watch7-branded-banner,
    
    /* Remove video-response list */
    .video-list,

    /* Remove comments */
    #comments-view,
    #watch-discussion,
    
    /* Remove liscense and category information box */
    #watch-description-extras,
    
    /* Remove comment actions */
    .separator,
    .yt-uix-button-group,
    .yt-uix-button-link,
    
    /* Removes login to comment box */
    .comments-post,
    
    /* Removes footer */
    #footer-container,
    
    /* Removes avatars */
    .yt-user-photo,
    
    /* Removes n videos plus seperator between username and n videos */
    .yt-user-separator,
    .yt-user-videos,
    
    /* Remove featured related videos */
    .related-video-featured,
    
    /* Remove add to "watch later" on related videos, remove releated channels and releated playlists */
    .addto-button,
    .related-channel,
    .related-playlist,
        
    /* HTML5 player */
    .html5-watch-later-button,
    .html5-small-player-button,
    .html5-large-player-button,
    .html5-more-info-button,
    .html5-endscreen-content,
    .html5-share-button,
    .html5-annotations-button,

    /* Sidebar */
    #watch7-sidebar,
    #guide-container,
    .watch-sidebar-head,
    #yt-uix-clickcard-card1
    {
        display: none !important;
    }
    
    /* Fix comment action padding */
    .content {
        margin-bottom: 5px !important;
    }
    
    /* Style comment rating */
    .comments-rating-positive {
        font-weight: bold;
        text-decoration: underline;
        margin-right: 10px !important;
    }
    .comments-rating-positive:hover {
        color: #aaa !important;
        cursor: pointer;
        text-decoration: none;
    }
    
    /* Pathetic attempt to center the youtube page */
    #page-container {
        margin: 0 auto !important;
        width: 1135px !important;
        padding-bottom: 0 !important;
    }
    
    /* Show uploaders photo */
    #watch7-user-header .yt-user-photo {
        display: block !important;
    }
    .yt-thumb-48 {
        width: 88px !important;
        margin-bottom: 20px !important;
    }
    #watch7-user-header .yt-thumb img {
        width: 88px !important;
    }

    /* Fix uploader padding */
    #watch7-user-header {
        padding-bottom: 30px !important;
    }

    /* Adjust view info (views, votes) */
    #watch7-views-info {
        margin-top: -10px !important;
    }
    
    /* Fix whitespace when removing featured video */
    /* Bug: 0x01 */
    #watch7-sidebar {
        margin-top: -404px !important;
    }
    
    /* Permanently colors links instead of having to hover the specific area */
    #watch-description a, .comment-list a {
        color: #438BC5 !important;
    }
    
    /* Increase description width */
    #watch-description-clip {
        width: 500px !important;
    }
    
    /* Remove VEVO-banner */
    #player, #watch7-video-container {
        background: none !important;
    }
    
    /* Fixed space above related videos list */
    #watch-related {
        margin-top: 13px !important;
    }
    
    /* Fix related videos when watching playlists */
    .watch-playlist #watch7-sidebar {
        margin-top: 0 !important;
    }
}
