css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

#additional-info {
    position: relative;
    float: left;
}

#license {
    clear: both;
    padding-top: 1em;
}

#main-header {
    background-color: rgba(105, 190, 4, 0.57) !important;
    border-radius: 0!important;
    box-shadow: 5px 5px 2px rgba(0, 0, 0, 0.39), 0 0 10px rgba(27, 103, 123, 0.24) inset;
    padding: 9px !important;
}

#left-sidebar {
    background-color: transparent;
    padding-top: 15px;
}

#left-sidebar > * {
    background-color: rgba(251, 251, 251, 0.83)  !important;
    border-radius: 7px !important;
    box-shadow: 0 -1px 6px rgba(109, 120, 0, 0.45) inset;
    margin: 0 3px 20px  !important;
    padding: 7px !important;
}

#content-wrapper {
    background-color: #F4FBD8;
    box-shadow: 0 6px 13px rgba(0, 0, 0, 0.78);
    margin: 5px;
    padding: 10px 5px;
    width: calc(100% - 14px) !important;
    min-height: 500px;
}

#screenshots {
    background-color: #E0E0E0 !important;
    border: 0 none !important;
    border-radius: 0 !important;
    box-shadow: 0 3px 5px;
}

h2 {
    border-bottom: 2px solid rgba(27, 26, 26, 0.5);
    color: #2E6CC1;
    text-shadow: 2px 2px 5px rgba(0, 0, 0, 0.42);
}
input[type="search"], input[type="text"], input[type="password"], input[type="button"], 
input[type="submit"], input[type="url"], textarea , button, input:not([type])  {
    -moz-appearance: none !important;
}

input[type="search"], input[type="text"], input[type="password"], input[type="url"], input:not([type]), select, textarea {
    background-color:#DADADA!important;
    border:0!important;
    box-shadow: 2px 2px 2px #9D9D9D inset !important;
    padding: 2px 2px !important;
}

#simple-search > input {
    padding: 2px 3px !important;
}

input[type="button"], input[type="submit"], button {
    background-color:#A6A4CB!important;
    border: 0!important;
    box-shadow: 0 0 4px -1px rgba(68, 62, 62, 0.75) inset, 2px 2px 4px -1px #8E8E8E;
    color: white;
    margin: 0;
    padding: 2px;
    text-shadow: 1px 1px black;
}
input[type="button"]:hover, input[type="submit"]:hover, button:hover {
    background-color: #8985CF;
    color:#8B88CB;
}

footer {
    padding-top: 1em;
    text-align: center;
}


.notice {
    background-color: #DAFF1B!important;
    box-shadow: 2px 2px 2px 2px rgba(96, 50, 50, 0.16) inset;
    margin: 0 1em!important;
    padding: 1px 10px!important;
}

a {
    color: #557DBC !important;
}
a:hover {
    color: #C159B9 !important;
}

section {
    width: 95%;
}

section#discussions-area {
    clear: both;
}
}
