css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.makeupalley.),
               domain(www.makeupalley.com) {
 
img[src="/art/logo.gif"] {
margin-left: 258px !important;
background-image: url(http://www.msnbc.msn.com/images/msnbc/logo01.gif);
background-position: top left;
background-repeat: no-repeat;
display:none;
}

/* header with logo & ad */
table[width="100%"] table[width="100%"] tr[bgcolor='#a5b7f1']{
display: none !important;
}



/* tool bar with text in it */

.topmenu, table[bgcolor="#b6f8a5"], td[bgcolor="#ffffdd"]
{
background-color: #293A6C !important;
color: #bbb !important;

}

table[bgcolor="#b6f8a5"]
{
padding:30px 0 30px 120px;
background-image: url(http://www.msnbc.msn.com/images/msnbc/logo01.gif);
background-position: left;
background-repeat: no-repeat;
}

/* search and icon bar */
table[bgcolor="#ffffde"]
{
background-color: white !important;
}

font[color="#c76ad4"]
{
color: grey !important;
}


/* mail change blue */
tr[bgcolor="#a5b7f1"], td[colspan="4"], td[bgcolor="#a5b7f1"], table[bgcolor="#a5b7f1"]
{
background-color: #ccc !important;
}

/* get rid of body icons */
li > a > img[src="/art/board/2.gif"], tr > td > img[src="/art/board/2.gif"] {
display:none;
}

img[src="/art/board/1.gif"], img[src="/art/board/2.gif"], img[src="/art/board/3.gif"], img[src="/art/board/4.gif"], img[src="/art/board/5.gif"], img[src="/art/board/6.gif"], img[src="/art/board/7.gif"], img[src="/art/board/8.gif"], img[src="/art/board/9.gif"], img[src="/art/board/10.gif"], img[src="/art/board/11.gif"], img[src="/art/board/12.gif"], img[src="/art/board/13.gif"]{
display:none;
}

/* changes the selection message to be purple */
span[id="intelliTxt"]
{
color: #666 !important;
}

font[color="#9e4369"]
{
color: #555 !important;
}

/* kill mua cares */
a[href='/cares/'], a[href='/faq/'], a[href='/swap/'], a[href='/diary/']
{
display: none;
}


/* intellitext message & flag */

center > center > p
{
display: none;
}

table[bgcolor="#cecece"]
{
display: none;
}

/* random yellow blocks */

[bgcolor="#ffffde"]
{
background-color: white !important;
}

/* ad remove */
td[rowspan="14"]
{
display: none;

}


}
@-moz-document url(http://www.makeupalley.com/board/default.asp?BoardID=2) {
td[align="left"] center table[width="95%"][bgcolor="#ffffff"][cellpadding="2"][cellspacing="2"][border="0"] 
td font[color="#9e4369"]
{
display:none;

}

table[width="95%"][bgcolor="#ffffff"]
{
display:none;
}



}
