css:
@-moz-document url-prefix("http://www.grafikart.fr/") {
body {
font-family: "ProximaNova", sans-serif;
background-color: rgb(22, 22, 22);
color: rgb(170, 170, 170);

}

tr:nth-child(odd) td {
background-color: rgb(26, 26, 26);
  color: rgb(170, 170, 170);
}
tr:nth-child(even) td {
background-color: rgb(36, 36, 36);
  color: rgb(170, 170, 170);
}
table a {
text-decoration: none;
color: rgb(101, 177, 155);
}
section.breadcrumb {
color: #909090;
background-color: rgb(15, 15, 15);
border-bottom: 1px solid #e7e7e7;
}
section.subhead {
border-bottom: 1px solid #335559;
background:rgb(21, 68, 63);
-webkit-box-shadow: 0px 1px 40px rgba(0,0,0,0.1) inset;
-moz-box-shadow: 0px 1px 40px rgba(0,0,0,0.1) inset;
box-shadow: 0px 1px 40px rgba(0,0,0,0.1) inset;
}
td {
  border: solid 1px rgba(0, 0, 0, 0.36);
}
div.title span {
background-color: rgba(241, 241, 241, 0);
color: rgb(45, 167, 133);

}
.bloc, table, .bloc-content, article.tuto, article.formation, div.theme-content a img, section.post .syntaxhighlighter, section.posts, article.featured .thumb, section.themes article, article.theme.list, aside.explorer, article.facture, #comments .comment.solver {
background-color: rgb(37, 37, 37);
  border: solid 1px rgb(88, 88, 88);
  }
  article.tuto .meta {
background-color: rgb(54, 54, 54);
border-top: 1px dashed rgb(0, 0, 0);
}
  article.tuto h2 {
color: rgb(3, 201, 153);
}
article.featured h2 {
color: rgb(31, 155, 120);
}
h1, h2, h3, h4, h5 {
color: rgb(31, 155, 120);
}
nav.tabs {
background-color: rgb(14, 14, 14);
border-bottom: 1px solid rgb(48, 48, 48);

}
}
