css:
@-moz-document domain("facebook.com") {
body { background-color: #ededed !important; }
.UFIRow { background-color: #eee !important; }

#leftCol { position: fixed !important; }

div#pagelet_bookmark_nav, div#pagelet_bookmark_nav div { display: none; }
div#pagelet_friends_online { display: none; }

div#rightCol { background: #f4f4f4 !important; }
div#pagelet_rhc_ticker { display: none !important; }
div#pagelet_reminders::before { content: "Reminders:"; }
div#pagelet_reminders { padding: 1em; background: #f4f4f4 !important; }
div#pagelet_ego_pane { display: none; }
div#pagelet_rhc_footer { display: none !important; }

.tickerOnTop .rightColumnWrapper { top: 39px !important; }


/* Updates march 2013 */

._3rbg { display: none; }
._493u ._4_7u { float: left; margin: 0; }
._4_7u { border: none; }
._4_7u, ._4_7u ol, ._4_7u ol > li { width: 100% !important; }
html ._4lh .fbTimelineTwoColumn[data-side="l"] .fbTimelineComposerUnit { width: 847px !important; }
._4lh .fbTimelineCapsule .fbTimelineUnit[data-side="l"] .topBorder { background: transparent; }
._4lh .fbTimelineCapsule .fbTimelineUnit[data-side="l"] .bottomBorder { background: transparent; }
._4lh .fbTimelineTwoColumn[data-side="l"] .timelineUnitContainer { width: 823px !important; }
._4lh .fbTimelineTwoColumn[data-side="l"] { border-radius: 0px; box-shadow: 1px 1px 4px rgba(35, 35, 35, 0.45); }
._4lh .fbTimelineUnit[data-side="l"] .fbTimelineFeedbackStub, ._4lh .fbTimelineUnit[data-side="l"] .fbTimelineUFI { width: 847px !important; }
}
