css:
@-moz-document domain("wikipedia.org") {
div#mw-page-base {
  height: 2.625em!important;
}
div#content {
  margin-top: 0px!important;
  margin-left: 0em!important;
  padding-left: 0.5em!important;
  padding-right: 0em!important;
}
div#mw-head div#p-personal {
  top: 0px!important;
  right: 0px!important;
}
div#mw-head div#p-personal ul {
  padding-left: 0px!important;
}
div#mw-head div#p-personal ul li,
div#left-navigation div#p-namespaces ul li,
div#right-navigation div#p-views ul li {
  margin-top: 0em!important;
  margin-bottom: 0px!important;
  font-size: 0.875em!important;
  line-height: 1.5!important;
  height: auto!important;
}
div#mw-head div#p-personal ul li a,
div#mw-head div#p-personal ul li span,
div#left-navigation div#p-namespaces ul li span a,
div#right-navigation div#p-views ul li span a {
  font-size: 1em!important;
  line-height: 1!important;
  height: auto!important;
}
li#pt-createaccount {
  margin-left: 0px!important;
}
li#pt-userpage {
  padding-left: 0px!important;
}
li#pt-login {
  margin-right: 4px!important;
}
div#left-navigation {
  margin-left: 0px!important;
}
div#left-navigation,
div#right-navigation {
  margin-top: 1.3125em!important;
  margin-bottom: 0px!important;
}
div#left-navigation div#p-namespaces,
div#left-navigation div#p-namespaces ul,
div#right-navigation div#p-views,
div#right-navigation div#p-views ul {
  height: auto!important;
}
div#left-navigation div#p-namespaces ul li span a,
div#right-navigation div#p-views ul li span a {
  padding-top: 0px!important;
}
h3#p-cactions-label {
  width: auto!important;
  height: auto!important;
}
h3#p-cactions-label span {
  font-size: .875em!important;
  line-height: 1.5!important;
  padding-top: 0px!important;
  padding-left: .5em!important;
}
h3#p-cactions-label a {
  height: 100%!important;
}
div#p-cactions div.menu {
    font-size: 0.875em!important;
    top: 1.5em!important;
    border-top-width: 1px!important;
}
div#p-cactions div.menu ul li span a {
    font-size: 1em!important;
    line-height: 1.5!important;
    padding: 0px .5em!important;
}
div#p-search {
  margin-left: 0px!important;
}
form#searchform,
div#simpleSearch {
  margin-top: 0px!important;
}
div#simpleSearch {
  height: auto!important;
  border-bottom: 0px!important;
  padding-bottom: 1px!important;
  padding-right: 0px!important;
  width: 25vw!important;
}
input#searchInput {
  padding: 0px!important;
  font-size: .875em!important;
  line-height: 1.5!important;
  margin-bottom: -2px!important;
}
input#searchButton {
  display: none!important;
}
#mw-panel {
  display: none!important;
}
div#footer {
    margin-left: 0px!important;
}
}
