css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://userscripts.org/"),
               url-prefix("https://userscripts.org/"){



BODY{
    min-width:950px!important;
}  

/* フッター */
#footer-content{
    width:100%!important;
}

/* 中央の要素 */
#main,
.user-info,
.admin_section{
    padding-top:50px!important;
    width:90%!important;
}


/* メニューリスト */
.subnav{
    height:30px!important;
    width:100%!important;
    position:absolute;
    left:0;
}
/* メニューリスト外枠 */
.subnav li{
    padding:5px 2px 10px 2px!important;

    border:none!important;
    height:10px!important;
    float:left;
}
/* メニューリスト内枠 */
.subnav a{
    padding:3px 5px 15px 2px!important;
    box-shadow:red 0.5px 2px 1px 0.5px!important;
    margin-top:-5px!important;
    height:5px!important;
    float:left;
}

/* フッター右 */
.col.ra{
    position:absolute;
    right:10px;
}


}/* end */
