css:
@-moz-document domain("userstyles.org") {
body {
    margin: 0;
    text-rendering: optimizeLegibility;
    font-family: "Helvetica","Lato","Segoe UI",sans-serif;
    height: 100%;
    overflow-x: hidden;
  }
  #main-header {
    padding: 5px 10px;
    background: linear-gradient(to bottom, #F5F5F5, #DDD);
    color: #000;
    margin: 0;
    border-radius: 0;
    z-index:100;
    position:fixed;
    width: 100%;
    box-shadow: 0 0 5px rgba(0,0,0,0.5);
    user-select: none;
    -webkit-user-select: none;
    -moz-user-select: none;
  }
  #main-header > h1 {
    font: 700 1em "Lato",sans-serif;
    text-transform: uppercase;
  }
  #main-header a {
    cursor: pointer;
    color: #696969;
    text-decoration: none;
  }
  #main-header a:hover {
    text-decoration: underline;
  }
  nav {
    background-color: rgb(237,238,240) !important;
    margin: 0 !important;
    padding: 2.5em 0 !important;
    border-radius: 0 !important;
    height: 100%;
    position: relative;
    box-shadow: 2px 0 1px #CCC;
  }
  #left-sidebar {
    top: 0;
    height: 100%;
    z-index: 0;
    position: fixed;
    float: left;
    width: 180px;
    margin: 0;
  }
  #search-terms {
    display: block;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    height: 24px;
    border-radius: 12px;
    padding: 2px 8px;
    width: 100%;
    border: 1px solid rgba(0,0,0,0.1);
    box-shadow: 0 1px 2px rgba(0,0,0,0.42) inset, 0 0 3px rgba(0,0,0,0.1);
  }
  #search-terms:focus {
    outline: none;
    box-shadow: 0 1px 2px rgba(0,0,0,0.5) inset, 0 0 3px rgba(0,0,0,0.1);
  }
  #search-submit, #social {
    display: none;
  }
  a {
    text-decoration: none;
  }
  a:hover {
    text-decoration: underline;
  }
  nav form {
    padding: 5px 10px 4em 10px;
  }
  nav li {
    margin: 0 !important;
  }
  nav li > a {
    display: block;
    padding: 5px 10px;
    color: #444 !important;
    border-bottom: 1px solid #DDD;
    cursor: pointer;
    -webkit-transition: 0.2s background;
  }
  nav li:nth-child(2) > a {
    border-top: 1px solid #DDD;
  }
  nav li > a:hover {
    background: #DDD;
    text-decoration: none;
  }
  #show-advanced-search { display: none; }
  #content-wrapper {
    margin: 26px 10px;
  }
  #main-article {
    margin-top: 1em;
  }
  .listing-no-screenshot {
    border-radius: 0 !important;
  }
  .install-info {
    border-radius: 13px;
    text-shadow: 0 0 1px rgba(0,0,0,0.25);
    color: #FFF;
    background: linear-gradient(#DDD,#AAA);
    padding: 5px 10px;
    font-size: 1em;
  }
  .install {
    border-radius: 13px;
    text-shadow: 0 0 1px rgba(0,0,0,0.25);
    color: #FFF;
    background-color: rgb(92, 170, 41);
    background-image: -moz-linear-gradient(rgb(152, 218, 80), rgb(92, 170, 41));
    background-image: -webkit-linear-gradient(rgb(152, 218, 80), rgb(92, 170, 41));
    background-image: -o-linear-gradient(rgb(152, 218, 80), rgb(92, 170, 41));
    background-image: linear-gradient(rgb(152, 218, 80), rgb(92, 170, 41));
    padding: 5px 10px;
    font-size: 1em;
  }
  #post-install-ad {
    display: none;
  }
  .install-symbol {
    display: none;
  }
  .install-note {
    display: none;
  }
  #switch-browser-note {
    margin-top: 1em;
  }
}

@-moz-document regexp(".*userstyles.org/users/.*") {
#main-article > ul {
    list-style-type: none;
    margin: 1.5em 0;
    padding: 0;
}
#main-article >ul > li > a {
    background: linear-gradient(#FAFAFA,#DDD);
    color: #000 !important;
    border: 1px solid #CCC;
    border-radius: 13px;
    padding: 5px 10px;
}
#main-article > ul > li {
    display: inline-block;
}
}
