css:
/* HipChat Dark Theme
 * Description: Inspired by Vibrant Ink Theme for Notepad++
 * Version: 1.0.4 (1/13/2014)
 * https://github.com/bpruitt-goddard/HipChat-Dark
 * Author: Brian Pruitt-Goddard
 */

 @-moz-document domain("hipchat.com") {

/* General Chats */
#chats .chatBlock {
  color: white !important;
}

#chats .dateDivider {
  background: #474747 !important;
  color: green !important;
}

#chats .chatBlock a, body.chat #profile a {
  color: orange !important;
}

#chats .historyLink {
  background: #474747 !important;
}

#chats .historyLink a {
  color: orange !important;
}

body.chat #autocomplete {
  background: #474747 !important;
}

body.chat #autocomplete div.member {
  color: white !important;
}

.chatState.chatBlock.infoBlock {
  background: black !important;
}

/* Left Chat Panel */
body.chat .logo, body.chat #tabs, body.chat li {
  background: #333333 !important; 
}

body.chat li.selected {
  background: #999999 !important;
}

body.chat li.selected a {
  color: black !important;
}

body.chat #status_ui {
  background: #1C1C1C !important;
}

body.chat #status_chooser div {
  background-color: #999999 !important;
  color: black !important;
}

body.chat #status_chooser div:hover {
  background-color: green !important;
  color: black !important;
}


/*My Chats */
#chats .me, #chats .me .nameBlock {
background: #373737 !important;
}
#chats .me .nameBlock p {
  color: orange !important;
}

/* Other's Chats */
#chats .them, #chats .them .nameBlock {
  background: black !important;
}

#chats .them .nameBlock p {
  color: #94A317 !important;
}

/* Profile Side Panel */
body.chat #profile {
  background: black !important;
  color: white !important;
}

body.chat #profile div.name {
  color: green !important;
}

/* Message Input */
#message_input, .textwrapper, #chats div.chat_display {
  background: #373737 !important;
  color: white !important;
}

/* Room Chats */
#chats .atTag {
  background: green !important;
  border: 1px solid green !important;
}

#chats .atTagMe{
  background: #326CA6 !important;
  border: 1px solid #326CA6 !important;
}

#roster div.list, #roster div.member {
  background-color: black !important;
}

#roster div.member.away, #roster div.member.xa {
  color: #EA8611 !important;
}

#roster div.member.dnd {
  color: #EA1C10 !important;
}

#roster div.member.unavailable {
  color: #999999 !important;
}

#roster div.member, #roster div.member.available {
  color: #B2E020 !important;
}

/* Bot Messages */
#chats .systemMessage .messageBlock, #chats .systemMessage .nameBlock {
  color: white !important;
  background: #75510F !important;
}

#chats .systemMessage .nameBlock p {
  color: #F34DEE !important;
}

#chats .systemMessage-green .messageBlock, #chats .systemMessage-green .nameBlock {
  color: white !important;
  background: green !important;
}

#chats .systemMessage-green .nameBlock p {
  color: white !important;
}

#chats .systemMessage-red .messageBlock, #chats .systemMessage-red .nameBlock {
  color: white !important;
  background: #EA1C10 !important;
}

#chats .systemMessage-red .nameBlock p {
  color: white !important;
}

/* Welcome Message */
#chats div.welcomeMessage td.nameBlock {
  background-color: #426A0F !important;
}

#chats td.nameBlock p {
  color: white !important;
}

#chats div.welcomeMessage td.messageBlock {
  background-color: #538513 !important;
}

/* Lobby */
body.chat #lobby {
  background: black !important;
}

body.chat #lobby .rooms_group {
  background: #474747 !important;
}

body.chat #lobby .lobby_display a {
  color: #326CA6 !important;
}

body.chat #lobby h1, body.chat #lobby h2, body.chat #lobby h2 a {
  text-shadow: none !important;
}

}
