/* +++ changelog +++
Oct 11, 2017
- works on https pages
April 16, 2014
- works on print pages too
April 19, 2013
- don't assume www in url
- re-hides "PIPELINE STORIES"
March 9th, 2013
- original release
*/

@-moz-document domain("anandtech.com") {

/* "Header Ad" */
.header_ad { display: none !important; }

/* DailyTechNews */
.dailytech_news { display: none !important; }

/* FaceBook */
.facebook_like, a[href*="facebook.com"], #facebook, iframe[src*="facebook.com"] { display: none !important; }

/* Twitter */
.twitter, a[href*="twitter.com"] { display: none !important; }

/* Hide Ghostery Google+ And Twitter buttons */
img[src*="chrome-extension://mlomiejdfkolichcflejclcbmpeaniij/"] { display: none !important; }

/* Hide privacyBadger buttons */
img[src*="chrome-extension://pkehgijcmpdhfbdbbnkijodmdjhbjlgp/"], .privacyBadgerReplacementButton { display: none !important; }

/* "featured_banner" */
.featured_banner { display: none !important; }

/* "Ads" */
.featured_banner, embed[id*="Advertisement"], img[src*="ai.php"] { display: none !important; }

/* "Social Buttons" */
a[class*="google"] { display: none !important; }
a[class*="facebook"] { display: none !important; }
a[class*="twitter"] { display: none !important; }
a[class*="mail"] { display: none !important; }
a[class*="post"] { display: none !important; }

/* "MORE PIPELINE STORIES" */
.pipeline, .pipeline_stories { display: none !important; }

/* "sponsored" */
div[class*="sponsored"] { display: none !important; }

/* "Bench" */
.bench_box { display: none !important; }

/* Header */
.header { display: none !important; }

/* Footer */
.footer { display: none !important; }

/* Comments */
#comments, a[href*="#comments"], #icAddComment, .blog_top_right { display: none !important; }

}

@-moz-document url-prefix(http://www.anandtech.com/show/), url-prefix(http://anandtech.com/show/), url-prefix(https://www.anandtech.com/show/), url-prefix(https://anandtech.com/show/) {

/* Header */
.header { display: none !important; }

/* "BreadCrumb" */
.breadcrumb_area { display: none !important; }

/* SideBar */
.sidebar { display: none !important; }

}

@-moz-document url-prefix(http://www.anandtech.com/print/), url-prefix(http://anandtech.com/print/), url-prefix(https://www.anandtech.com/print/), url-prefix(https://anandtech.com/print/) {

/* Header */
header[class*="header noprint"] { display: none !important; }

/* "Bread Crumb" */
div[class*="breadcrumb_area noprint"] { display: none !important; }

}
