* {color: #665E55 !important; font-family:'arial' !important;}
BODY {background: #665E55 !important;
background-repeat:repeat !important;
background-position: center top !important;
background-attachment:fixed !important;
}

.name, .details {text-transform: capitalize !important;}

a:link, a:active, a:visited {color: #90B5A2 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}
a:hover {color: #665E55 !important; text-decoration:none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}

hr {background: #F2E9C6 !important; }

header {background:#A4CEB9 !important;border-bottom: 1px dashed #D9EEDA !important; /*[[Topbar]]*/

section, #content > div {background: #FDFCE5 !important; border: grey; }

#tabs li, #content > div > div {background: #FDFCE5 !important; border: 1px solid #F2E9C6 !important; }
#itemDetails {border: none !important; }

img:hover {opacity:0.6 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}   

.heading td, .grad, #forums td {background:#FDFCE5 !important; }

/* Menu de gauche */
h4 {border: none !important;background: #A4CEB9 !important; color:white !important;
padding: 5px !important; text-shadow: 0px 0px 3px white; letter-spacing: 2px;
text-transform: uppercase; font-size: 8px !important;}
.toggler {color: white !important; font-size: 8px !important;}

aside > div > ul > li {background: #FDFCE5 !important;}
aside > div > ul > li:hover {
background: -moz-linear-gradient(left, #F2E9C6, #A4CEB9) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left, #F2E9C6, #A4CEB9) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left, #F2E9C6, #A4CEB9) repeat scroll 0 0 transparent !important;}
aside > div > ul {border: none !important;}


h3, h2 {background: #A4CEB9 !important; background-image: url('http://img15.hostingpics.net/pics/536156pattern.jpg') !important; color:white !important; border: none !important; 
padding: 7px 7px 7px 15px !important; text-shadow: 0px 0px 3px white; letter-spacing: 3px;
text-transform: uppercase; font-size: 10px !important; margin-top: 22px !important;}
h3 a:link, h2 a:link, th a:link, h3 a:visited, h2 a:visited, th a:visited {color:white !important; 
font-size: 9px !important; letter-spacing: 1px;}

th {background: #A4CEB9 !important; border: none !important;}


input, select, textarea, button, option {background: #F2E9C6 !important;}


/* Boutons barre du haut */
#realms > li > a > img, .buttons > li > img, .buttons > span > li > a > img {
background: #A4CEB9 !important; border: none !important; }

#realms img:hover, .buttons img:hover, #realms .active img {box-shadow: 0 1px 5px #665E55 inset !important;}

#options {background: #A4CEB9 !important; border: none !important;}

#instant {border: #F2E9C6 !important;}


.spoiler > .content {background: #F2E9C6 !important; padding:10px!important;}

#compatibility .bar, .bar {
background: -moz-linear-gradient(left top , #F2E9C6, #A4CEB9) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left top , #F2E9C6, #A4CEB9) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left top , #F2E9C6, #A4CEB9) repeat scroll 0 0 transparent !important;}

.pieLabelBackground, .pieLabel {background: #FDFCE5 !important;}

.synopsis img {box-shadow:none !important; border: none !important;}

.comments > div > div:nth-of-type(odd) {background: transparent !important; border-bottom: 1px solid #F2E9C6 !important;
margin-top: 20px !important;}
.comments td {font-size: 12px !important;}
.comments img {border: 5px solid #665E55 !important; border-radius:10px !important;
margin-right: 10px !important; box-shadow: 0px 0px 3px #F2E9C6 !important;}
.reply-in-place {background:#F2E9C6 !important; padding: 1px 10px 2px 10px !important;
border: 2px solid #A4CEB9 !important; border-radius:10px !important;}


/* Flèches pour parcourir dramas sur le profil */
#navigation-buttons span.active, #navigation-buttons span:hover {
background: none !important;}
.navigation > span {background: #F2E9C6 !important;}
.navigation > span:hover, .navigation > div > div:hover {background: #F2E9C6 !important;}

/*couleurs une fois sur deux dans la recherche */
.items > div:nth-child(1), .items > div:nth-child(4), .items > div:nth-child(5), .items > div:nth-child(8), .items > div:nth-child(9) 
{background: none repeat scroll 0 0 #F2E9C6 !important;}

/*couleurs une fois sur deux dans la liste dramas */
#list > table > tbody > tr:nth-child(even), #stats tr:nth-child(even)
{background: none repeat scroll 0 0 #F2E9C6 !important;}

/*couleurs une fois sur deux dans la liste des groupes & reviews */
.episodes tr:nth-child(even), .items tr:nth-child(even), .reviews tr:nth-child(even)
{background: none repeat scroll 0 0 #F2E9C6 !important;}

#listCount {background: #F2E9C6 !important;}

.mainbox, #roles > div > div, .roles > div > div {background: #F2E9C6 !important;}
.infobox {background: none repeat scroll 0 0 rgba(242, 233, 198, 0.9) !important; 
padding: 6px !important; margin-top:-4px !important; margin-left: 80px !important;}

section img {
box-shadow: 0px 0px 3px #A4CEB9 !important; 
border: 2px solid #A4CEB9 !important; border-radius: 15px 0px 0px 15px !important;}

.mainboxfeat {background: none repeat scroll 0 0 rgba(255, 255, 255, 0.8) !important; }

#rating_stats .aggregate table td:nth-of-type(1), #rating_stats .aggregate table td:nth-of-type(2), #rating_stats > div 
{background: #F2E9C6 !important; border: none !important;}


/*pop-up*/
#dialog .inside > div > .border > .content > .body, #dialog .inside > div > .border > .content > .loading {background: #FDFCE5 !important;
border-color: #D9EEDA !important; }
#dialog .inside > div > .border {background: none repeat scroll 0 0 rgba(164, 206, 185, 0.7) !important;}
#dialog .inside > div > .border > .content > h1 {background: #D9EEDA !important; 
text-align:center !important; letter-spacing: 3px; border: #D9EEDA !important;
text-transform: uppercase; font-size: 11px !important;}
.esc {background: none !important; letter-spacing: 0px; font-size: 9px !important;}

