css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://nlwnewg.appspot.com/"),
                           url-prefix("https://nlwnewg.appspot.com/"){

a[href="http://enq-maker.com/gcrueY4"]
{display:none;}

a:link {color:#555!important;
text-decoration:none!important;}
a:visited {color:#555!important}
a:active {color:#4682B4!important}
a:hover {color:#4682B4!important;
text-decoration:underline!important;}
a:hover img {opacity:0.8;}

#nlwButtonDiv {
display:block!important;
width: 16px!important;
height: 16px!important;
left: 1px!important;
top: 2px!important;
background: no-repeat url(http://cdn21.atwikiimg.com/nlwnew/?cmd=upload&act=open&pageid=1&file=official_hidariue.png) !important;
opacity: 1!important;
}
#nlwButtonDiv:hover {
width: 200px!important;
height: 16px!important;
left: 2px!important;
top: 3px!important;
background: no-repeat url(http://cdn21.atwikiimg.com/nlwnew/?cmd=upload&act=open&pageid=1&file=official_hidariue2.png) !important;
}
  
.twoColFixRtHdr #header {display:none;}
.twoColFixRtHdr {
background: url(http://www21.atwiki.jp/nlwnew/?cmd=upload&act=open&page=%E3%83%88%E3%83%83%E3%83%97%E3%83%9A%E3%83%BC%E3%82%B8&file=bg1.gif) !important;
}

#logo_area {
text-indent: -9999px;
background: no-repeat url(http://www21.atwiki.jp/nlwnew/?cmd=upload&act=open&page=%E3%83%88%E3%83%83%E3%83%97%E3%83%9A%E3%83%BC%E3%82%B8&file=logo1.gif) !important;
background-color: #666!important;
background-position: 50%!important;
}

#kanban_box {box-shadow: 0 0 5px #aaa;}

#header_menu {display:none!important;}
#firefox_notice_head {display:none!important;}
#firefox_notice {display:none!important;}
#nlwrankdata_head {
width:1050px!important;
border-color: #777!important;
}

#nlwrankdata {
width:1050px!important;
background-color: #fff!important;
border-bottom-left-radius: 15px;
border-bottom-right-radius: 15px;
box-shadow: 0 10px 10px -10px rgba(0, 0, 0, 0.5), 0 1px 4px rgba(0, 0, 0, 0.3), 0 0 40px rgba(0, 0, 0, 0.1);
overflow:auto!important;
}
#nlwrankdata img {box-shadow: 0px 0px 10px -5px rgba(0, 0, 0, 0.8);}

#nlwtagdata {
width:1050px!important;
background-color: #fff!important;
border-bottom-left-radius: 15px;
border-bottom-right-radius: 15px;
box-shadow: 0 10px 10px -10px rgba(0, 0, 0, 0.5), 0 1px 4px rgba(0, 0, 0, 0.3), 0 0 40px rgba(0, 0, 0, 0.1);
overflow:auto!important;
}

#tagRelatedSitesDiv {
background-color: rgba(30, 30, 30, 0.95)!important;
color: #ff00ff!important;
width:1050px!important;
height:630px!important;
top: 10%!important;
left: 22%!important;
padding: 0 10px;
box-shadow: 0 0 20px #000;
}
#tagRelatedSitesDiv a:link {color:#ddd!important;}
#tagRelatedSitesDiv a:visited {color:#ddd!important;}
#tagRelatedSitesDiv a:active {color:#ddd!important;}
#tagRelatedSitesDiv a:hover {color:#6c9dc6!important;}
#tagRelatedSitesDiv img {border-radius: 10px;}

#nlwdata_head {
width:1050px!important;
border-color: #777!important;
}

#nlwdata {
width:1050px!important;
background-color: #fff!important;
border-bottom-left-radius: 15px;
border-bottom-right-radius: 15px;
box-shadow: 0 10px 10px -10px rgba(0, 0, 0, 0.5), 0 1px 4px rgba(0, 0, 0, 0.3), 0 0 40px rgba(0, 0, 0, 0.1);
overflow:auto!important;
}
#nlwdata img {box-shadow: 0px 0px 10px -5px rgba(0, 0, 0, 0.8);}

#nlwdataRes {
width:1050px!important;
background-color: #fff!important;
border-bottom-left-radius: 15px;
border-bottom-right-radius: 15px;
box-shadow: 0 10px 10px -10px rgba(0, 0, 0, 0.5), 0 1px 4px rgba(0, 0, 0, 0.3), 0 0 40px rgba(0, 0, 0, 0.1);
overflow:auto!important;
}
#nlwdataRes a {
background-color: #ffd9df!important;
-moz-transition: 0.3s;
-webkit-transition: 0.3s;
border-radius: 10px;
text-decoration:none!important;
padding: 0 10px}
#nlwdataRes a:hover {
color: #333!important;
background: #ffc0cb!important;
border-radius: 10px;}
.reses {word-wrap:break-word;}

#footer {box-shadow: 0 0 20px #555;}
#footer a,
#footer a:link,
#footer a:visited,
#footer a:active,
#footer a:hover {
padding:2px;
color:#cd5c5c!important;
text-decoration:none!important;
}
}
