css:
@-moz-document domain("anandtech.com") {
body {
    background-color: #444 !important;
  }

  header {
    border-bottom: 6px solid #444 !important;
  }

  .topic_menu {
    background-color: #414141 !important;
  }

  .topic_menu li a {
    display: block !important;
  }
    
  .topic_menu li a:hover {
    color: #F25F1E !important;
  }
  
  section.main_cont {
    width: 665px !important;
  }

  section.content {
    background-color: #F6F6F6 !important;
    padding: 15px 5px 20px 15px !important;
  }

  nav.breadcrumb {
    width: 100% !important;
  }

  .articleContent p {
    margin: 13px 4px !important;
    color: #333 !important;
    vertical-align: top !important;
  }

  div.generic_cont > p, 
  div.articleContent > p {
    text-align: justify !important;
  }

  div.articleContent p strong {
    color: #333 !important;
  }

  @media not all and (-webkit-min-device-pixel-ratio:0) {  

  .articleContent p:first-child::first-letter {
    font-size: 42px !important;
    float: left !important;
    line-height: 41px !important;
    font-weight: bold !important;
    margin: 1px 3px 0 0 !important;
  }

  .articleContent p:first-child strong::first-letter {
    font-size: 1em !important;
    float: none !important;
    line-height: 21px !important;
    font-weight: 700 !important;
    margin: 0 !important;
  }
  
}

  .articleContent img, .review>img {
    border: 1px solid #888 !important;
    display: inline-block !important;
    margin: 0 auto !important;
  }

  .articleContent p img[style*="float: left"] {
    margin: 0 8px 8px 0 !important;
    border: none !important;
  }

  .articleContent p img[style*="float: right"] {
    margin: 0 0px 8px 8px !important;
    border: none !important;
  }

  .articleContent h2 {
    margin-top: 16px !important;
    margin-bottom: 8px !important;
  }

  .articleContent p[align="center"],
  .articleContent p[style*="text-align: center"] {
    text-align: center !important;
    margin: 13px 0px !important;
    font-size: 85% !important;
    font-style: italic !important;
    color: #666 !important;
  }

  .articleContent div iframe {
    margin: 8px 0 !important;
  }

  .articleContent input[type="button"] {
    margin: 8px 4px !important;
    padding: 6px 8px !important;
    background: -moz-linear-gradient(top, rgba(64,176,232,1) 1%, rgba(29,110,147,1) 100%) !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,rgba(64,176,232,1)), color-stop(100%,rgba(29,110,147,1))) !important;
    background: -webkit-linear-gradient(top, rgba(64,176,232,1) 1%,rgba(29,110,147,1) 100%) !important; 
    background: -o-linear-gradient(top, rgba(64,176,232,1) 1%,rgba(29,110,147,1) 100%) !important;
    background: -ms-linear-gradient(top, rgba(64,176,232,1) 1%,rgba(29,110,147,1) 100%) !important;
    background: linear-gradient(to bottom, rgba(64,176,232,1) 1%,rgba(29,110,147,1) 100%) !important;
    border-radius: 4px !important;
    border: none !important;
    box-shadow: 0 1px 1px rgba(255, 255, 255, 0.95) inset, 0px 1px 6px #AAA, 0px 1px 1px #333, 0px -1px 0px rgba(0, 0, 0, 0.4) inset !important;
    color: #FFF !important;
    text-shadow: 0px 0px 4px #5D8EAD, -1px -1px 0 #184A6B !important;
  }

  .articleContent input[type="button"]:hover {
    background: -moz-linear-gradient(top, rgba(123,206,247,1) 0%, rgba(0,119,193,1) 100%) !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(123,206,247,1)), color-stop(100%,rgba(0,119,193,1))) !important;
    background: -webkit-linear-gradient(top, rgba(123,206,247,1) 0%,rgba(0,119,193,1) 100%) !important;
    background: -o-linear-gradient(top, rgba(123,206,247,1) 0%,rgba(0,119,193,1) 100%) !important;
    background: -ms-linear-gradient(top, rgba(123,206,247,1) 0%,rgba(0,119,193,1) 100%) !important;
    background: linear-gradient(to bottom, rgba(123,206,247,1) 0%,rgba(0,119,193,1) 100%) !important;
  }
  .articleContent input[type="button"]:active {
    background: -moz-linear-gradient(top, rgba(32,131,181,1) 0%, rgba(91,166,198,1) 100%) !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(32,131,181,1)), color-stop(100%,rgba(91,166,198,1))) !important;
    background: -webkit-linear-gradient(top, rgba(32,131,181,1) 0%,rgba(91,166,198,1) 100%) !important;
    background: -o-linear-gradient(top, rgba(32,131,181,1) 0%,rgba(91,166,198,1) 100%) !important;
    background: -ms-linear-gradient(top, rgba(32,131,181,1) 0%,rgba(91,166,198,1) 100%) !important;
    background: linear-gradient(to bottom, rgba(32,131,181,1) 0%,rgba(91,166,198,1) 100%) !important;
  }

  .articleContent table tr td {
    margin: 0 !important;
    border: 0 !important;
  }

  .articleContent table tbody tr:first-child td {
    background: #333 !important;
  }

  .articleContent table tbody tr:first-child td img {
    border: none !important;
  }

  .articleContent tr.contentwhite td {
    color: #EEE !important;
  }

  div.article_links a {
    font-weight: bold !important;
    font-size: 1.1em !important;
    text-shadow: 1px 1px 0px #FFF !important;
  }

  div.article_links a.fl-rt::after {
    content: ' »' !important;
  }

  div.article_links a.fl-lt {
    color: #7DB5C0 !important;
  }

  div.article_links a.fl-lt::before {
    content: '« ' !important;
  }

  div.thumbTagContainer {
    font-family: 'Rokkitt', serif !important;
    font-size: 1.5em !important;
    background: #444 !important;
  }

  div.thumbTagContainer a {
    color: #59DEF7 !important;
  }

  div.thumbTagContainer ul li a:hover img {
    border: 1px solid #2295AB !important;
    opacity: 1.0 !important;
    -moz-opacity: 1.0 !important;
    outline: 4px solid #2295AB !important;
  }

  div#comments.discussion {
    margin-top: 16px !important;
    border-top: 1px solid #AAA !important;
  }

  div.blog_comments {
    padding-top: 20px !important;
  }

  ul#ulComments {
    background: #F6F6F6 !important;
  }

  #ulComments li {
    border: 0px solid #EEE !important;
    border-radius: 0px !important;
    padding: 12px 4px !important;
    position: relative !important;
    background: none !important;
    color: #333 !important;
    margin-top: -1px !important;
  }

  #ulComments li:not(:first-child) {
    border-top: 1px solid #CCC !important;
  }

  #ulComments li:first-child {
    margin-top: 8px !important;
  }

  #ulComments li span {
    font-family: 'Arial', 'Helvetica', sans-serif !important;
    line-height: 1.5em !important;
    padding: 0 !important;
    margin-top: 4px !important;
    font-size: 14px !important;
    vertical-align: top !important;
  }

  #ulComments li span br {
    line-height: 2.2em !important;
  }

  #ulComments li span br + br {
    display: none !important;
  }

  #ulComments li span br + br::before {
    content: " " !important;
    height: 10px !important;
    display: inline-block !important;
  }

  #ulComments li h4 {
    font-family: 'Arial', 'Helvetica', sans-serif !important;
    padding: 0px !important;
    font-size: 11px !important;
  }

  #ulComments li h4 a:first-child {
    font-size: 13px !important;
    font-weight: 700 !important;
    color: #007E96 !important;
    text-shadow: 1px 1px 0px #FFF !important;
  }

  #ulComments li h4 a:not(:first-child) {
    color: #2295AB !important;
  }

  #ulComments li h4 a:not(:first-child):hover {
    text-decoration: underline !important;
  }

  #ulComments li span a:hover {
    color: #F25F1E !important;
  }

  #ulComments li a.reply_btn {
    background: #F25F1E !important;
    color: #FFF !important;
    font-family: 'Arimo', sans-serif !important;
    font-size: 80% !important;
    width: auto !important;
    height: 16px !important;
    line-height: normal !important;
    margin: 0 !important;
    border: none !important;
    padding: 1px 4px 0 !important;
    visibility: hidden !important;
    clear: both !important;
    position: absolute !important;
    top: 4px !important;
    right: 4px !important;
    font-weight: bold !important;
  }

  #ulComments li:hover a.reply_btn {
    visibility: visible !important;
  }

  #ulComments li a.reply_btn:hover {
    background: #2295AB !important;
  }

  #ulComments li[style*="padding-left"] {  
    margin-top: -1px !important;
    border-radius: 0 !important;
    background: none !important;
    color: #4A4A4A !important;
    border: none !important;
    border-top: 1px solid #DDD  !important;
    padding: 12px 4px !important;
  }  

  #ulComments li[style*="padding-left"]:first-child {
    margin-top: 8px !important;
  }

  #ulComments li[style*="padding-left"]:first-child::after {
    content: ' (replying to previous page) ' !important;
    font-size: 10px !important;
    color: #999 !important;
    line-height: 20px !important;
    display: block !important;
    width: 100% !important;
    height: auto !important;
    position: absolute !important;
    top: -20px !important;
    left: 0px !important;
    right: 0 !important;
    text-align: right !important;
    border-radius: 0 !important;
    padding: 2px !important;
    font-weight: normal !important;
    border: none !important;
  }

  #ulComments li[style*="padding-left"]::before {
    content: ' ' !important;
    position: absolute !important;
    left: 0px !important;
    top: 0px !important;
    color: #CCC !important;
    height: 4px !important;
    letter-spacing: 0px !important;
    line-height: 1px !important;
    text-align: left !important;
    font-size: 10px !important;
    font-family: 'Arial', 'Helvetica', sans-serif !important;
    font-weight: bold !important;
  }
      
  #ulComments li[style*="padding-left:18px"] {
    margin-left: 24px !important;
    margin-right: 0px !important;
  }
  #ulComments li[style*="padding-left:18px"]::before { content: '– –' !important; width: 9px !important;}

  #ulComments li[style*="padding-left:36px"] {
    margin-left: 48px !important;
    margin-right: 0px !important;
  }
  #ulComments li[style*="padding-left:36px"]::before { content: '– – – –' !important; width: 18px !important;}

  #ulComments li[style*="padding-left:54px"] {
    margin-left: 72px !important;
    margin-right: 0px !important;
  }
  #ulComments li[style*="padding-left:54px"]::before { content: '– – – – – –' !important; width: 27px !important;}

  #ulComments li[style*="padding-left:72px"] {
    margin-left: 96px !important;
    margin-right: 0px !important;
  }
  #ulComments li[style*="padding-left:72px"]::before { content: '– – – – – – – –' !important; width: 36px !important;}

  #ulComments li[style*="padding-left:90px"] {
    margin-left: 120px !important;
    margin-right: 0px !important;
  }
  #ulComments li[style*="padding-left:90px"]::before { content: '– – – – – – – – – –' !important; width: 45px !important;}

  #ulComments li#replybox {
    border-radius: 0 !important;
    padding: 0 0 1px !important;
    margin-top: -1px !important;
    background: #FAFAFA !important;
    border-top: none !important;
    position: relative !important;
  }

  #ulComments li[style=""] + li#replybox { margin-left: 24px !important; }
  #ulComments li[style*="padding-left:18px"] + li#replybox { margin-left: 48px !important; }
  #ulComments li[style*="padding-left:36px"] + li#replybox { margin-left: 72px !important; }
  #ulComments li[style*="padding-left:54px"] + li#replybox { margin-left: 96px !important; }
  #ulComments li[style*="padding-left:72px"] + li#replybox { margin-left: 120px !important; }
  #ulComments li[style*="padding-left:90px"] + li#replybox { margin-left: 144px !important; }

  li#replybox:not(:first-child)::before {
    content: '↑' !important;
    position: absolute !important;
    text-align: center !important;
    width: 20px !important;
    top: 2px !important; 
    left: -23px !important;
    font-family: 'Arial', 'Helvetica', sans-serif !important;
    font-size: 30px !important;
    color: #2D8394 !important;
    font-weight: bold !important;
  }

  #ulComments span#icMessage {
    background: #F6F6F6 !important;
    color: #666 !important;
    display: inline-block !important;
    font-size: inherit !important;
    line-height: 1.3em !important;
    border-radius: 0 !important;
    padding: 6px !important;
  } 

  #replybox .blog_post_btm h3 {
    margin-top: 0 !important;
  }

  span#icMessage a {
    color: #2295AB !important;
  }

  #ulComments textarea#CommentInput {
    resize: vertical !important;
    width: 100% !important;
    margin-top: 1px !important;
    border: none !important;
  }

  input.submit_comment_btn {
    background: #F25F1E !important;
    color: #FFF !important;
    border-radius: 4px !important;
  }

  input.submit_comment_btn:hover {
    background: #F0F0F0 !important;
    color: #F25F1E !important;
  }

  .pipeline_stories, article.twitter, article.dailytech_news {
    background-color: #EEE !important;
    color: #000 !important;
    border: 1px solid #DDD !important;
    padding: 6px !important;
    margin-bottom: 28px !important;
  }

  article.facebook_like ~ div[style*="padding-bottom"] {
    display: none !important;
  }

  .dailytech_news h2 {
    background-color: #414141 !important;
    border: none !important;
  }
      
  .dailytech_news h2 a {
    color: #59DEF7 !important;
    background: none !important;
    margin-right: 0 !important;
    padding: 5px 10px 0 0 !important;
  }

  .dailytech_cont ul {
    background: none !important;
    background-color: #F6F6F6 !important;
    padding: 0 !important;
    margin-top: 10px !important;
  }

  .dailytech_cont ul li {
    border-bottom: 1px solid #DDD !important;
  }

  .dailytech_cont ul li:last-of-type {
    border: none !important;
  }
    
  .pipeline_stories h2 {
    color: inherit !important;
  }

  .pipeline_title {
    padding-bottom: 15px !important;
  }
  
  .pipeline_title a {
    color: #2295AB !important;
  }

  .pipeline_stories strong img {
    display: none !important;
  }

  .pipeline_stories strong,
  .pipeline_stories span {
    padding: 0px !important;
  }
    
  .pipeline_stories span {
    background-color: #757575 !important;
  }

  .pipeline_stories span a {
    color: #FFF !important;
  }

  .pipeline_stories strong a,
  .pipeline_stories span a {
    display: block !important;
    padding: 10px !important;
  }

  .pipeline_stories strong a:hover {
    background: #333 !important;
  }

  .pipeline_stories span a:hover {
    background: #666 !important;
  }
        
  .pipeline_stories ul {
    background-color: #F6F6F6 !important;
    border: none !important;
    padding: 0 !important;
  }

  .pipeline_stories ul li,
  .twitter_cont ul li {
    border-bottom: 1px solid #DDD !important;
  }

  .pipeline_stories ul li,
  .dailytech_cont ul li {
    padding: 0 !important;
  }

  .pipeline_stories ul li:last-child,
  .twitter_cont ul li:last-child {
    border-bottom: none !important;
  }
            
  .pipeline_stories ul li a, 
  .twitter_cont ul li a, 
  .dailytech_cont ul li a {
    color: #666 !important;
  }

  .pipeline_stories ul li a,
  .dailytech_cont ul li a {
    padding: 5px 8px !important;
    display: block !important;
  }

  .pipeline_stories ul li a:hover,
  .dailytech_cont ul li a:hover,
  .twitter_cont ul li:hover {
    background: #FFF !important;
  }

  .pipeline_stories ul li a:hover, 
  .twitter_cont ul li a:hover, 
  .dailytech_cont ul li a:hover {
    color: #2295AB !important;
  }

  .twitter_cont ul {
    background-color: #F6F6F6;
    padding: 0 10px !important;
  }

  article.twitter h2 {
    background-position: -12px 0px !important;
    padding-top: 12px !important; 
    padding-left: 14px !important;
  }

  article.twitter h2 img {
    top: 18px !important;
    right: 46px !important;
  }

  div.twitter_cont,
  div.twitter_box {
    border: none !important;
    padding: 0 !important;
    margin: 0 !important;
    width: auto !important;
  }

  div.twitter_box { margin-top: 10px !important;}
  div.twitter_cont { margin-bottom: 10px !important;}

  .twitter_cont ul li a span {
   color: #2295AB !important;
   font-style: italic !important;
  }

  .twitter_box a.twitter_follow_btn {
    background: #444 !important;
    font-size: 1.2em !important;
    line-height: 2.0em !important;
    height: auto !important;
    color: #EEE !important;
    border-radius: 2px !important;
  }

  .twitter_box a.twitter_follow_btn:hover {
    background: #2295AB !important;
  }

  .twitter_box a.twitter_follow_btn span {
    font-size: 1.0em !important;
    color: #2295AB !important;
    font-family: inherit !important;
    font-weight: normal !important;
  }

  .twitter_box a.twitter_follow_btn:hover span {
    color: #F25F1E !important;
  }

  article.twitter {
    margin: 0 !important;
    padding: 6px !important;
    width: auto !important;
  }

  article.twitter strong img {
    display: none !important;
  }

  .footer, .footer_left {
    background-color: #222 !important;
  }

  footer .links h2 {
    background-color: #F25F1E !important;
  }

  div.cont_box1 {
    border-top: 1px solid #FFF !important;
    border-left: 1px solid #FFF !important;
    border-bottom: 1px solid #D0D0D0 !important;
    border-right: 1px solid #D9D9D9 !important;
    box-shadow: 1px 1px 3px #DDD, 0 0 6px #DDD !important;
    padding-right: 4px !important;

  }

  .cont_box1_txt {
    width: 346px !important;
    padding: 5px 2px 2px 2px !important;
  }

  .cont_box1_txt h2 a {
    color: #2295AB !important;
    font-weight: bold !important;
    text-shadow: 1px 1px 0 #FFF !important;
    font-size: 20px !important;
  }

  .cont_box1_pic {
    background: #FFF !important;
    border-top: none !important;
    border-left: none !important;
    border-bottom: none !important;
    border-right: 1px solid #DDD !important;
  }

  .cont_box1_pic a {
    margin: 0 auto !important;
  }

  div.pipeline {
    background-color: #757575 !important;
    border: none !important;
  }

  div.pipeline h2 {
    background-color: #F25F1E !important;
  }

  div.pipeline_cont ul li {
    margin: 3px 0 !important;
    padding: 5px !important;
  }
  
  div.pipeline_cont ul li:nth-child(odd) {
    background: #666 !important;
  }

  div.pipeline_cont ul li a span {
    white-space: nowrap !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
  }

  div.featured_banner {
  	width: 665px !important;
  }

  div.featured_info {
    text-shadow: 1px 1px 0 #000 !important;
    background: -moz-linear-gradient(top, rgba(0,0,0,1) 0%, rgba(0,0,0,0.9) 7%, rgba(0,0,0,0.8) 70%, rgba(0,0,0,0.75) 100%) !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,1)), color-stop(7%,rgba(0,0,0,0.9)), color-stop(70%,rgba(0,0,0,0.8)), color-stop(100%,rgba(0,0,0,0.75))) !important;
    background: -webkit-linear-gradient(top, rgba(0,0,0,1) 0%,rgba(0,0,0,0.9) 7%,rgba(0,0,0,0.8) 70%,rgba(0,0,0,0.75) 100%) !important;
    background: -o-linear-gradient(top, rgba(0,0,0,1) 0%,rgba(0,0,0,0.9) 7%,rgba(0,0,0,0.8) 70%,rgba(0,0,0,0.75) 100%) !important;
    background: -ms-linear-gradient(top, rgba(0,0,0,1) 0%,rgba(0,0,0,0.9) 7%,rgba(0,0,0,0.8) 70%,rgba(0,0,0,0.75) 100%) !important;
    background: linear-gradient(to bottom, rgba(0,0,0,1) 0%,rgba(0,0,0,0.9) 7%,rgba(0,0,0,0.8) 70%,rgba(0,0,0,0.75) 100%) !important;
  }
  div.bench_box { width: 665px !important;}
  div.bench_titleBox span {
  	margin-left: 8px !important;
  	width: 425px !important;
  }

  div.bench_titleBox strong {
  	width: auto !important;
  }

  div.bench_carousel,
  div.bench_titleBox {
  	padding: 10px 22px !important;
  }

  div.bench_carousel ul li {
  	margin-right: 12px;
  }
  div.bench_carousel ul li:last-of-type {
  	margin-right: 0 !important;
  }

  div.bench_carousel ul li:hover {
    outline: 2px solid #F25F1E !important;
  }

  div.bench_carousel ul li:hover a span {
    color: #F25F1E !important;
  }

  a[href^="mailto"] { position: relative;}

  a[href^="mailto"]:hover {
    background: #F25F1E !important;
    color: #FFF !important;
    padding: 0px 2px !important;
    margin: 0 -2px !important;
  }

  a[href^="mailto"]:hover::before {
    position: absolute !important;
    display: block !important;
    line-height: 20px !important;
    height: 100% !important;
    width: 32px !important;
    content: " mail: " !important;
    background: #2295AB !important;
    color: #FFF !important;
    text-align: left !important;
    font-size: 14px !important;
    font-family: Rokkitt !important;
    padding: 0px !important;
    top: 0 !important;
    left: -32px !important;
  }


  article.facebook_like {
    display: none !important;
  }

  div.at13rightad {
    display: none !important;
  }

  .generic_cont h3 {
    color: #2295AB !important;
  }
}
