css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
body {
background: #191919 !important;
}

body#dashboard_editing {
background: #191919 !important;
}

*{
text-shadow:none !important;
-webkit-box-shadow:none !important;    
-moz-box-shadow:none !important;    
box-shadow:none !important;
}

#footer, #footer a {
color:#666666 !important;
background-image:none !important;
}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0,0,0,0.5) !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(0,0,0,0.75) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding-top: 20px;
padding-bottom: 20px;
}

h1 {color:#666666!important;}
h1:hover {color:#666666!important;}
h2 {color:#666666!important;}
h2:hover {color:#666666!important;}
h3 {color:#666666!important;}
h3:hover {color:#666666!important;}
h4 {color:#666666!important;}
h4:hover {color:#666666!important;}
h5 {color:#666666!important;}
h5:hover {color:#e6e6e6!important;}
h6 {color:#666666!important;}
h6:hover {color:#666666!important;}

a:hover {color:#CCCCCC!important}

img#content_top, img#content_bottom {
display: none;
}

#auto_pagination_loader {
background:rgba(56,161,207, 0.00)!important;
}
		
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px white !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important; 
}

.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px #111111 !important;
}

#posts .post {
letter-spacing: 0px !important;
background:#191919!important;
}

a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor {
background-color: transparent !important;
}

.notification a.notification_target {
text-decoration:underline!important;
}

.notification .notification_sentence {
max-width:100%!important;
}
      
.notification.can_ignore:hover .notification_sentence .hide_overflow {
max-width:100%!important;
}
  
.avatar_frame{background-color:transparent!important;border:none!important;}
.content.is_media, 
.mask.top, 
.mask.bottom {
background: none !important; 
}

.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
border-radius: 16px !important;
}

.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
-moz-box-shadow: 1px 1px 3px #000000 !important;
}

.login.sign_up,
A[href="/forgot_password"] {
display: none !important;
}

#nav .nav_item.active {
background-color:#3e3e40;
}
	
#right_column{
color:#3e3e40;
background:none!important;
}	

#right_column .controls_section .selected {
background:rgba(255,255,255,0.2)!important;
background-image:none!important;
border-top:1px solid rgba(0,0,0,.09)!important;
}

#right_column .controls_section .recessed {
background-color:transparent!important;
}
	
#posts .post .post_avatar {
border: 0px!important;
background-color: #111111!important;
}

#left_column #posts .notification .avatar {
border-color: #222222 !important;
background-color: #000000 !important;
}

#left_column #posts .post .permalink {
background-color:#3e3e40; 
}

#left_column #posts .post .permalink {
background: #222 !important;
border-radius:50%;
}

#left_column #posts .post .permalink:after {
background: #111 !important;
border-radius:40%;
position:absolute;
top:4px;
left:-2px;
content:'';
height:95%;
width:84%;
}

#nav .nav_item.active, 
#nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#3e3e40 !important;
background-image:none !important;
border:0px !important
}

.notification_type_icon {
display:none !important  
}

#left_column #posts .notification {
background: rgba(0,0,0,0.8) !important;
color:#666 !important;
border-color: #333 !important;
}

#posts .notification.alt .nipple {
border-right: 6px solid #333 !important;
}

#posts .notification .nipple {
width: 0;
height: 0;
border-top: 6px solid transparent;
border-bottom: 6px solid transparent;
border-right: 6px solid #333 !important;
position: absolute;
left: -5px;
top: 10px;
}

#right_column:after {content:none!important}
div.plexi.color.instant.show {background-color:rgba(0,0,0,.95)!important}


.post.fan_mail div {
    background:none!important;
}

#container {
    width: 90% !important;
    padding-left: 5% !important;
    padding-right: 5% !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}

#left_column.left_column {
    width: -webkit-calc(100% - 215px) !important;
}

ol#posts.posts {
    margin: 0 !important;
    width: 100% !important;
}

.post_full {
    width: -webkit-calc(100% - 188px) !important;
  margin-left:168px!important;
}

div.new_post_buttons {
    background-repeat: repeat !important;
  margin-bottom:50px!important;
}

#post_buttons {
    width: 532px !important;
    padding-left: 50% !important;
    margin-left: -266px !important;
}

div.post_content {
    background-color: transparent !important;
}

a.post_permalink {
    -webkit-filter: grayscale(100%) brightness(.2);
}

  div.l-header {
    width: 100%!important;
}
#tabs_outter_container {
    width: -webkit-calc(100% - 250px)!important;
}
div.link_button.clickable {
    background-color: transparent!important;
    border: 0px!important;
}
div.notification_pane {
    background-color: #000!important;
}

div#user_graphs {
    -webkit-filter: invert(1)!important;
}

div.row {
    background: none!important;
}

div.notifications_block.ui_stats.showing_3.monochrome.clearfix.series_0.active_left {
    -webkit-filter: invert(1)!important;
}

div.date_header.clearfix {
    background: #070707!important;
}

div.ui_note.is_friend {
    background: #070707!important;
}

div.part-toggle {
    background: #111!important;
}

      #fan_mail {
    font-size: 14px!important;
}

.graph_top_post_icon .ui_post_badge {
    -webkit-filter: invert(1)!important
}

.post_form_wrapper.detached.v_center_el {
    left: 150px!important;
    margin: 0!important;
    width: -webkit-calc(100% - 250px)!important;
}

.ui_tops .post {
    width: 490px!important;
}

a.link_title, a.link_source {
    color: #444!important
;
    max-width: 1000px!important;
    width: 100%!important;
}

a.link_title:hover, a.link_source:hover {
    color: #999!important
;
}

div.link_button {
    min-height: 1px!important;
}

div.pinned_sidebar_footer {
    display: none!important;

div.post_form_wrapper_inner {
    width: 100%!important;
}

div.post_form_wrapper_inner.editing.active {
    width: 100%!important;
}

div.post_media, div.link_text_outer {
    height: auto!important;
    margin: 0!important;
    min-height: 1px!important;
    padding: 0!important;
}

iframe {
    visibility: visible!important;
}

input.post_tags {
    display: none;
}

li.chat_line {
    background-color: #070707!important;
    border-color: #111!important;
}

li.notification {
    min-height: 75px!important;
    margin-left: 168px!important;
    padding-left: 40px!important;
    width: -webkit-calc(100% - 228px)!important;
}

li.notification.single_notification {
    min-height: 74px!important;
    padding-left: 50px!important;
    width: -webkit-calc(100% - 240px)!important;
}

textarea#message {
    font-size: 14px!important;
}

}

@-moz-document url-prefix("http://www.tumblr.com/dashboard/iframe") {
body {background:none!important}
}

@-moz-document domain("tumblr.com") {
iframe {
    visibility: visible!important;
}

}

@-moz-document url-prefix("http://assets.tumblr.com"), url-prefix("https://assets.tumblr.com") {
textarea#message {
    font-size: 14px!important;
    line-height: 1.2!important;
}

}
