css:
@-moz-document url("https://www.tumblr.com/login"), url("http://www.tumblr.com/dashboard"), url("http://www.tumblr.com/new/blog"), url("http://www.tumblr.com/inbox"), url("http://www.tumblr.com/help"), url("https://www.tumblr.com/preferences"), url("http://www.tumblr.com/lookup"), url("http://www.tumblr.com/explore"), url("http://www.tumblr.com/following"), url("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/dashboard/"), url-prefix("http://www.tumblr.com/new/"), url-prefix("http://www.tumblr.com/tumblelog/"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/spotlight/"), url-prefix("http://www.tumblr.com/edit/") {
body {

  background:url("http://i1096.photobucket.com/albums/g321/CaptainPeroxide813/jesus-christ2.png") no-repeat fixed !important;
  background-color: rgba(44,71,98, 0.95) !important;
  background-position: 0% 0% !important;
  background-size: 100% 100%  !important;
}



#logo {
  width: 0px !important;
  height: 0px !important;

  width: 196px !important;
  height: 44px !important;
}



background-image: url(http://i.imgur.com/tnHnj.png) !important;
background-repeat: no-repeat;
position: absolute;
display: block;
width: 76px;
height: 90px;
top: 11px !important;
left: -1px !important;




new_post {
  background-color: rgba(255, 255, 255, 1.0) !important;
}



#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(44,71,98, .00) !important;
}
 


#posts LI[class*=" post"] {
  background: rgba(255, 255, 255, 1.0) !important;
}



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255,1.0) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(44,71,98, 1.0)  !important;
}



li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}



.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}



.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
}
