css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {




/* MAIN */

#dashboard_create_tumblelog, #hello_hello, #dashboard_editing, #dashboard_members, #main_custom_themes, #dashboard_directory, #dashboard_lookup_contacts, #dashboard_likes, #dashboard_transcoding, #main_help, #dashboard_tumblelog_settings, #account_display_onboard, #dashboard_block, #main_doc_page, #dashboard_settings, #dashboard_index, #dashboard_drafts, #dashboard_submissions, #dashboard_post_queue, #dashboard_followers, #dashboard_following, #tagged_display, #dashboard_inbox {
background-color: #a0a0a0 !important;
background-image: url('http://i49.tinypic.com/30rw5qu.png'), url('http://i49.tinypic.com/28gsh3d.png') !important;
background-position: bottom left, bottom left !important;
background-attachment: fixed, fixed !important;
background-repeat: no-repeat, no-repeat !important;
}

#logo {
height: 60px !important;
margin-top: 8px !important;
}

#logo > .logo_anchor {
background-image: url('http://i49.tinypic.com/24e7vau.png') !important;
}

#auto_pagination_loader, #sidebar_footer_nav.pinned_sidebar_footer {
visibility: hidden;
}

#dashboard_index div.l-content a {
border-bottom: none !important;
}

#return_to_top .return_to_top_icon {
background: url('http://data.sainet.co.uk/medigel/scroll_to_top/scroll-to-top_standard-1.png') no-repeat !important;
}

#logged_out #logo .default {
visibility: hidden !important;
}

.plexi {
background: rgba(0,0,0,0.5) !important;
}




.new_post_label .new_post_label_icon {
background-image: url('http://data.sainet.co.uk/medigel/post_icons/design/post_icons_garrus.png') !important;
margin-top: 8px !important;
background-size: 520px 190px !important;
opacity: 1 !important;
}

#posts .post.new_post {
background-image: url('http://i47.tinypic.com/2ql69ut.png'), url('http://data.sainet.co.uk/medigel/background/general/090909.jpg') !important;
background-position: bottom center !important;
background-repeat: no-repeat, repeat !important;
}

#posts .post.new_post .arrow {
background: transparent url('http://data.sainet.co.uk/medigel/nipple/nipple_masseffect.png') !important;
background-repeat: no-repeat !important;
}

div.l-content #new_post .new_post_label {
color: rgba(255,255,255,.95) !important;
line-height: 4.3em;
font-weight: bold !important;
font-size: .9em !important;
text-shadow: -1px -1px 0 rgba(0,0,0,0.5) !important;
text-transform: uppercase !important;
}





/* CONTENT */

#dashboard_create_tumblelog div.l-content, #main_explore div.l-content, #hello_hello div.l-content, #dashboard_members div.l-content, #main_custom_themes div.l-content, #dashboard_directory div.l-content, #dashboard_lookup_contacts div.l-content, #dashboard_likes div.l-content, #dashboard_transcoding div.l-content, #main_help div.l-content, #dashboard_tumblelog_settings div.l-content, #account_display_onboard div.l-content, #dashboard_block div.l-content, #main_doc_page div.l-content, #dashboard_settings div.l-content, #dashboard_index div.l-content, #dashboard_editing div.l-content, #dashboard_drafts div.l-content, #dashboard_submissions div.l-content, #dashboard_post_queue div.l-content, #dashboard_followers div.l-content, #dashboard_following div.l-content, #tagged_display div.l-content, #dashboard_inbox #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0,0,0,0.3) !important;
-webkit-border-radius: 6px !important;
border-radius: 20px !important;
padding-top: 20px !important;
padding-bottom: 20px !important;
}




/* TAB NOTICE */

#new_post_notice_container.tab_notice {
color: #ffffff !important;
background-color: #a40f0f !important;
}

#dashboard span.tab_notice_value {
color: #000000 !important;
}

#search_query {
background-color: rgba(0,0,0,0.15) !important;
color: rgba(255,255,255,0.6) !important;
}

#unread_tag_notice.pill.notice, #untrack_tag_button, .chrome.flat.pill {
background: rgba(255,255,255,.5) !important;
border: none !important;
color: rgba(0,0,0,0.6) !important;
}

#container .selection_nipple, #container .tab_notice_nipple {
visibility: hidden;
}




/* RADAR */

div.l-content #right_column .radar {
background-color: #0a0a0a !important;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
padding-bottom: 5px !important;
}

div.l-content #right_column .radar_avatar .radar_tumblelog_avatar {
box-shadow: none !important;
margin-left: 5px !important;
}

div.l-content #right_column .radar .radar_footer {
margin-top: 4px !important;
margin-bottom: 1px !important;
}

div.l-content #right_column .radar .radar_controls {
border-bottom: rgba(0,0,0,0.15) 1px solid !important;
}

div.l-content #right_column .thumbnail_anchor {
box-shadow: 0pt 1px 0pt rgba(255, 255, 255, 0.25) inset, 0pt 1px 3px rgba(0, 0, 0, 0.25);
}

div.l-content #right_column .radar_content .post {
background: transparent !important;
}

div.l-content .radar_post {
border: none !important;
}

div.l-content .radar_tumblelog_label {
width: 160px !important;
}




/* RIGHT COLUMN */

div.l-content #right_column li {
background-color: rgba(0,0,0,0.6) !important;
}

div.l-content #right_column li:hover {
box-shadow: rgba(255,255,255,0.02)  5px 0px 15px inset !important;
}

div.l-content #right_column {
background-color: transparent !important;
}

div.l-content #right_column.swipeable {
background: transparent !important;
}

div.l-content #right_column::after {
background: none !important;
}

div.l-content #right_column .small_links a{
color: rgba(255,255,255,0.7) !important;
}

div.l-content #right_column a, div.l-content .publish_on_day, div.l-content .publish_on_time, div.l-content #right_column small,div.l-content #right_column .link_arrow, div.l-content #right_column #tumblr_radar .radar_footer span, div.l-content #right_column .count, div.l-content #right_column .mark_date, div.l-content a.username, div.l-content #page_heading {
color: #ffffff !important;
}

div.l-content .post_avatar.flat.lighter_blue, #right_column a.send_fan_mail, div.l-content #right_column .following, div.l-content #right_column .spotlight, div.l-content #right_column .likes {
background-color: rgba(255,255,255,0.2) !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
box-shadow: none !important;
}

div.l-content .dashboard_options_form, div.l-content #MissingEdraftQueueTools.controls_section li, div.l-content #MissingEmassDeleter.controls_section li {
color: #ffffff !important;
background-color: rgba(255,255,255,0.2) !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
box-shadow: none !important;
}

div.l-content #right_column .likes {
border-radius: 6px 6px 0px 0px !important;
-webkit-border-radius: 6px 6px 0px 0px !important;
}

div.l-content #right_column .spotlight {
border-radius: 0px 0px 6px 6px !important;
-webkit-border-radius: 0px 0px 6px 6px !important;
}

div.l-content #right_column .likes:hover, div.l-content #MissingEdraftQueueTools.controls_section li:hover, div.l-content #MissingEmassDeleter.controls_section li:hover, #right_column a.send_fan_mail:hover, div.l-content #right_column .following:hover, div.l-content #right_column .spotlight:hover {
box-shadow: rgba(255,255,255,0.04) 5px 0px 15px inset !important;
}


div.l-content #right_column .likes {
border-radius: 6px 6px 0px 0px !important;
-webkit-border-radius: 6px 6px 0px 0px !important;
}

div.l-content #right_column .open_blog {
color: #ffffff !important;
background-image: none !important;
background-color: rgba(200,0,0,0.9) !important;
border-radius: 6px 6px 0px 0px !important;
-webkit-border-radius: 6px 6px 0px 0px !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.05) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.05) 1px solid !important;
}

div.l-content #right_column .open_blog:hover {
box-shadow: rgba(255,255,255,0.09) 5px 0px 25px inset !important;
}

div.l-content .dashboard_options_form_disclaimer, div.l-content .dashboard_options_form a {
color: rgba(255,255,255,9) !important;
}

div.l-content .dashboard_options_form_divider {
visibility: hidden !important;
}

div.l-content #right_column .open_blog a {
padding-left: 0px !important;
overflow: hidden !important;
}

div.l-content #right_column .open_blog .hide_overflow {
margin-left: 23px !important;
}

div.l-content .open_blog .sub_control.link_arrow {
top: 11px;
left: 11px;
width: 15px !important;
height: 15px !important;
}

#xfollowers_button.followers .hide_overflow {
overflow: visible !important;
}

#xfollowers_button.followers {
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
}

div.l-content #right_column .no_push, div.l-content #right_column .no_push.selected, div.l-content #right_column .selected, div.l-content #right_column .controls_section {
border-color: transparent  !important;
}

div.l-content #right_column .no_push {
border: none !important;
padding-top: 0px !important;
}

div.l-content #right_column .no_push.selected {
border: none !important;
}

div.l-content #right_column .controls_section  {
background-color: transparent !important;
}

div.l-content #right_column #MissingE_marklist.controls_section li {
background-color: #4795bc !important;
}

div.l-content #right_column .radar #MissingE_marklist.controls_section .MissingE_marklist_title.recessed {
background: none !important;
box-shadow: none !important;
}






/* POST STYLE */

div.l-content .arrow {
background: url('http://data.sainet.co.uk/medigel/nipple/nipple_e5e5e5.png') no-repeat transparent scroll!important;
}

div.l-content #posts .post {
background-color: #e5e5e5 !important;
}

div.l-content a.permalink {
background: transparent url('http://data.sainet.co.uk/medigel/dogear/dogear_nebula.png') !important;
margin-top: 1px !important;
margin-right: 3px !important;
}

div.l-content p.read_more_container a.read_more {
color: #a91111 !important;
text-decoration: none !important;
font-style: italic !important;
}

div.l-content .post_info a {
color: #6d0000 !important;
}

div.l-content .full_answer_container_wrapper {
border-top: 1px solid rgba(0,0,0,0.05) !important;
}

div.l-content .no_posts_found {
color: rgba(0,0,0,0.2) !important;
}

div.l-content #posts .post .post_content, div.l-content #posts .post .post_text_wrapper {
background: transparent !important;
background-image: none !important;
}

div.l-content .full_answer_container.clearfix {
background: transparent !important;
}

div.l-content .footer_links a, div.l-content .footer_links.with_tags a, div.l-content .footer_links .source_url {
color: rgba(0,0,0,0.7) !important;
}

div.l-content #posts .post_footer_links a.tag {
color: rgba(0,0,0,0.4) !important;
}

div.l-content .post_info {
color: rgba(0,0,0,0.9) !important;
}

div.l-content .post .tags a {
color: rgba(0,0,0,0.8) !important;
}

div.l-content .conversation_lines {
border: 1px solid rgba(0,0,0,0.08) !important;
border-radius: 4px !important;
}

div.l-content .conversation_lines .chat_line {
background-color: rgba(0,0,0,0.05) !important;
border-color: rgba(0,0,0,0.08) !important;
color: rgba(0,0,0,0.8) !important;
}

div.l-content #posts .tags {
line-height: 16px !important;
}

.source_url_gradient {
visibility: hidden !important;
}




/* ASKS */

div.l-content #posts .post_question {
background: rgba(0,0,0,0.1) !important;
-webkit-border-radius: 6px !important;
border-radius: 6px !important;
border: none !important;
color: rgba(0,0,0,0.7) !important;
}




/* NOTES */

div.l-content #posts .notes .note {
background: rgba(0,0,0,0.1) !important;
color: rgba(0,0,0,0.5) !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
box-shadow: none !important;
text-shadow: none !important;
}

div.l-content #posts .notes .note .avatar {
border-radius: 3px !important;
-webkit-border-radius: 3px !important;
}

div.l-content #posts .notes .note a {
color: rgba(0,0,0,0.7) !important;
}

div.l-content #posts .notes blockquote {
border-color: rgba(0,0,0,0.7) !important;
}

div.l-content #posts .notes_control a {
color: rgba(0,0,0,0.7) !important;
}

div.l-content #posts .MissingE_notesSorter {
color: rgba(0,0,0,0.6) !important;
}

.MissingE_sorterButton {
opacity: 0.8 !important;
border: 1px solid rgba(0,0,0,0.02) !important;
}

.MissingE_sorterButton:hover {
opacity: 1.0 !important;
}

div.l-content #posts .notes .note .notification_type_icon:hover, div.l-content #posts .notes .note .notification_type_icon.like_icon:hover, div.l-content #posts .notes .note .notification_type_icon.answer_icon:hover, div.l-content #posts .notes .note .notification_type_icon.reblog_icon:hover {
background-repeat: no-repeat !important;
opacity: 0.8 !important;
}

div.l-content #posts .notes .note .notification_type_icon {
background: url("http://data.sainet.co.uk/medigel/general/notification_icon_black.png") !important;
background-repeat: no-repeat !important;
opacity: 0.4 !important;
}

div.l-content #posts .notes .note .notification_type_icon.like_icon {
background: url("http://data.sainet.co.uk/medigel/general/notification_icon_like_black.png") !important;
opacity: 0.4 !important;
}

div.l-content #posts .notes .note .notification_type_icon.answer_icon {
background: url("http://data.sainet.co.uk/medigel/general/notification_icon_answer_black.png") !important;
opacity: 0.4 !important;
}

div.l-content #posts .notes .note .notification_type_icon.reblog_icon {
visibility: hidden !important;
}




/* NOTIFICATIONS */

div.l-content .notification, .toast_outer {
font-family: HelveticaNeue, Helvetica, Arial, sans-serif !important;
background-color: rgba(0,0,0,0.9) !important;
color: rgba(255,255,255,0.6) !important;
border: none !important;
}

div.l-content .notification a {
color: rgba(255,255,255,0.95) !important;
}

div.l-content .notification blockquote {
border-color: rgba(255,255,255,0.6) !important;
}

div.l-content .notification .notification_right .preview_frame {
border: 1px solid rgba(0,0,0,0.2) !important;
}

div.l-content .notification .notification_right .preview_frame.regular {
background-color: rgba(0,0,0,0.25) !important;
} 

.photo_reply_image_container {
border: 1px solid rgba(0,0,0,0.5) !important;
}

.toast_border {
border: none !important;
}




/* FANMAIL */

div.l-content #posts .post.fan_mail .message {
font-size: 16px !important;
background: none !important;
line-height: 19px !important;
font-family: sans-serif, helvetica, tahoma, arial !important;
}

#posts .controls a, .controls_link {
color: rgba(0,0,0,0.6) !important;
}

#posts .controls .reply_link {
height: 20px !important;
opacity: 0.5 !important;
}

#posts .controls .reply_link:hover {
opacity: 0.65 !important;
}

.fan_mail_read_more {
background-color: rgba(0,0,0,0.5) !important;
margin-top: 10px !important;
}

#dashboard_inbox #posts div, #dashboard_submissions #posts div {
background-image: none !important;
}

div.l-content .post_text_wrapper, div.l-content .post_content.clearfix {
background-color: #ffffff !important;
}

#right_column a.send_fan_mail {
background-image: none !important;
text-shadow: none !important;
box-shadow: none !important;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
}

div.l-content #posts .post.fan_mail {
background-color: #191919 !important;
color: rgba(255,255,255,0.7) !important;
background-image: none !important;
-webkit-border-radius: 6px !important;
border-radius: 6px !important;
border: none !important;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.46) !important;
}

div.l-content #posts .post.fan_mail a {
color: rgba(255,255,255,0.4) !important;
font-weight: bold !important;
}

div.l-content .post.fan_mail .arrow {
background: url('http://data.sainet.co.uk/medigel/nipple/nipple_legion.png') no-repeat scroll !important;
}






/* POST EDITOR */

#new_post .main_content {
background: rgba(255,255,255,0.95) !important;
color: rgba(0,0,0,0.7) !important;
border: none !important;
}

#new_post .popover_inner.clearfix, #new_post .post_options .popover_inner, #tag_suggest.popover, .post_options.popover {
background: rgba(255,255,255,0.95) !important;
}




/* AVATARS */

.avatar_frame, .post_avatar, .avatar {
background-color: transparent !important;
border-color: transparent !important;
}

div.l-content #posts .notification .avatar_frame {
border: 0px solid !important;
box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.46);
}




/* PAGINATION */

div.l-content .short_post_labels .short_label a {
border: none !important;
text-shadow: none !important;
color: #ffffff !important;
font-weight: bold !important;
}

div.l-content .short_post_labels .short_label {
border: none !important;
}

div.l-content #new_post.short_new_post.post.new_post {
background: none !important;
background-image: none !important;
border: none !important;
text-shadow: none !important;
box-shadow: none !important;
}

div.l-content #dashboard_controls span.dashboard_controls_arrow_first_page {
background-image: url("http://data.sainet.co.uk/medigel/general/dashboard_controls_arrow_first_page_white.png") !important;
}

div.l-content #dashboard_controls span.dashboard_controls_arrow_right {
background-image: url('http://data.sainet.co.uk/medigel/general/dashboard_controls_arrow_right_white.png') !important;
}

div.l-content #dashboard_controls span.dashboard_controls_arrow_left {
background-image: url("http://data.sainet.co.uk/medigel/general/dashboard_controls_arrow_left_white.png") !important;
}

div.l-content #left_column #dashboard_controls a, div.l-content #left_column #dashboard_controls span.control {
text-shadow: none !important;
color: #ffffff !important;
}

div.l-content #pagination #previous_page_link {
background-color: transparent !important;
color: rgba(0,0,0,0.75) !important;
}




/* MISCELLANEOUS */

#main_help .chrome.big.clear.help_contact_support_button {
color: #ffffff !important;
background-color: rgba(255,255,255,0.8) !important;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
border-top:  rgba(255,255,255,0.08) 1px solid !important;
border-left:  rgba(255,255,255,0.04) 1px solid !important;
border-bottom: rgba(0,0,0,0.08) 1px solid !important;
border-right: rgba(0,0,0,0.04) 1px solid !important;
background-image: none !important;
box-shadow: none !important;
}

.MissingE_bookmark_text, .MissingE_bookmark_line {
background: none !important;
color: rgba(0,0,0,0.8) !important;
border: none !important;
}

#no_results_message {
color: rgba(255,255,255,0.4) !important;
}

#main_help #results.help_results.clearfix {
margin-bottom: 20px !important;
background: none !important;
}

#main_help div.l-content a, #main_help div.l-content span{
color: rgba(255,255,255,0.9) !important;
}

#main_column {
width: 600px !important;
}

#help_footer {
background: rgba(255,255,255,0.4) !important;
}

#main_help #help_search.help_search {
margin-top: -30px !important;
}

#main_help #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
margin-top: 9px !important;
}

#main_about #nav_about, #main_help #nav_help, #main_contact #nav_contact, #main_apps #nav_apps, #main_tips #nav_tips, #main_causes #nav_causes, #main_press #nav_press, #main_team #nav_team, #main_jobs #nav_jobs, #main_developers #nav_developers, #main_sponsors #nav_sponsors, #main_policy .terms #nav_terms, #main_policy .privacy #nav_privacy, #main_policy .community #nav_community, #main_policy .impressum #nav_impressum {
background: none !important;
}

#main_doc_page #developers span.title, #main_doc_page #developers, div.white_pane a, body#dashboard_followers div.l-content h1 {
color: rgba(0,0,0,0.9) !important;
}

#main_doc_page p#developers {
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
box-shadow: 0pt 1px 5px rgba(0, 0, 0, 0.46);
}

#main_doc_page form#help_search {
box-shadow: 0pt 1px 5px rgba(0, 0, 0, 0.46);
}

#main_doc_page div.l-content div#results a {
color: rgba(255,255,255,0.9) !important;
}

#dashboard_following div.l-content, #dashboard_settings div.l-content, #dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color: #e4e4e4 !important;
}

#dashboard_following .grey_note {
background: rgba(0,0,0,0.4) !important;
text-align: center;
padding: 5px !important;
}

#dashboard_block div.l-content #left_column.clearfix {
background-color: #ffffff !important;
color: #000000 !important;
-webkit-border-radius: 6px !important;
border-radius: 6px !important;
}

#dashboard_block div.l-content #left_column.clearfix a {
color: #000000 !important;
}

#dashboard_block div.l-content #left_column.clearfix .description {
color: #000000 !important;
}

#dashboard_lookup_contacts div.l-content #finder_container {
background: #e4e4e4 !important;
}

#dashboard_directory #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
width: 100% !important;
background: #e4e4e4 !important;
}

div.l-content .answer_container {
background-color: #ffffff !important;
box-shadow: 0pt 1px 5px rgba(0, 0, 0, 0.46);
color: #000000 !important;
}

div.l-content .answer_container a {
color: #000000 !important;
background-color: #ffffff !important;
}

#tumblr_lightbox {
background-color: rgba(0,0,0,0.5) !important;
}

#dashboard_settings .right_column,  #dashboard_tumblelog_settings .right_column {
background: transparent !important;
}




/* POPOVER */

div.l-content #popover_blogs {
background: #a40000 !important;
}

div.l-content .popover_inner {
box-shadow: none !important;
background: none !important;
}

div.l-content #popover_blogs .popover_menu_item {
box-shadow: 1px 0px 1px rgba(0,0,0,0.1) inset !important;
background: none !important;
border: transparent !important;
}

div.l-content #popover_blogs .popover_menu_item:hover {
background-color: rgba(255,255,255,0.05) !important;
}

div.l-content #popover_blogs .blog_icon {
box-shadow: none !important;
border: none !important;
}




/* FOOTER */

#footer, #footer a {
color: rgba(255,255,255,0.8) !important;
border: none !important;
}




}
