css:
/* by makondo, 12/10 */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(https://addons.mozilla.org), 
url-prefix(https://static.addons.mozilla.net), 
url-prefix(https://services.addons.mozilla.org/en-US/firefox/discovery/addon/) {

/* hide the slide show >*/.section-teaser.featured,
.stats, 
/* collections basket */ 
IMG[src="https://addons.mozilla.org/media//img/amo2009/illustrations/logo-collections-100x125.png"],
IMG[src="https://addons.mozilla.org/media//img/zamboni/footer-logo-med.png"],
IMG[src="https://addons.mozilla.org/media///img/zamboni/anon_user.png"],
[src="https://static-cdn.addons.mozilla.net/media/img/illustrations/logo-collections-100x125.png?b=45dd624"],
/* contributions stuff > */ .notification,
/* empty avatars  > */
[src="https://static-cdn.addons.mozilla.net/media//img/zamboni/anon_user.png"], [src="https://static-ssl-cdn.addons.mozilla.net/media//img/zamboni/anon_user.png"],
IMG[src="https://static-cdn.addons.mozilla.net/media/img/illustrations/logo-collections-100x125.png?b=c280c2d5"], #site-welcome, #collections-landing, .footerlogo, /*other apps link> */ #aux-nav > UL > LI:last-child, #win-tablet
{ display: none !important; }

#promos .control:after
{ opacity: .4 !important;
background-color: transparent !important;
border-color: rgba(155,155,155,.5) !important;
box-shadow: 4px 4px 8px rgba(0,0,0, .6) !important; }

#promos .control:hover:after
{ opacity: .8 !important;
border-color: rgba(204,102,0,.4) !important; }

#global-header-tab a img
{ opacity: .65 !important; }

#global-header-tab a img:hover, .listing .item.incompatible .info,.listing .item.incompatible .action
{ opacity: 1 !important; }

/* incompatable with your Fx version */
.listing .item.incompatible .info,.listing .item.incompatible .action
{ background: rgba(181,170,154, .3) !important; }

/* mozilla link */ .mozilla, .site-balloon, #aux-nav ul li a:hover, .arrow, 
/* dictionaries page >*/.full thead, .full tr:nth-child(2n), .full tr:nth-child(2n+1), .button.add span
{ background: none !important;
border: none !important;
box-shadow: none !important; }

.s-featured #side-nav .s-featured a,.s-users #side-nav .s-users a,.s-rating #side-nav .s-rating a,.s-created #side-nav .s-created a, .secondary.user-avatar, .user-role, .compat-info thead, .no-restart, .notice.dependencies
{ background-color: transparent !important; }

body, .feature.promo-collection.promo,
.feature.ryff, .feature.promo, .metadata .links a
{ background-image: none !important; }

.mozilla span:hover
{ color: #DC760C !important; }

#masthead > H1 > A
{ padding-left: 12px !important; }

#social-footer > UL > LI:first-child,
#footer-logo
{ opacity: 0 !important; }

h2, h3, .header-search form, div.header-search span.wrap, .tagitem
{ background: none !important; }

/* contribute, etc. */.notification, .notification-box.error,
.install-wrapper.unreviewed, .item, #search-query, 
#user-account, .tab-wrapper, .tab-nav.main li a,
.tab-panel.tab-selected, .detail p#back
{ background: transparent !important; }

#header, .listing.results, .icon, .contribute, .selected a,
.listing-header li a:hover, .listing-footer, .pagination li a:hover,
.current, .header-search
{ box-shadow: none !important; 
background: none !important; }

.listing, .primary, .results-inner, .listing-footer,
.item, #no-restart-msg, .extra, #search-query, .featured,
#user-edit, #user-account, .tab-panel.tab-selected, .primary.island.c section,
.listing.results.island.hero.c, #sorter > UL > LI a, #site-nav>ul>li,
/* blue dotted borders */
.other-categories.highlight ul+ul, .divider, .info, .action, p
{ border: none !important; }

.modal-subscription .notification-box div,
.refinements, .other-categories.highlight ul+ul, .divider
{ border-bottom: none !important; }

.categories ul, .review-detail, .primary.island.c section
{ border-bottom: 1px solid rgba(0,0,0, .4) !important; }

.item .item-info, .object-lead .meta
{ border-left: 1px solid rgba(0,0,0, .4) !important; }

ul.further-navigation, #tag-box p, .item .meta,
.object-lead table tr td, .object-lead table tr th,
.further-navigation, .reviews .highlight > div
{ border-top: 1px solid rgba(0,0,0, .4) !important; }

.avatar, .notification-box.error, .notice.dependencies
{ border: 1px solid rgba(0,0,0,.3) !important;
border-radius: 3px !important; 
box-shadow: 4px 4px 8px rgba(0,0,0, .5) !important; }

/* ============ the green stuff */
.install-button a, .install-button,
.install.featuredaddon,
.install.unreviewed,
.install.lite
{ background: none !important; }

.site-title
{ padding: 0 !important;
margin-top: -24px !important;
margin-bottom: 16px !important; }

.site-title a
{ font-size: 16px !important;
color: black !important; }

/* charts */
#perf-results
{ border: 1px solid #444 !important; }

#perf-results td, .review
{ border: none !important;
border-bottom: 1px solid #555 !important; }

.impact .slower
{ border: none !important; }

.bar
{ box-shadow: inset rgba(50,50,50,0.5) 0px 1px 6px 3px !important;
background: linear-gradient(to top,  #fff 0%, #fff 0%, rgba(0, 0, 0, .1) 100%, rgba(19,24,28, .5) 95%) !important; }

/* the slide show */ .teaser-header, .teaser-header ol, /* compatibility page */.compat-info TBODY tr
{ background: none !important;
border: none !important; }

.addons li a
{ background: transparent !important;
border: none !important;
box-shadow: none !important; }

.persona-preview .created,.persona-preview .users,.persona-preview .reviews, .personas .users b
{ background-color: rgba(155,155,155,.5) !important;
color: #ccc !important;
margin-left: 4px !important; }

.persona-preview a,.persona-preview a:hover
{ background-color: transparent !important; 
border: 1px solid transparent !important; }

.persona-preview [data-browsertheme]
{ box-shadow: 2px 2px 4px rgba(0,0,0, .6) !important;
margin-left: 4px !important; }

.persona-hover .created,.persona-hover .users,.persona-hover .reviews, .persona-hover .personas .users b
{ background-color: rgba(155,155,155,.5) !important; }

.persona-hover .persona-inner
{ background-color: transparent !important; }

/* ======================== top links, other apps menu */
.other-apps
{ position: static !important;
margin-left: -52px !important;
margin-right: 20px !important; 
top: 0px !important; }

#other-apps > LI > A
{ margin-top: -24px !important; }

a.controller
{ background-image: none !important; }

.change.expanded, #other-apps ul, #other-apps li ul li, 
.account.expanded, .account.expanded ul, .account.expanded li ul li
{ border: none !important; }

#other-apps li ul li, .account.expanded li ul li
{ background: linear-gradient(to top, 
rgba(54,64,74,0.4) 0%, rgba(0, 0, 0,.2) 100%, rgba(0,0,8,0.5)) no-repeat !important;
box-shadow: 1px 0 5px rgba(44,54,71,.5) inset, 1px 2px 8px rgba(0,0,0,.3) !important;
border-radius: 3px !important;
z-index: 1 !important; }

.change.expanded a
{ color: black !important; }

#aux-nav > UL > LI:last-child > UL
{ margin-top: 0px !important; }

#aux-nav > UL > LI a
{ background: transparent !important;
box-shadow: none !important;
border: none !important; }

/* top nav links */
/*#site-nav
{ margin-left: 0px !important; }

#site-nav ul li
{ font-size: 11px !important;
margin: -4px !important;
z-index:-1 !important; }*/

/* ==========================screenshots */

#lightbox-container-image-box, ul.thumbs li,
#lightbox .content, #lightbox .controls
{ background: none !important;
border: none !important;
box-shadow: none !important; }

#lightbox .close
{ background: transparent !important;
border: none !important;
-box-shadow: none !important; }

ul.thumbs li .inner, .notice.c.author, .persona-list-3col, #lightbox > SECTION
{ border-radius: 5px !important; 
background: linear-gradient(to top,
rgba(54,64,74,0.4) 0%, rgba(0, 0, 0,.2) 100%, rgba(0,0,8,0.5)) no-repeat !important;
box-shadow: 0 0 5px rgba(44,54,71,.6) inset, 2px 2px 8px rgba(0,0,0,.5) !important;
border: 1px solid rgba(0,0,8, .5) !important; }

#lightbox-image-details span
{ color: #ccc !important;
text-shadow: 2px 2px 2px #000, 2px 2px 2px #000 !important; }

.previews, .previews .control
{ background: transparent !important; 
border: none !important;
box-shadow: none !important;
padding: 4px 0 !important; }

.previews li
{ padding: 0 !important; }

.previews li a
{ border: none !important;
box-shadow: none !important;
padding: 0 !important; }

.previews li a img
{ background: transparent !important; 
border: 1px solid rgba(0,0,0, .4) !important;
box-shadow: 1px 4px 20px 8px rgba(44,54,71,.2) inset, 1px 2px 8px rgba(0,0,0,.4) !important; }

/* ================== close button */
/*#lightbox > SECTION > DIV:first-child > A
{ box-shadow: none !important; }*/

#lightbox .close
{ box-shadow: none !important;
width: 0px !important;
height: 24px !important;
padding-left: 24px !important;
background-repeat: no-repeat !important;
background-color: rgba(155,155,155,.5) !important;}
/* ============================ font */
html, #footer
{ font-family: "Segoe UI", Tahoma, sans serif !important; }

b, p, span, a:hover, .firefox h2, #header h1, h5, h3, BLOCKQUOTE, 
.categories .items, .subcategories .items, label, .secondary-item-list.n h4,
#additional-resources ul, .secondary h4, small, .category.more-info, .version.article li, li, .adu, .listing.results.island h1, .person-info tr th, #id_password, #daily-users
{ color: black !important; }

h1
{ text-shadow: 1px 0px 1px #000 !important; }

p, span, .adu, .byline a
{ text-shadow: none !important; }

a, strong, label, ol.breadcrumbs li
{ color: #202E3A !important;
text-shadow: 1px 1px 2px #777, 1px 1px 2px #777 !important; }

a:hover
{ background-color: transparent !important; }

.info h3 a:hover
{ text-decoration: none !important; }

/* addon name on search pages */
.info h3 a
{ font-size: 14px !important;
color: #000 !important;
text-shadow: 1px 0px 0px #222 !important;
/*border-bottom: 1px solid #555 !important;*/ }

.firefox h2, .selected a, .current a, h2, .rating .selected, h1
{ font-size: 16px !important; }

.notification h3, .byline a, .rating, #addon-summary
{ font-size: 14px !important; }

.notification .suggestion p
{ font-weight: normal !important;
font-size: 12px !important; }

.highlight a, #categories a
{ color: #969DA0 !important;
text-shadow: 1px 2px 2px #222, 1px 2px 2px #999 !important; }

.highlight a:hover, #categories a:hover, .primary h1, #lightbox span
{ color: #C1CACE !important;
text-shadow: 1px 2px 1px #000, 1px 2px 1px #999 !important; }

/* selected tab */
#sorter li.selected a
{ font-weight: bold !important;
font-size: 16px !important;
text-shadow: 1px 0px 0px #000 !important; }

/* addon name & version */
.addon, .version-number
{ font-style: normal !important;
font-weight: bold !important;
font-size: 18px !important;
color: #D1DDCC !important;
text-shadow: 1px 2px 1px #000 !important; }

span.no-restart,span.featured
{ padding: 0 !important;
border: none !important;
float: right !important;
color: #CC6600 !important;
text-shadow: 1px 2px 1px #000 !important; }

/*==============  profile tabs */
.tab.tab-selected a
{ font-size: 14px !important;
color: #D2C991 !important;
text-shadow: 0px 1px 2px #000 !important; }

#nav-main ul, #compat-report
{ background: linear-gradient(to top,
rgba(54,64,74,0.4) 0%, rgba(0, 0, 0,.4) 100%, rgba(0,0,8,0.7)) no-repeat !important;
box-shadow: 1px 0 5px rgba(44,54,71,.5) inset, 1px 2px 8px rgba(0,0,0,.3) !important;
border-radius: 5px !important; }

#compat-report > TBODY > TR, #compat-report td
{ background: rgba(155,155,155,.5) !important;
border: 1px solid black !important; }

#compat-report a, .notification-box.error
{ font-weight: bold !important;
color: black !important;
text-shadow: 1px 1px 2px #444, 1px 1px 2px #999 !important; }

#nav-main ul li a:hover
{ color: #F78736 !important;
background: transparent !important; }

#nav-main ul li ul li a,
#nav-main ul li a
{ font-size: 14px !important;
color: #ccc !important;
text-shadow: 1px 1px 2px #000, 1px 1px 2px #000 !important; }

#nav-main ul li.selected a, #nav-main ul li.selected a,
#nav-main ul li.current a
{ color: #F78736 !important;
font-size: 16px !important;
background: none !important; }

#nav-main ul li a, #nav-main ul
{ border: none !important; }

#nav-main ul li ul li a:hover
{ background: linear-gradient(to top,
rgba(54,64,74,0.2) 0%, rgba(0, 0, 0,.3) 100%, rgba(0,0,8,0.3)) no-repeat !important;
box-shadow: 1px 0 5px rgba(0,0,0,.5) inset, 1px 2px 8px rgba(0,0,0,.3) !important;
border: none !important;
color: #F78736 !important; }

/* ========================= left panel */
.refinements a, #categoriesdropdown a
{ color: black !important;
font-weight: normal !important;
font-size: 13px !important; }

.refinements a:hover
{ color: #ccc !important; }

.c a, .c a:hover
{ text-shadow: 1px 0px 0px #000 !important;
background: transparent !important; }

.facet
{ border: none !important;
border-bottom: 1px solid #444 !important; }

.facet:hover
{ background: linear-gradient(to top,
rgba(54,64,74,0.2) 0%, rgba(240,240,240,.3) 100%, rgba(255,255,255,0.3)) no-repeat !important;
box-shadow: 1px 0 5px rgba(0,0,0,.5) inset, 1px 2px 8px rgba(0,0,0,.3) !important; }

/* ============================ search & input */

div.header-search
{ margin-top: -46px !important; 
margin-right: 430px !important; }

.header-search input
{ height: 30px !important; }

#search-q
{ padding: 0 6px !important; }

input[type="text"], TEXTAREA
{ position: relative !important;
 z-index: 1 !important;
-moz-appearance: none !important; 
border-radius: 5px !important; 
border: 1px solid rgba(0,0,8, .5) !important;
background: linear-gradient(to top,
rgba(54,64,74,0.4) 0%, rgba(0, 0, 0,.2) 100%, rgba(0,0,8,0.5)) no-repeat !important;
box-shadow: 0 0 5px rgba(44,54,71,.6) inset, 1px 2px 8px rgba(0,0,0,.3) !important;
color: #ccc !important;
text-shadow: 2px 2px 2px #333, 2px 2px 2px #333 !important; }

/* ========================= buttons */
a.button
{ border: 1px solid rgba(0,0,0, .3) !important;
box-shadow: 2px 2px 4px 4px rgba(0,0,0,.2) inset !important;
border-radius: 5px !important; }

a.button:hover, .highlight a.button:hover, #report-user-abuse:hover
{ background: rgba(155,155,155,.5) !important; }

BUTTON[type="submit"]:hover
{ color: #111 !important; }

select, .highlight a.button, BUTTON[type="submit"], #add-review, A[class="button"][href="/en-US/firefox/collections/add"], #promos > DIV > UL > LI:nth-child(4) > DIV > P:last-child > A, #report-user-abuse, .button, .button.add
{ -moz-appearance: none !important; 
border-radius: 5px !important; 
background: linear-gradient(to top,
rgba(54,64,74,0.4) 0%, rgba(255, 255, 255,.2) 100%, rgba(0,0,8,0.5)) no-repeat !important;
box-shadow: inset 2px 2px 12px 4px rgba(44,54,71,.5), 2px 4px 8px rgba(0,0,0,.4) !important;
border: 1px solid rgba(0,0,8, .5) !important;
color: #ccc !important;
text-shadow: 1px 1px 2px #333, 1px 1px 2px #333 !important; }

#add-review:hover, #search-q:focus, A[class="button"][href="/en-US/firefox/collections/add"]:hover, #promos > DIV > UL > LI:nth-child(4) > DIV > P:last-child > A:hover, .button.add:hover
{ background: rgba(155,155,155,.5) !important; }

.button.next, .button.previous, .button.prev.disabled, .button.next.disabled
{ background: none !important; }

.button.add.prominent span
{ padding-left: 0 !important; }

/* ======================== categories panel */
.highlight, #categories,
/* my collections */ .secondary-item-list.n
{ background: linear-gradient(to top,
rgba(54,64,74,0.4) 0%, rgba(0, 0, 0,.2) 100%, rgba(0,0,8,0.7)) no-repeat !important;
box-shadow: 1px 0 5px rgba(44,54,71,.5) inset, 2px 2px 14px 2px rgba(0,0,0,.4) !important;
border: 1px solid rgba(0,0,8, .5) !important;
border-radius: 5px !important; }

.categories .items, .subcategories .items
{ background: transparent !important; }

/* ========================== results panel */

#sorter>ul>li.extras ul, 
#sorter>ul>li.extras:hover>a, 
#sorter>ul>li.extras ul a:hover
{ background: linear-gradient(to top,
rgba(54,64,74,0.2) 0%, rgba(240,240,240,.3) 100%, rgba(255,255,255,0.3)) no-repeat !important;
box-shadow: 1px 0 5px rgba(0,0,0,.5) inset, 1px 2px 8px rgba(0,0,0,.3) !important; } 

#sorter>ul>li.extras:hover:after
{ background: none !important; }

.results-inner, .section-teaser.featured, .featured
{ background: none !important; }

.listing .item
{ background: linear-gradient(to top,
rgba(54,64,74,0.2) 0%, rgba(0, 0, 0,.1) 100%, rgba(0,0,8,0.1)) no-repeat !important;
box-shadow: 2px 4px 18px 2px rgba(240,240,240,.2) inset,  0px 2px 6px 1px rgba(0,0,0,.4) !important;
border: 1px solid rgba(50,50,50, .7) !important;
border-radius: 5px !important; 
margin-bottom: 2px !important; }

.featured-inner.interactive, .listing, .paginator.c
{ background: none !important;
box-shadow: none !important;
border: none !important; }

.featured-inner, #monthly > DIV
{ border-radius: 5px !important;
 background: linear-gradient(to top,
rgba(54,64,74,0.2) 0%, rgba(0, 0, 0,.1) 100%, rgba(0,0,8,0.1)) no-repeat !important;
box-shadow: 2px 4px 18px 2px rgba(240,240,240,.2) inset,  0px 2px 6px 1px rgba(0,0,0,.4) !important;
border: 1px solid rgba(50,50,50, .4) !important; }

#monthly h3, #monthly p
{ color: #ccc !important; }

.desc.vtruncate
{ font-size: 12px !important; }

.listing-header
{ border-radius: 10px 10px 0 0 !important; }

.listing-footer
{ border-radius: 0 0 10px 10px !important; }

/* ==================== addon page */

.article, HGROUP, .policy-statement, #dictionaries, .island 
{ background: linear-gradient(to top,
rgba(54,64,74,0.1) 0%, rgba(0, 0, 0,.3) 100%, rgba(0,0,8,0.1)) no-repeat !important;
box-shadow: 1px 0 5px rgba(44,54,71,.5) inset, 2px 4px 10px rgba(0,0,0,.4) !important;
border: 1px solid rgba(0,0,0, .2) !important;
border-radius: 5px !important;
font-size: 13px !important;
color: #ccc !important;
text-shadow: 1px 0px 0px #000 !important; }

HGROUP h1
{ color: #C8C3AD !important;
text-shadow: none !important;  }

#breadcrumbs
{ margin: -20px 0 20px -70px !important; }

.hovercard.addon .summary, 
.control.prev.disabled, .control.next.disabled,
.control.next, .control.prev
{ background: transparent !important; }

#addon-icon
{ -moz-transform: scale(.6,.6)!important;
padding: 0 !important; }

/* hover popups */ 
.hovercard:hover, .hover, .menu-nav>ul>li:hover>a, .menu-nav>ul>li:hover:after, .menu-nav>ul>li>ul, .menu-nav em, #site-search-suggestions .wrap
{ background: linear-gradient(to top,  
 rgba(155,155,155,.9) 0% ,rgba(199,199,199,.9) 1% ,rgba(255,255,255,.9),rgba(177,177,177,.9), rgba(255, 255, 255,.9) ) no-repeat !important;
box-shadow: inset 1px 0 5px rgba(44,54,71,.7), 2px 4px 10px rgba(0,0,0,.5) !important;
border: 1px solid rgba(0,0,0, .2) !important;
border-radius: 3px !important; }

.hovercard:hover .more
{ background: rgba(155,155,155, .4) !important;
padding: 6px !important;
border: 1px solid #444 !important;
border-radius: 3px !important; }

.sel
{ background-color: rgba(111,111,111, .3) !important;
box-shadow: inset 0px 0px 8px 2px rgba(0,0,0, .4) !important;
border: none !important; }

#site-search-suggestions .wrap
{ margin-top: -14px !important;
border: none !important; }

.menu-nav>ul>li>ul a, #site-search-suggestions .wrap ul li b, #site-search-suggestions span
{ color: #ddd !important;
text-shadow: 2px 2px 2px #000 !important; }

#site-nav > UL > LI > UL > LI > EM > A:hover, #site-search-suggestions a:hover, .sel
{ color: #CC6600 !important; }

.menu-nav>ul>li>ul a:hover
{ color: #CC9933 !important; }

.hovercard h3
{ font-family: "Segoe UI", Arial, sans-serif !important; }

.more
{ font-family: "Segoe UI", Arial, sans-serif !important;
font-size: 13px !important;
color: black !important;
font-weight: bold !important;
text-shadow: 2px 0px 6px #999, 2px 0px 6px #777 !important; }

.version.item > DIV > UL > LI
{ font-weight: bold !important;
color: black !important; }

.version.item div
{ padding: 6px !important; }

/* ========== details =========== */
.island>section 
{ border-color: #555 !important; }

header hgroup
{ margin-bottom: 10px !important;
padding-left: 4px !important; } 

#footer .section
{ margin-left: 100px !important; }

#footer #lang_form
{ margin-left: 0 !important;
margin-top: -10px !important; }

#social-footer
{ height: 32px !important;
font-size: 16px !important;
padding: 0 !important; }

#footer-menu li
{ width: 80px !important;
margin: 0 !important;
padding: 0 !important; }

#footer-menu ul li li
{ padding: 0 !important; }

BLOCKQUOTE
{ overflow-x: visible !important; }

h4.author, h4.author a
{ top: -12px !important;
left: -2px !important;
color: black !important; }

.section-teaser .prev, .section-teaser .next
{ margin-bottom: -14px !important; }

.addon-info.js h3, #addon-reviews h3
{ text-shadow: 2px 2px 2px #444 !important;  }

HGROUP
{ background: none !important;
border: none !important;
box-shadow: none !important;  }

/* rating graph bar */
.grouped_ratings .rating_bar span.bar
