css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
body {    background:#222222  !important;  }

ol#posts li blockquote,body.mceContentBody blockquote {margin-left: 0 !important;margin-right: 0 !important;padding-left: 10px !important;border-width: 4px !important;border-color: #000000 !important;}

ol#posts li blockquote blockquote,body.mceContentBody blockquote blockquote {border-color: #000000 !important;}

ol#posts li blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote {border-color: #000000 !important;}

ol#posts li blockquote blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote blockquote {border-color: #000000 !important;}

ol#posts li blockquote blockquote blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote blockquote blockquote {border-color: #000000 !important;}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {    background color: #ffffff; !important;  }

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {    background:#222222 !important;    -webkit-border-radius: 20px;    -moz-border-radius: 20px;    border-radius: 20px;    margin: auto;    padding: 20px 20px;  }h1 {color: #333333;text-shadow: 0 0 3px #000000;}

.no_posts_found, h1.dashboard_header {color: #000000;}

img#content_top, img#content_bottom {display: none;}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {    background: none;  }

#right_column .dashboard_nav_item {    background: none repeat-x;    background-attachment: initial;    background-position-x: 50%;    background-position-y: 100%;    background-origin: initial;    background-clip: initial;    background-color: initial;}

#right_column .dashboard_nav_item #hide_radar {background-image: none;background-repeat-x: no-repeat;background-repeat-y: repeat;background-attachment: initial;background-position-x: 0%;background-position-y: 0%;background-origin: initial;background-clip: initial;background-color: #333333;}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {background: rgba(0,0,0,0.0);}

#nav .nav_item.active {    background: rgba(00,0,0,0.5) !important;  }

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {    background: rgba(00,0,0,0.5) !important;  }

ol#posts li.notification.alt {    background-color: rgba(0,0,0,0.5);}

ol#posts li.notification.first_notification {    background-color: rgba(0,0,0,0.5);}

ol#posts li.notification {    background-color: rgba(0,0,0,0.5);    border-bottom: 1px solid #000000;}

ol#posts li.notification.last_notification {    border-bottom: 1px solid #000000;}

ol#posts li.post .permalink {    background: rgba(00,0,0,0.5);}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {background: rgba(0,0,0,0.8);border: solid #000000;}

ol#posts li.post .post_avatar{border-bottom: 0px solid #000000;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {border-top: 1px solid #000000;border-bottom: 1px solid #000000;}

#right_column a#dashboard_switch_blog_menu_current:hover {background: none;}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {background: none;}

form.dashboard_options_form {background-color: rgba(0,0,0,0.0);border-bottom: 1px solid #000000;border-bottom-left-radius: 10px 10px;border-bottom-left-radius: 10px 10px;border-bottom-right-radius: 10px 10px;border-bottom-right-radius: 10px 10px;border-top-left-radius: 10px 10px;border-top-left-radius: 10px 10px;border-top-right-radius: 10px 10px;border-top-right-radius: 10px 10px;color: #000000;display: block;font-size: 13px;margin-bottom: 25px;padding: 15px 22px;}form.dashboard_options_form .option_container {border-top: 1px solid #000000;}

.no_posts_found, h1.dashboard_header {color: #333333;}

div#right_column,div#right_column:after{
background: none !important;
}

#dashboard_controls_open_blog #open_blog_link {
  color: #ffffff;
}

div.selection_nipple {
display:none; !important;
}

div.nipple {
display:none !important;
}

.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){
background-repeat:no-repeat!important;
width:9px!important;
height:9px!important;
margin-top:-12px!important}.selection_nipple.white{background-position:0 0!important;
width:35px!important;
height:35px!important}.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0)!important}

#inbox_button.tab.iconic.selected .selection_nipple:not([class="selection_nipple white"]){margin-left:-10px!important}

#pagination a {
color: #ffffff;
text-decoration: none;
background-color: #222222;
padding: 5px 8px;
margin-left: 10px;
white-space: nowrap;
}

.footer_links a {
display: block;
color: #ffffff;
white-space: nowrap;
text-decoration: none;
padding: 0 6px;
}

#footer {
text-align: center;
color: #ffffff;
font-size: 13px;
}

.plexi.show.color {
  background: #222222;}
  
#dashboard_controls a, #dashboard_controls .control {
color: #ffffff;
text-decoration: none;
  text-shadow: #222222 1px 1px 1px;}

#dashboard_controls .dashboard_controls_arrow_right {
  display: none;
}

#dashboard_controls .dashboard_controls_arrow_left, #dashboard_controls .dashboard_controls_arrow_right, #dashboard_controls .dashboard_controls_arrow_first_page {
width: 0px;
height: 0px;
}
}
