  background-color: #171717 !important;	
  background-position: 0% 0% !important;
  background-size: cover !important;


}


div.l-header .tab.iconic a{
  background-color: #3caa57 !important;
  border-radius: 20px !important;  
}

#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 270px !important;
  height: 85px !important;
  background-repeat: no-repeat;
}


img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],

.new_post_label_icon {

opacity: 0.00 !important;  

}


#new_post{

  background-image: url(http://i911.photobucket.com/albums/ac318/niteskyzang3l/DashIcons/seiji-1_zps16210ba9.png) !important;
  background-position: top center !important; 
  background-repeat: no-repeat !important;

}


#new_post {

  opacity: 1.0 !important;

  background-color: #ffffff !important;
  border-bottom: 2px solid rgba(252,199,246,0)!important;
  border-top: 2px solid rgba(252,199,246,0)!important;
}


#posts LI[class*=" post"] {

  
