css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://eksisozluk.com/), url-prefix(http://eksisozluk.com/), url-prefix(https://eksisozluk.com/) {

body > header {background-color: #2d425f !important;}

body > header a{color: #ffffff !important;}

body > header a:hover {color: #000000 !important;}

body {background-color:#2d425f !important;width:100% !important;}

body a {color:#9efcfd !important;}

body a:hover{text-decoration:none !important; color:#cff1fc !important;}



#container {float:left !important;margin-left: 10px !important;width:97% !important;max-width:none !important;max-width:1400px !important;color:#ffffff !important;}

#content-section {margin:0 !important; width:100% !important;}

#content {margin-right:17px !important; margin-left:17px !important;}

#main {margin-left: 250px!important;}
#entry-list footer .entry-date {
    color: #ffffff !important;}

.sub-title-menu a, #entry-list footer div.feedback a {color:#cccccc !important;}

.sub-title-menu a:hover, #entry-list footer div.feedback a:hover {color:#cff1fc !important;}

#entry-list footer time, #entry-list footer address {color:#cccccc !important;}
h1 small, h2 small, h3 small, h4 small, h5 small, h6 small {color:#cccccc !important;}
#entry-list > li:before {color:#cccccc !important; border-bottom-color: #999999 !important;}
small {color:#cccccc !important;}
#sub-navigation {background-color: #2d425f !important;}

#sub-navigation a:hover {color:#9efcfd !important;}

#top-navigation > ul .new-update, #sub-navigation > ul .new-update {

    background-color: rgba(60, 255, 0, 0.8) !important;}

#top-navigation > ul > li > a { 

background-color: #444444 !important;

border-radius:8px !important;

border-width:1px !important;

border-style: solid !important;

border-color: #333333 !important;

}



#threads li article.unread h2 {color:#ffa200 !important;}

#threads li article.unread h2 small {color: #ffffff !important;}

#threads li article.unread p {color: #ababaa !important;}



#index-section {width:250px !important; background-color:#2d425f !important; }

#partial-index .topic-list.partial li > a {padding-top:0px !important; padding-bottom:0px !important;}

.topic-list li {border-top-width:1px !important; border-top-color:#888888 !important;}



#aside{display: none !important;}

#message-thread {max-width:860px !important;}

html.no-touch .topic-list li > a {color:#dddddd !important;}

html.no-touch .topic-list li > a:hover {

background-color:#2d425f !important;

color:#9efcfd !important;

}

fieldset legend {color:#999999 !important;}



html.no-touch #feed-refresh-link:hover {

color:#9efcfd !important;

background-color: #111111 !important;}

html.no-touch .dropdown .dropdown-menu li a {color:#000000 !important;}

html.no-touch .dropdown .dropdown-menu li:hover {

    background-color: #888888 !important;

    }

.topic-list.partial li.numbered > a > small {

    position: static !important;

    color:#9efcfd !important;}

h1, h2, h3, h4, h5, h6 {color:#9efcfd !important;}



label, input, select, textarea {color: #515151 !important;}



#user-badges a {background-color:#ffffff !important;}

.tabs {background-color: #2d425f !important;}

.tabs a:hover {background-color:#9efcfd !important; color:#000000 !important;}

html.no-touch #threads li article:hover {background-color: #3e5a82 !important; }

#message-thread .incoming, #message-thread .outgoing {background-color: #3a3a3a !important;

color:#ffffff !important;}

#threads li article p {color: #ffffff !important;}

.share-links li > a {color: #000000 !important;}
.share-links li > a:hover {color: #888888 !important;}
#user-entry-stats li {color:#cccccc !important;}
.ui-autocomplete li a {color:#000000 !important;}
button.primary, input.primary[type="submit"], input.primary[type="button"] {
background-color:#555555 !important;}
#entry-list .caylak {background-color: #403a2d !important;}

h2 {font-size: 15px !important;}

}
