css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ankiweb.net") {


html,body{
background:#888!important;
cursor:default;
}
button{
cursor:pointer;
}

img{
opacity:1!important;
}

#content, #qa, #qa_box{
background:#f0f0f0!important;
}

SPAN#leftStudyMenu A{
cursor:pointer!important;
}

A.mitem{
color:white!important;
background:none!important;
border:none!important;
}
A.mitem:hover{
text-shadow: 1px 0px 2px #A88E22;
}
DIV.smallText{
text-shadow: 1px 0px 2px #A88E22;
opacity:0.3;
}

SPAN#leftStudyMenu a{
cursor:default;
background:white!important;
}
SPAN#leftStudyMenu a:hover{
box-shadow:1px 1px 7px #A88E22;
}

DIV#footerInner{
display:none;
}
DIV#footerInner:hover{
opacity:1;
}

BUTTON{
height:50px;
}

DIV#qa{
font-family:"Droid Arabic Naskh";
font-size: 34px;
text-shadow: 1px 0px 3px #000;
}
DIV#qa span[style*='color:']{
text-shadow: 0px 0px 1px #000;
}

DIV#easebuts button{
background:#8BC5D8!important;
color:black!important;
text-shadow: 0px 0px 37px #ffffff!important;
}

DIV#easebuts button:hover{
text-shadow: 0px 0px 7px #ffffff!important;
box-shadow: 0px 0px 7px #000!important;
}

DIV#easebuts button#ease1{
background:#3E0000!important;
color:white!important;
text-shadow: 0px 0px 37px #ffffff!important;
}
DIV#easebuts button#ease1:hover{
text-shadow: 0px 0px 1px #000!important;
box-shadow: 0px 0px 7px #ff0000!important;
}

td.answertd{
color:#999!important;
}

}
