css:
@-moz-document domain("userstyles.org") {
 /*
 * Author Page
*/
.overlay_background,
.navigation,
.walking,
.android_button_button,
.android_button_banner
{ display: none !important; }

.view_inner {
  width: 100%;
  max-width: 1100px;
  padding-top: 2em;
}

table.author-styles tr>td {
  padding: 4px 8px;
}

table.author-styles tr>:nth-child(6),
table.author-styles tr>:nth-child(7) {
  display: none;
}

table.author-styles tr>:nth-child(1) a {
  font-size: 16px;
  text-decoration: none;
  display: inline-block;
}

.statistic table, th, td {
  border-color: #ccc;
}

 /*
 * Edit Page
*/
input[type="text"], textarea {
  border: 1px solid #ddd;
  font-size: 16px;
}
input[type="button"],
input[type="submit"]{
  display: inline-block;
  align-items: center;
  background-color: #3498db;
  color: #fff;
  height: 36px;
  border-bottom: solid 3px #627295;
  border-radius: 3px;
  min-width: 100px;
}

}
