   Updated February 9, 2014 */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.freerepublic.com") {

body.index-page h3{
    font-size : medium !important;
    margin-bottom : 0 !important;
    margin-top : 1em !important; }
    
body.index-page ul.listing .meta .attrib {
    margin-top : 0 !important; }

body.index-page ul.listing div.text {
    margin-top : 0 !important; }
    
body.index-page ul.listing div.content {
    margin-left : 0 !important; }

* { background: rgb(116, 111, 111) !important;
 border-color: rgba(79, 79, 79, 0.96) !important;
 font-family:Arial, Helvetica, sans-serif !important;
 color: rgba(190, 190, 190, 0.75) !important;
 text-shadow: rgba(79, 79, 79, 0.95) 0px 0px 1px,
 rgba(79, 79, 79, 0.9) 0px 0px 2px,
 rgba(79, 79, 79, 0.9) 0px 0px 3px,
 rgba(79, 79, 79, 0.8) 0px 0px 4px,
 rgba(79, 79, 79, 0.7) 0px 0px 5px,
 rgba(79, 79, 79, 0.6) 0px 0px 6px !important;
 letter-spacing: 0.01em !important;
 text-decoration: none !important; }

/*     Text Selection Color     */
 ::-moz-selection {  background-color: rgba(190, 190, 190, 0.75)  !important; 
        color: rgba(190, 190, 190, 0.75)  !important; }   


/* JAN 26 2013 PROBLEM:  body.index-page ul.listing .meta .attrib a, */

a, body.index-page ul.listing .meta .attrib a, a * { color: rgba(211, 211, 211, 0.95) !important;
    transition-property: text-shadow !important;
	transition-duration: 1.5s !important;
	transition-timing-function: linear !important;
	/* Firefox 4 */
	-moz-transition-property:text-shadow !important;
	-moz-transition-duration:1.5s !important;
	-moz-transition-timing-function:linear !important;
	/* Safari and Chrome */
	-webkit-transition-property:text-shadow !important;
	-webkit-transition-duration:1.5s !important;
	-webkit-transition-timing-function:linear !important;
	/* Opera */
	-o-transition-property:text-shadow !important;
	-o-transition-duration:1.5s !important;
	-o-transition-timing-function:linear !important;
        outline: 0 !important; }

a:visited, body.index-page ul.listing .meta .attrib a, a:visited * { color: rgba(211, 211, 211, 0.95) !important;
        transition-property: text-shadow !important;
	transition-duration: 1.5s !important;
	transition-timing-function: linear !important;
	/* Firefox 4 */
	-moz-transition-property:text-shadow !important;
	-moz-transition-duration:1.5s !important;
	-moz-transition-timing-function:linear !important;
	/* Safari and Chrome */
	-webkit-transition-property:text-shadow !important;
	-webkit-transition-duration:1.5s !important;
	-webkit-transition-timing-function:linear !important;
	/* Opera */
	-o-transition-property:text-shadow !important;
	-o-transition-duration:1.5s !important;
	-o-transition-timing-function:linear !important; }

a:hover, /* body.index-page ul.listing .meta .attrib a, */ a:hover * { color: rgba(255, 255, 255, 0.75) !important;
 text-shadow: rgb(255, 255, 0) 0px 0px 3px, 
  rgb(255, 128, 0) 0px 0px 4px, 
  rgb(255, 128, 0) 0px 0px 5px, 
  rgb(255, 0, 0) 0px 0px 6px,  
  rgb(255, 0, 0) 0px 0px 8px !important;
        transition-property: text-shadow, color !important;
	transition-duration: 0.2s !important;
	transition-timing-function: linear !important;
	/* Firefox 4 */
	-moz-transition-property:text-shadow, color !important;
	-moz-transition-duration:0.2s !important;
	-moz-transition-timing-function:linear !important;
	/* Safari and Chrome */
	-webkit-transition-property:text-shadow, color !important;
	-webkit-transition-duration:0.2s !important;
	-webkit-transition-timing-function:linear !important;
	/* Opera */
	-o-transition-property:text-shadow, color !important;
	-o-transition-duration:0.2s !important;
	-o-transition-timing-function:linear !important; }

    input[type=text], textarea {
  -webkit-transition: all 0.30s ease-in-out !important; 
  -moz-transition: all 0.30s ease-in-out !important; 
  -ms-transition: all 0.30s ease-in-out !important; 
  -o-transition: all 0.30s ease-in-out !important; 
  outline: none !important; 
  padding: 3px 0px 3px 3px !important; 
  margin: 5px 1px 3px 0px !important; 
  border: 1px solid rgba(190, 190, 190, 0.75) !important; 
}
 
input[type=text]:focus, textarea:focus {
  box-shadow: 0 0 5px #DCDCDC !important; 
  padding: 3px 0px 3px 3px !important; 
  margin: 5px 1px 3px 0px !important; 
  border: 1px solid rgba(190, 190, 190, 0.75) !important; 
}

  /* Top Shadow */
body:before {
          content: "" !important;
          position: fixed !important;
          top: -10px !important;
          left: 0 !important;
          width: 100% !important;
          height: 10px !important;

          -webkit-box-shadow: 0px 0px 10px rgba(0,0,0,.8) !important;
           box-shadow: 0px 0px 10px rgba(0,0,0,.8) !important;
           z-index: 100 !important; }

div#header strong { color: rgb(154, 205, 50) !important;
 text-shadow: rgba(79, 79, 79, 0.95) 0px 0px 1px,
 rgba(79, 79, 79, 0.9) 0px 0px 2px,
 rgba(79, 79, 79, 0.9) 0px 0px 3px !important;
 text-decoration:blink !important;
 text-transform:capitalize !important; } 
 
 div#header strong:hover { color: rgba(255, 255, 255, 0.75) !important;
 text-shadow: rgb(255, 255, 0) 0px 0px 3px, 
  rgb(255, 128, 0) 0px 0px 4px, 
  rgb(255, 128, 0) 0px 0px 5px, 
  rgb(255, 0, 0) 0px 0px 6px,  
  rgb(255, 0, 0) 0px 0px 8px !important;
        transition-property: text-shadow, color !important;
	transition-duration: 0.2s !important;
	transition-timing-function: linear !important;
	/* Firefox 4 */
	-moz-transition-property:text-shadow, color !important;
	-moz-transition-duration:0.2s !important;
	-moz-transition-timing-function:linear !important;
	/* Safari and Chrome */
	-webkit-transition-property:text-shadow, color !important;
	-webkit-transition-duration:0.2s !important;
	-webkit-transition-timing-function:linear !important;
	/* Opera */
	-o-transition-property:text-shadow, color !important;
	-o-transition-duration:0.2s !important;
	-o-transition-timing-function:linear !important; }
   
}
