css:
@-moz-document domain("www.gooshe.net") {
@font-face {
	font-family:"BBCNassim";
	src: url( "http://lab.3hh.ir/negro/wp-content/themes/negroid/fonts/BBCNassim.eot" );
	src: local( "â˜º" ),
	url( "http://lab.3hh.ir/negro/wp-content/themes/negroid/fonts/BBCNassim.woff" )
	format( "woff" ),
	url( "http://lab.3hh.ir/negro/wp-content/themes/negroid/fonts/fonts/BBCNassim.ttf" )
	format( "truetype" );
}



body, input, textarea {
	font-family:BBCNassim, BMitra,"Helvetica Neue",Helvetica,Arial,"Nimbus Sans L",sans-serif !important;
}

a {
	transition: color .4s;
	-moz-transition: color .4s;
	-webkit-transition: color .4s;
	-o-transition: color .4s;
}

img {
	opacity:1;
	transition: opacity .4s;
	-moz-transition: opacity .4s;
	-webkit-transition: opacity .4s;
	-o-transition: opacity .4s;
}

img:hover {opacity:.7;}


.meta-nav {display:none}
.home #content .post {background:rgba(255,255,255,0.1) !important;}
.entry-title a {color:black !important; text-shadow:0 1px white !important;}
.entry-content p {text-align:justify !important;}
.entry-title {color:black !important; background:rgba(255,255,255,0.3) !important; padding:10px !important; border-bottom:2px solid rgb(166,166,166) !important; font-size:30px !important;}
.entry-date {color:black !important;}
.af-default #content .entry-content { color:black !important; font-weight:normal !important;}
.af-default #content .entry-content a {color:black !important;}


.single #content header {
	clear: both;
    float: none !important; 
    margin: 0 220px 0 0 !important;
    padding: 0 !important;
    display: table !important;
 }

.single #content header .entry-title {
  font-size: 42px !important;
      line-height: 48px !important;
      display: table-header-group !important;
      padding: 11px 8px !important;
      -webkit-margin-before: 0 !important;
    -webkit-margin-after: 0 !important;
}
#content .entry-title {
      font-weight:normal !important;
}
    
.single #content header .entry-date {
    margin: 0 !important;
    font-size: 14px !important;
    padding: 11px 16px !important;
    display: table-footer-group !important;
    vertical-align: middle !important;
}
.menu li a {
	font-weight:normal !important;
}
#site-description {
	padding: 0 8px !important;
	margin: 10px 70px 0 0 !important;
	font-size: 16px !important;
}

#content {font-size:16px !important;}
#content h3 {line-height:2 !important;}
        
content .video-player, #content object, #content iframe {margin: 0 !important;}
        
.single #content .entry-content {
	clear: left !important;
	display: block !important;
	float: left !important;
	line-height: 22px !important;
	width: 500px !important;
    right:-88px !important;
}

    
    
[class^="nav-"], [class*=" nav-"] {
	text-align:right !important;
}
#comments, #comments .commentlist ul.children li {
	margin-right:0 !important;
}
.fn, .commentmetadata {
	position: relative;
    right: 60px;
    width:auto !important;
}
.avatar {z-index:15 !important;}
}
