css:
@-moz-document url('http://www.tokyo-np.co.jp/'),
url-prefix('http://www.tokyo-np.co.jp/article/'),
url-prefix('http://www.tokyo-np.co.jp/s/article/'),
url-prefix('http://www.tokyo-np.co.jp/koudoku/'),
url-prefix('http://www.tokyo-np.co.jp/sitemap/'),
url-prefix('http://www.tokyo-np.co.jp/tokyoguide/'),
url-prefix('http://www.tokyo-np.co.jp/igo-shogi/'),
url-prefix('http://www.tokyo-np.co.jp/event/'),
url-prefix('http://www.tokyo-np.co.jp/movie/'),
url-prefix('http://www.tokyo-np.co.jp/gogai/'),
url-prefix('http://www.tokyo-np.co.jp/daizukai/'),
url-prefix('http://www.tokyo-np.co.jp/seikatuzukan/'),
url-prefix('http://www.tokyo-np.co.jp/k-shiken/'),
url-prefix('http://www.tokyo-np.co.jp/gakujin/'),
url-prefix('http://www.tokyo-np.co.jp/tbook/'),
url-prefix('http://www.tokyo-np.co.jp/book/'),
url-prefix('http://www.tokyo-np.co.jp/policy/'),
url-prefix('http://www.tokyo-np.co.jp/mobile/'),
url-prefix('http://www.tokyo-np.co.jp/omoide/'),
url-prefix('http://www.tokyo-np.co.jp/database/') {
/*== tokyo-np.co.jp ==*/

/*-- unwanted contents --*/
.yahoo_ad,
.PR2,
#Banner-area,
.Contents-cm
 {display:none !important;}

/*-- generic table breaker --*/
table, caption, thead, tfoot, tbody, tr, th, td {
 width: auto !important; min-width: 0px !important; max-width: none !important;
 height: auto !important; min-height: 0px !important; max-height: none !important;
 display: inline-block !important;
 }

/*-- logo & search bar --*/
#Logo-area {height: auto !important;}
#Logo-area tr:first-child {display: none !important;}
#Logo-area h1 {display: inline !important;}
#Logo-area .logo img {margin: 0 !important;}
#Logo-area .search-area
 {float: right; font-size: inherit !important; padding: 0 !important;}

/*-- navi bar1 % 2 --*/
.Gnavi-chu1 .menu, .Gnavi-chu2 .menu, .Gnavi-spo1 .menu, .Gnavi-spo2 .menu
 {padding: 0 !important;}
.Gnavi-chu1 td, .Gnavi-chu2 td, .Gnavi-spo1 td, .Gnavi-spo2 td
 {white-space: normal !important;}
.Gnavi-chu1 .menu .navi-area, .Gnavi-chu1 .link .navi-area
 {height: auto !important; margin: 0 0 0.2em 0 !important;}
.Gnavi-chu1 .menu table, .Gnavi-spo1v .menu table
 {margin: 0 !important;}
.Gnavi-chu1 .menu td.first, .Gnavi-spo1 .menu td.first
 {padding-left: 0.2em !important;}
.Gnavi-chu1 .menu td, .Gnavi-spo1 .menu td
 {font-size: inherit !important; padding: 0 0.2em !important;}
.Gnavi-chu1 .link, .Gnavi-chu2 .link, .Gnavi-spo1 .link, .Gnavi-spo2 .link
 {padding: 0 !important;}
.Gnavi-chu1 .link img, .Gnavi-spo1 .link img
 {margin: 0 !important; content: attr(alt);}
.Gnavi-chu2 .navi-area, .Gnavi-spo2 .navi-area
 {height: auto !important;}

/*-- articles 2columns layout --*/
table#i_Topbody {margin: 0 !important;}
#Contents {
 width: 100% !important; min-width: 460px;
 padding: 0 !important;
 margin-right: -340px;
 }
#Contents>a, #Contents>br {display: none !important;}
#Contents>div, #Contents>table {width: auto; margin-right: 340px !important;}

/*-- left side --*/
.Headline .picnews-area2 .picnews {
 background-color: white !important; 
 margin: 0 !important; padding: 0 !important;
 width: auto !important;
 }
.Headline .picnews-area2 table.photo td.caption { 
 font-size: inherit !important;
 padding: 0 !important;
 }
.index-tab #NewsLinkArea td.on span,
.index-tab #NewsLinkArea td.off span
 {width: auto !important; min-width: 3em;}
.pagetop {font-size: inherit !important; margin: 0 !important; padding: 0 !important;}
.pagetop p{background-color: pink; margin: 0 !important; text-align: center;}

/*-- footer --*/
#Footer-area {margin: 0 !important;}
#Footer-area .footer01 {height: auto !important;}
#Footer-area .copy {font-size: inherit !important; padding: 0 !important;}
}
