@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Web Developer Tabs */
/* 05/2/2013 */
/* by mo_he_ji */

.tabbrowser-tab {
  -moz-appearance: none!important;
  border: 0 solid transparent !important; 
  background:transparent !important;
}

#TabsToolbar:not(:-moz-lwtheme) {
  box-shadow: 0 1px 0 hsla(209,29%,72%,.25) inset !important;
  background-image: -moz-linear-gradient(top, hsl(209,18%,34%), hsl(210,24%,16%)) !important;
  border-left: 1px solid rgba(10%,10%,10%,.4) !important;
  border-right: 1px solid rgba(10%,10%,10%,.4) !important;
  border-top: 1px solid #0C1620 !important;
  border-top-left-radius: 0px !important;
  border-top-right-radius: 0px !important;
}

#TabsToolbar:-moz-lwtheme {
  margin-top: -2px !important;
  margin-bottom: -5px !important;
}

#main-window[tabsontop="true"] #nav-bar:not(:-moz-lwtheme) {
  border-top-left-radius: 0px !important;
  border-top-right-radius: 0px !important;
}

#TabsToolbar > toolbarbutton:-moz-lwtheme,
#TabsToolbar .toolbarbutton-1:-moz-lwtheme {
  margin-top: 4px !important;
  margin-bottom: 5px !important;
}

.tab-close-button {
  list-style-image: url("chrome://browser/skin/devtools/toolbarbutton-close.png") !important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
  min-width: 16px !important;
  width: 16px !important;
  -moz-appearance: none !important;
  border-style: none !important;
  margin-right: 0px !important;
}

.tab-close-button:hover {
  -moz-image-region: rect(0, 32px, 16px, 16px) !important;
}

.tab-close-button:hover:active {
  -moz-image-region: rect(0, 48px, 16px, 32px) !important;
}

.tab-stack {
  min-height: 28px !important;
}

#TabsToolbar .arrowscrollbox-scrollbox {
  padding-top: 1px !important;
}

.tab-stack > .tab-content {
  -moz-appearance: none !important;
  border-style: solid !important;
  border-width: 2px 13px !important;
  outline: none !important;
}

.tab-content:not([selected="true"]):not(:hover):not(:-moz-lwtheme) {
  color: rgb(205, 216, 228) !important;
  text-shadow: rgba(11, 12, 13, 0.45) 0px -1px 0px !important;
}

.tab-content[selected="true"]:not(:-moz-lwtheme),
.tab-content:hover:not(:-moz-lwtheme) {
  color: hsl(208,100%,60%) !important;
  text-shadow: 0 0 6px hsl(208,100%,60%) !important;
}

.tab-throbber[pinned],
.tab-icon-image[pinned] {
  -moz-margin-start: 5px !important;
  -moz-margin-end: 5px !important;
}

.tabbrowser-tab[pinned]:first-child > .tab-stack > .tab-content:not([selected="true"]) {
  -moz-appearance: none !important;
  -moz-box-align: center !important;
  margin-top: 1px !important;
  margin-bottom:1px !important;
  border: 1px solid hsla(211,68%,6%,.4) !important;
  background: -moz-linear-gradient(hsla(209,13%,54%,.35), hsla(209,13%,54%,.1) 85%, hsla(209,13%,54%,.2)) padding-box !important;
  box-shadow: 0 1px 0 hsla(209,29%,72%,.15) inset, 0 0 0 1px hsla(209,29%,72%,.1) inset, 0 0 0 1px hsla(209,29%,72%,.1), 0 1px 0 hsla(210,16%,76%,.1) !important;
}

.tabbrowser-tab[pinned]:not(:first-child) > .tab-stack > .tab-content:not([selected="true"]) {
  -moz-appearance: none !important;
  -moz-box-align: center !important;
  margin-top:1px !important;
  margin-bottom: 1px !important;
  border-top: 1px solid hsla(211,68%,6%,.4) !important;
  border-bottom: 1px solid hsla(211,68%,6%,.4) !important;
  border-left: 1px solid transparent !important;
  border-right: 1px solid hsla(211,68%,6%,.4) !important;
  background: -moz-linear-gradient(hsla(209,13%,54%,.35), hsla(209,13%,54%,.1) 85%, hsla(209,13%,54%,.2)) padding-box !important;
  box-shadow: 0 1px 0 hsla(209,29%,72%,.15) inset, 0 0 0 1px hsla(209,29%,72%,.1) inset, 0 0 0 1px hsla(209,29%,72%,.1), 0 1px 0 hsla(210,16%,76%,.1) !important;
}

.tabbrowser-tab[pinned] > .tab-stack > .tab-content {
  border-radius: 3px !important;
}

.tabbrowser-tab[pinned]:first-child:not(:last-child) > .tab-stack > .tab-content {
  border-radius: 3px 0 0 3px !important;
}

.tabbrowser-tab[pinned]:not(:first-child) > .tab-stack > .tab-content {
  border-radius: 0 !important;
  margin-left: -1px !important;
}

.tabbrowser-tab[pinned]:last-child:not(:first-child) > .tab-stack > .tab-content {
  border-radius: 0 3px 3px 0!important;
  margin-left: -1px !important;
}

.tabbrowser-tab[pinned]:first-child > .tab-stack > .tab-content[selected="true"] {
  -moz-appearance: none !important;
  -moz-box-align: center !important;
  margin-top: 1px !important;
  margin-bottom: 1px !important;
  border: 1px solid hsla(211,68%,6%,.5) !important;
  background: -moz-linear-gradient(hsla(211,68%,6%,.1), hsla(211,68%,6%,.2)) !important;
  box-shadow: 0 1px 3px hsla(211,68%,6%,.5) inset, 0 0 0 1px hsla(209,29%,72%,.1), 0 1px 0 hsla(210,16%,76%,.1) !important;
}

.tabbrowser-tab[pinned]:not(:first-child) > .tab-stack > .tab-content[selected="true"] {
  -moz-appearance: none !important;
  -moz-box-align: center !important;
  margin-top: 1px !important;
  margin-bottom: 1px !important;
  border: 1px solid hsla(211,68%,6%,.5) !important;
  border-left: 1px solid hsla(211,68%,6%,.2) !important;
  background: -moz-linear-gradient(hsla(211,68%,6%,.1), hsla(211,68%,6%,.2)) !important;
  box-shadow: 0 1px 3px hsla(211,68%,6%,.5) inset, 0 0 0 1px hsla(209,29%,72%,.1), 0 1px 0 hsla(210,16%,76%,.1) !important;
}

.tabbrowser-tab[pinned][titlechanged] > .tab-stack > .tab-content:not([selected="true"]) {
  background-image: -moz-radial-gradient(center 3px, circle cover, rgba(255,255,255,1) 3%, rgba(186,221,251,.75) 40%, rgba(127,179,255,.5) 80%,
  rgba(127,179,255,.25)) !important;
}

.tabbrowser-tab[pinned][titlechanged] > .tab-stack > .tab-content:not([selected="true"]):hover {
  background-image: -moz-linear-gradient(hsla(0,0%,100%,.4), hsla(0,0%,75%,.4) 80%),
                    -moz-radial-gradient(center 3px, circle cover, rgba(255,255,255,1) 3%, rgba(186,221,251,.75) 40%, rgba(127,179,255,.5) 80%,
                    rgba(127,179,255,.25)) !important;
}

#tabbrowser-tabs:not([overflow="true"]) > tab:not([pinned]):first-child:not(:last-child) > .tab-stack > .tab-content:not([selected="true"]) {
  -moz-border-image: url("chrome://browser/skin/devtools/breadcrumbs/ltr-start.png") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

#tabbrowser-tabs:not([overflow="true"]) > tab:not([pinned]):first-child:not(:last-child) > .tab-stack > .tab-content[selected="true"] {
  -moz-border-image: url("chrome://browser/skin/devtools/breadcrumbs/ltr-start-selected.png") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

#tabbrowser-tabs[overflow="true"] > tab:not([pinned]):first-child:not(:last-child) > .tab-stack > .tab-content:not([selected="true"]) {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

#tabbrowser-tabs:not([overflow="true"]) > tab:not([pinned]):first-child:last-child > .tab-stack > .tab-content[selected="true"] {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: 0px !important;
}

#tabbrowser-tabs[overflow="true"] > tab:not([pinned]):first-child:not(:last-child) > .tab-stack > .tab-content[selected="true"] {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

#tabbrowser-tabs:not([overflow="true"]) .tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]):last-child > .tab-stack > .tab-content:not([selected="true"]) {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: 0px !important;
}

#tabbrowser-tabs[overflow="true"] > tab:not([pinned]):first-child:last-child > .tab-stack > .tab-content[selected="true"],
#tabbrowser-tabs:not([overflow="true"]) .tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]):last-child > .tab-stack > .tab-content[selected="true"] {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: 0px !important;
}

.tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]):not(first-child):not(:last-child) > .tab-stack > .tab-content:not([selected="true"]) {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

.tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]):not(first-child):not(:last-child) > .tab-stack > .tab-content[selected="true"] {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

#tabbrowser-tabs > tab:not([pinned]):first-child ~ *:not(:last-child) > .tab-stack > .tab-content:not([selected="true"]),
.tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned])~*:not(:last-child) .tab-stack > .tab-content:not([selected="true"]) {
  -moz-border-image: url("chrome://browser/skin/devtools/breadcrumbs/ltr-middle.png") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

#tabbrowser-tabs > tab:not([pinned]):first-child ~ *:not(:last-child) > .tab-stack > .tab-content[selected="true"],
.tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned])~*:not(:last-child) .tab-stack > .tab-content[selected="true"] {
  -moz-border-image: url("chrome://browser/skin/devtools/breadcrumbs/ltr-middle-selected.png") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

#tabbrowser-tabs:not([overflow="true"]) .tabbrowser-tab:not([pinned]) ~ :last-child > .tab-stack > .tab-content:not([selected="true"]) {
  -moz-border-image: url("chrome://browser/skin/devtools/breadcrumbs/ltr-end.png") 2 13 2 13 fill stretch !important;
  margin-right: 0px !important;
}

#tabbrowser-tabs:not([overflow="true"]) .tabbrowser-tab:not([pinned]) ~ :last-child > .tab-stack > .tab-content[selected="true"] {
  -moz-border-image: url("chrome://browser/skin/devtools/breadcrumbs/ltr-end-selected.png") 2 13 2 13 fill stretch !important;
  margin-right: 0px !important;
}

#tabbrowser-tabs[overflow="true"] .tabbrowser-tab:not([pinned]) ~ :last-child > .tab-stack > .tab-content:not([selected="true"]) {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: 0px !important;
}

#tabbrowser-tabs[overflow="true"] .tabbrowser-tab:not([pinned]) ~ :last-child > .tab-stack > .tab-content[selected="true"] {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: 0px !important;
}

#tabbrowser-tabs[overflow="true"] .tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]):last-child > .tab-stack > .tab-content:not([selected="true"]) {
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

#tabbrowser-tabs[overflow="true"] .tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]):last-child > .tab-stack > .tab-content[selected="true"]{
  -moz-border-image: url("data:image/png;base64,") 2 13 2 13 fill stretch !important;
  margin-right: -11px !important;
}

.tabbrowser-tabs[positionpinnedtabs="true"] > arrowscrollbox > .scrollbutton-up {
  padding-left: 4px !important;
}

.tabbrowser-tab[pinned] > .tab-stack > .tab-content {
  -moz-margin-start: 0px !important;
}

.tabbrowser-tabs:not([positionpinnedtabs="true"]) > arrowscrollbox > .scrollbutton-up {
  border-radius: 3px 0 0 3px !important;
}

.tabbrowser-tabs[positionpinnedtabs="true"] > arrowscrollbox > .scrollbutton-up {
  border-radius: 0 !important;
}

.tabbrowser-tabs > arrowscrollbox > .scrollbutton-down {
  border-radius: 3px 0 0 3px !important;
}

#tabbrowser-tabs:not([positionpinnedtabs="true"]) > arrowscrollbox > .scrollbutton-up,
#tabbrowser-tabs > arrowscrollbox > .scrollbutton-down {
  opacity: 1 !important;
  -moz-appearance: none !important;
  border: 1px solid hsla(210,8%,5%,.45) !important;
  background: -moz-linear-gradient(hsla(212,7%,57%,.35), hsla(212,7%,57%,.1)) padding-box !important;
  box-shadow: 0 1px 0 hsla(210,16%,76%,.15) inset, 0 0 0 1px hsla(210,16%,76%,.15) inset, 0 1px 0 hsla(210,16%,76%,.15) !important;
  margin-top: 4px !important;
  margin-bottom: 5px !important;
}

#tabbrowser-tabs[positionpinnedtabs="true"] > arrowscrollbox > .scrollbutton-up {
  opacity: 1 !important;
  -moz-appearance: none !important;
  border: 1px solid hsla(210,8%,5%,.45) !important;
  border-left: 1px solid transparent !important;
  background: -moz-linear-gradient(hsla(212,7%,57%,.35), hsla(212,7%,57%,.1)) padding-box !important;
  box-shadow: 0 1px 0 hsla(210,16%,76%,.15) inset, 0 0 0 1px hsla(210,16%,76%,.15) inset, 0 1px 0 hsla(210,16%,76%,.15) !important;
  margin-top: 4px !important;
  margin-bottom: 5px !important;
}

#tabbrowser-tabs > arrowscrollbox > .scrollbutton-up:not([disabled="true"]):active,
#tabbrowser-tabs > arrowscrollbox > .scrollbutton-down:not([disabled="true"]):active {
  border-color: hsla(210,8%,5%,.6) !important;
  background: -moz-linear-gradient(hsla(220,6%,10%,.3), hsla(212,7%,57%,.15) 65%, hsla(212,7%,57%,.3)) !important;
  box-shadow: 0 0 3px hsla(210,8%,5%,.25) inset, 0 1px 3px hsla(210,8%,5%,.25) inset, 0 1px 0 hsla(210,16%,76%,.15) !important;
}

#tabbrowser-tabs > arrowscrollbox > .scrollbutton-up:not([disabled="true"]) > .toolbarbutton-icon,
#tabbrowser-tabs > arrowscrollbox > .scrollbutton-down:not([disabled="true"]) > .toolbarbutton-icon {
  -moz-appearance: none !important;
  list-style-image: url("chrome://browser/skin/devtools/breadcrumbs-scrollbutton.png") !important;
  -moz-image-region: rect(0px 7px 16px 0px) !important;
  margin: 0 4px !important;
}

#tabbrowser-tabs > arrowscrollbox > .scrollbutton-up[disabled="true"] > .toolbarbutton-icon,
#tabbrowser-tabs > arrowscrollbox > .scrollbutton-down[disabled="true"] > .toolbarbutton-icon {
  -moz-appearance: none !important;
  list-style-image: url("chrome://browser/skin/devtools/breadcrumbs-scrollbutton.png") !important;
  -moz-image-region: rect(0px 7px 16px 0px) !important;
  margin: 0 4px !important;
  opacity: .1 !important;
}

#tabbrowser-tabs > arrowscrollbox > .scrollbutton-up:not([disabled]):active:hover > .toolbarbutton-icon,
#tabbrowser-tabs > arrowscrollbox > .scrollbutton-down:not([disabled]):active:hover > .toolbarbutton-icon {
  -moz-image-region: rect(0px 14px 16px 7px) !important;
  margin: 0 4px !important;
  padding-top: 1px !important;
}

#new-tab-button:not(:-moz-lwtheme) {
  padding-bottom: 2px !important;
}

.tabs-newtab-button:-moz-lwtheme {
  margin-top: 4px !important;
  margin-bottom: 5px !important;
}

.tabs-newtab-button {
  width: 26px !important;
  -moz-appearance: none!important;
  border: 0 solid transparent !important; 
  background:transparent !important;
  list-style-image: url(chrome://browser/skin/tabbrowser/newtab-inverted.png) !important;
}

.tabs-newtab-button:hover {
  width: 26px !important;
  -moz-image-region: auto !important;
  background-image: -moz-linear-gradient(rgba(255,255,255,0), rgba(255,255,255,.5)),
                    -moz-linear-gradient(transparent, rgba(0,0,0,.25) 30%),
                    -moz-linear-gradient(transparent, rgba(0,0,0,.25) 30%) !important;
  background-position: 1px -1px, 0 -1px, 100% -1px !important;
  background-size: calc(100% - 2px) 100%, 1px 100%, 1px 100% !important;
  background-repeat: no-repeat !important;
}

.tabs-newtab-button:not([disabled])[open]:hover {
  width: 26px !important;
  -moz-image-region: auto !important;
  background-image: -moz-linear-gradient(bottom, transparent, rgba(0,0,0,.15)),
                    -moz-linear-gradient(bottom, transparent, rgba(0,0,0,.15) 30%),
                    -moz-linear-gradient(bottom, transparent, rgba(0,0,0,.15) 30%) !important;
  background-position: left, left, right !important;
  background-size: auto, 1px 100%, 1px 100% !important;
  background-repeat: no-repeat !important;
}

#TabsToolbar .toolbarbutton-1 > .toolbarbutton-menu-dropmarker,
#TabsToolbar .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker {
  list-style-image: url("chrome://browser/skin/toolbarbutton-dropdown-arrow-inverted.png") !important;
}

#TabsToolbar:last-child :-moz-any(#back-button, #forward-button, #reload-button, #stop-button, #home-button, #print-button, #downloads-button, #history-button, #bookmarks-button, #bookmarks-menu-button, #new-tab-button, #new-window-button, #cut-button, #copy-button, #paste-button, #fullscreen-button, #zoom-out-button, #zoom-in-button, #sync-button, #feed-button, #alltabs-button, #tabview-button):not(:-moz-any(#alltabs-button,#tabview-button,#new-tab-button,#sync-button[status])) > .toolbarbutton-icon {
  list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important;
}
