css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('lenta.ru'){

a, a:hover { 
text-decoration: underline !important;
} 

.g-application {
padding: 8px 0 0 !important;
margin: 0 auto !important;
width: 1200px !important;
}

.span8 + .span4:nth-child(2), [class^="b-banner"], .b-sidebar-menu .mini, .b-footer, #root_footer, [class*=fuck], .b-sidebar-menu .origin ul li.delimiter {
display: none !important;
}

[class="span8"], .b-tags-list .g-layout, .b-topic-layout .g-layout, .b-topic-layout .g-layout .span12{
width: 1140px !important;
}

.b-layout .g-layout [class="span8"] {
margin-left: -80px  !important;
}

.b-layout .g-layout [class="span8"] [class="span8"] {
margin-left: 20px  !important;
}

[class="row b-top7-for-main"] .span4, [class="span8"] .span4 {
width: 560px !important;
}

.b-tabloid .b-tabloid__row {
margin: 0px !important;
}

.b-tabloid .b-tabloid__row .span4, [class="b-topic-longgrid b-topic-addition"] .span4 {
width: 365px !important;
}

.b-tabloid .b-tabloid__row{
width: 1160px  !important;
margin-left: -120px !important;

}

.b-header-main, .b-header-inner, .b-search-result  {
padding-right: 200px!important;
width: 1000px  !important;
}

.b-search-result  {
width: 100%  !important;
}

.b-tags-list, .b-topic-layout {
width: 1200px  !important;
}

.b-ears {
left:229px !important;
}

.b-up {
left: 0px!important;
}

.first-item .js-dh {
margin-right: 100%!important;
}

.b-sidebar-menu .origin {
background: none  !important; 
width: 100% !important;
left: 0 !important;
height: 28px !important;
}

.b-sidebar-menu .origin ul {
line-height: 25px;
}

.b-sidebar-menu .origin ul li {
float: left !important;
margin: 0 5px !important;
}

.b-sidebar-menu .origin .b-sidebar-menu__wrap {
background: rgb(37, 37, 37) !important;
margin: -30px auto !important;
width: 1200px !important;
position: relative !important;
height: 28px !important;
}

.b-sidebar-menu .origin form[class="search"] input {
float: right !important;
width: 100px !important;
}

.b-sidebar-menu .origin form[class="search"] i {
margin-top: 8px !important;
}

.b-feature_article {
background: none !important;
}

.b-yellow-box__wrap {
background: rgb(234, 233, 218)!important;
}

}
