css:
@-moz-document domain("dashboard.bloglines.com") {
.unread .entry-title.no-feedtitle {
    font-weight: bold !important;
}

.read .entry-row {
    background: none repeat scroll 0 0 #FFFFFF !important;
}

.entry-row .entry-title > span {
    font-family: Arial,sans-serif !important;
    font-size: 1.1em  !important;
    white-space: nowrap  !important;
}

.read .entry-row, .read .entry-row a {
    color: #444 !important;
}

.groupItems-title {
    color: #333 !important;
    font-family: Georgia,serif;
    font-size: 1.3em !important;
    height: 25px !important;
    line-height: 25px !important;
}

.entry-feedName span {
    font-family: Arial, sans-serif !important;
}

#smartreader-feeds-headerTop a {
    color: #000000 !important;
}

.read .entry-row, .unread .entry-row {
    height: 2.2em !important;
}

.nv-treeview-container li.nv-treeview-static {
    padding-left: 5px !important;
}

.nv-treeview-spacer {
    /*display: none !important; */
    width:5px !important;
}

.nv-sidebarContainer {
    width: 208px !important;
}

#smartreader-main {
    padding: 0 0 0 0 !important;
    margin: 0 0 0 0 !important;
}
}
