css:
@-moz-document domain("www.pivotaltracker.com") {
#controlPanel #project_name_row .projectName a {
  font-weight: bold;
  color: #333;
}

#header h1 {
  display: none;
}

#header .navigation_area {
  margin-top: 0;
}

#header {
  height: auto;
}

#project_name_row {
  float: left;
}

#velocity {
  float: left;
  margin-left: 1em;
}

#controls_search_velocity_row {
  float: auto;
}

#controlPanel #controls_search_velocity_row > div.right {
  margin-top: 0;
}

#controlPanel {
  height: auto;
  padding-bottom: 0.8em;
  margin-bottom: 0.4em;
}

#controlPanel #controls_search_velocity_row ul#buttonPanel #panelList > li,
#controlPanel #controls_search_velocity_row .dropdown_menu.main_menu {
  margin-top: 0.4em;
}

.panel {
  min-width: 420px !important;
}
  
.storyLabels {
  background: rgba(0, 0, 0, 0.1) !important;
  border-radius: 3px !important;
  border: #ccc !important;
  padding: 0 0.2em 0 0.2em !important;
}
    
.storyLabels:empty {
  display: none;
}
    
.flyoverIcon {
  display: none !important;
}

.storyPreviewText {
  padding-left: 50px !important;
}

.epicTable .storyPreviewText {
  padding-left: 0 !important
}

.epicTable .icons_container {
  width: 20px !important;
}

.epic_icon_container {
  display: none !important;
}

#layout {
  border-spacing: 0 !important;
  padding: 0 !important;
}

.storySelector {
  border-left: none;
  margin: auto;
}

.story_content .item .storyItem .description_section textarea,
.story_content .item .storyItem .description_section div.formatted_text_area,
.preview_flyover .body .section p,
.preview_flyover .tasks.readonly ol.task_list > li.task_item label,
.details .tasks .task_list > li.task_item label,
.comments .commentList .comment .text {
  font-family: /*[[commentfont]]*/;
}

}
