css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xkcd.com") {

#transcript {
	display: block !important;
	border: 1px solid #999;
	margin: 1em 60px;
	white-space: pre-wrap;
	font-variant: normal;
	padding: 1em;
	position: relative;
	border-radius: .5em;
}

#transcript:before {
	content: 'Transcript:';
	position: absolute;
	top: -1px;
	left: -1px;
	background-color: #999;
	color: #fff;
	padding: 0 .5em;
	border-radius: .5em 0 .5em 0;
}
#transcript:empty {
	border: none;
}
#transcript:empty:before {
	content: 'Transcript is empty :(';
	position: static;
}

}
