css:
@-moz-document domain("feedly.com") {
html, body, div, span, a, h1, h2, h3, .u100Entry, .title { font-family: "Segoe UI", HelveticaNeue-condensed, sans-serif !important; }
#greetings { display: none !important; }
#feedlyPart0 { background: rgb(65,65,65) !important; border-color: transparent; }
#floatingBar { background: rgb(85,85,85) !important; }
#floatingBar, #floatingTitleBar, #floatingTitleBar a { color: rgb(200,200,200) !important; display: none !important; }
.entryHolder { background: transparent !important; }
.u100Frame { background: rgba(0,0,0,0.05) !important; }
.u100Frame.selectedEntry { background: rgb(45, 45, 45) !important; font-family: "Segoe UI", HelveticaNeue-condensed, sans-serif; }

#feedlyTitleBar, #feedlyTitleBar a, .entryHeader a { color: rgb(200,200,200) !important; }
.entryBody, .entryBody div, .entryBody h1, .entryBody h2, .entryBody h3, .entryBody h4, .entryBody table, .entryBody tbody, .entryBody tr, .entryBody td, .entryBody b { color: rgb(220,220,220) !important; }
.entryBody { font-size: 14px !important; }
div.entryBody { max-width: 2000px; }
.action, .entryBody a, .entryBody a:visited { color: rgb(130,189,26) !important; }
.topWikiWidget { margin-top: -20px; }
.topWikiWidget div { display: none; }
div.bottomWikiWidget { display: none; }
#sourcesModule_part, #discoverModule_part { background: rgb(85,85,85) !important; margin-top: 31px; padding: 0 12px 16px 12px; width: 200px; border: 1px solid rgb(45, 45, 45); border-radius: 4px; }
.feedIndexTitle { color: rgb(160,160,160) !important; }
a.title.read, a.entryTitle { font-weight: normal !important; }
a.entryTitle.unread { color: rgb(130,189,26) !important; }
#sourcesModule_part h2, #discoverModule_part h2 { margin-top: 30px; }
.preferencesItemLabel, .preferencesPanel label { color: rgb(220,220,220) !important; }
.preferencesItemDescription { color: rgb(200,200,200) !important; }
#feedlyTabs .simpleUnreadCount { color: rgb(205,0,0) !important; opacity: 1 !important; font-size: 12px !important; }
.tab .feedIndexTitleHolder { font-size: 13px; }
div.nonEmpty { font-weight: normal !important; }
a.websiteCallForAction { display: none; }
}

#fullyLoaded { height: 800px; }
