css:
@-moz-document domain(www.boston.com) {
body {
    background-color: #3f5f9c !important;
}
#container {
    background-color: #FFFFFF !important;
}
#containerBorder {
 border-style: none solid solid !important;
 border-color: #666666 !important;
 border-width: medium 2px 2px !important;
}

.tt {
		padding-top: 2px !important;
}
h3 {
/* All h3s go bigger */
	font-size: 17.5px !important;
	line-height: 19px !important;
}
.img80headlineLeftBold a, .img178h3leftbold a, .img178h3left a, .img100headlineBoldLeft a {
/* the 178 views are here only for when the image is above the headline. */
	font-weight: bold !important;
	font-size: 14px !important
}
#Col1 .img80h3Left h3 {
/* But, we dont want to increase the size when its siting next to an 80x80 and in the first column */
       font-size: 12px !important;
       line-height: 14px !important ;
}
/* tightening up spacing on linklist by two pixels per line */
ul.linklist li {
	line-height: 13px !important;
	padding-bottom: 4px !important;
}

h2 {
	font-size: 24px !important;
	line-height: 26px !important;
	font-weight: bold !important;
}
h3.sectionHeader {
/* section headers all get a blue line underneath and a tiny increase in size */
	font-size: 15.5px !important;
        border-bottom: 3px solid #3F5F9C !important;
	padding-bottom: 2px !important;
}
h3.sectionHeader a:after {
/* but sections headers that are links have the decoration added in yellow - only works for FF and Safari */
    content: " Â»" !important;
    color: #FF9933 !important; 
}
h3.sectionHeader a:hover {
    color: #2851A2 !important;
}
ul.sectionLinks {
/* section links are limited to one line */
	margin-top: 0px !important;
	border-top: 0px !important;
	height: 13px !important;
	overflow: hidden !important;
	border-bottom-color:#666666 !important;
}
.dotted4px, .dotted4pxnopad {
/* getting rid of the 4px dotted as a divider between wells - we may stll have use for them elsewhere */
	height:0px !important;
	margin:6px 0pt 10px !important;
	width:auto !important;
}
.dotted1px {
/* replaced 1px with 9 px of space - but really this should be removed by the producer and replaced with a 10px spacer */
	border-top: 0px !important;
	margin:4px 0pt !important;
}
.leadOut a {
      color: #ffffff !important;
      background: none !important;
     padding-right: 7px !important;
     padding-top: 0px !important;
}
body#hp #jobs 
.sectionHeader a {
     display: inline !important;
}
body#hp #autos
   .sectionHeader a {
   display: inline !important;
}
.img100headlineBoldLeft img, .img100h3Left img, .img100h2Left img, .img80h3Left img, .img80headlineBoldLeft img, .img80headlineLeftBold img {
     float: right !important;
     margin-left: 5px !important;
     margin-right: 0px !important;
}

}
