css:
@-moz-document domain("watch.impress.co.jp") {
/* Remove Ads */
.billboard,
[id^='gpt'],
.mapcamera,
aside.right .social,
aside.right .special,
#main>.ad,
.OUTBRAIN
{display: none !important;}

/* Remove Elements */
#global-list,
#breadcrumb,
#category-nav,
footer,
#pc-floating-layer
{display: none !important;}

/* Layout */
#page-wrap {
  width: 800px;
  margin: 0 auto 100px;
}
#page {
  display: flex;
  flex-direction: column;
  width: 800px;
}
#extra {
  order: 2;
  width: auto;
}
#extra .list,
#extra .list ul.list-02 li.item,
#extra .list ul.list-02 li.item .body,
#extra .list ul.list-02 li.item div.body div.image + div.text p.title {
  width: auto;
}
#main {
  order: 1;
  width: 800px;
}
#main > *,
#extra > .ad.right > * {
  margin-top: 2em;
}
body.gnav-hide header {
  padding-bottom: 0;
}

/* Styling */
#global-nav>div>h1 {
  border: 0;
}
#global-nav>div {
  width: auto;
}
.hdg-03 {
  margin-top: 3em;
}
div.image-wrap.float.right {
  margin-left: 30px;
}
}
