css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("192.168.0.2") {
/*
=============================
Remote Potato - EPG Grid Styles
(C)2010 FatAttitude

Please be aware that the EPG Grid
style sheet specification can change 
at any time.

Proceed at your own risk!

=============================
*/


body
{
    font-family: Arial;
    color: #666666;
}
p.heading
{
}
/* DIV FOR THE MAIN MENU ON THE FRONT PAGE */
#mainmenu
{
}
p.recorderror
{
	color :#AA0000;
}
p.recordfailure
{
	color :#AA0000;
}
p.recordsuccess
{
	color :#00AA00;
}
/*  THIS CSS CLASS IS APPLIED TO LIST ITEMS WHEN A SHOWING OF A TV PROGRAMME WILL RECORD */
.upcomingwillrecord
{
	color: #00AA00;
}
/*  THIS CSS CLASS IS APPLIED TO LIST ITEMS WHEN A SHOWING OF A TV PROGRAMME WILL NOT RECORD */
.upcomingwontrecord
{
	color: #AA0000;
}
/* MANAGE SERIES LIST  */
ul.allschedulerequests
{
}
#searchresults
{
}
/* THE SEARCH SHOWS FORM */
form#searchshowbytitle, #searchshowbytitle input, #searchshowbytitle select
{
    font-size: 0.9em;
}
/* A RECORD DOT IMAGE THAT APPEARS WITHIN SEARCH RESULTS WHEN A SHOW OR SERIES WILL RECORD */
#searchresultsrecorddot
{
}
/* THE TEXT ON THE 'INFORMATION' PAGE */
#infotext
{
}

/* THE TABLE AND TEXT INPUT BOXES ON THE 'LOGIN' PAGE */
#logintable
{
}
#logintable input
{
}


/* MUSIC */

#musicrootmenu
{
	margin-left: 10px;
    font-size: 1.2em;
}
#musicrootmenu ul
{
    font-size: 1.1em;
}
#musiccollection
{
}
#musiccollectionindex
{
    font-size: 0.8em;
    margin-bottom: 30px;
}
#musiccollectionindex a
{
    color: #BBBBBB;
}
#musiccollectionitems
{
    font-size: 0.9em;
    color: #FFFFEE;
}
.musiccollectionitemssection
{
    margin-bottom: 16px;
}
#musiccollectionitems ul
{
    font-size: 0.9em;
    color: #FFFFEE;
}

#musicsongslist
{
font-size: 0.8em;
}
.musicsongslisttitlecolumn
{
width: 230px;
}


/* PICTURE LIBRARY */
#picturelibrarytable 
{
}
#picturelibrarytable  td
{
    width: 110px;
}


/* VIDEO LIBRARY */
#videolibrarytable 
{
}
#videolibrarytable  td
{
}



/*  THE NAVIGATION FORM, USUALLY DISPLAYED AT THE TOP */
#epggridform
{
    margin-top: 20px;
    margin-left: 74px;
    margin-bottom: 5px;
}
#epgjumptonow
{
    display: inline;
    font-size: 0.85em;
}
/* THE DIV CONTAINING THE ENTIRE EPG GRID */
.epggridcontainer
{
    width: 100% !important;
    overflow: hidden;
    border: none;
    font-size: 0.8em;
    margin: 0px;
}
/* THIS GOES AT THE START OF THE VERY FIRST ROW, AND USUALLY CONTAINS THE 'UP ARROW' TO NAVIGATE THE EPG CHANNELS */
.epggridtopleftspacer
{
    width: 70px;
    border: none;
    clear: both;
    float: left;
    overflow: hidden;   
    font-size: 1.0em;
    margin-right: 4px;
    margin-bottom: 4px;  /* The distance between the header row and the table */
    height: 20px;
    padding: 0px;
    text-align: center;
}
/* THE COLUMN HEADERS GO IN THE VERY FIRST ROW, THEY DISPLAY THE TIMES OF DAY */
.epggridcolumnheader
{
    display: block;
    border: solid 1px #000000 !important;
    color: Black;
    font-size: 1.0em;
    font-weight: bold;
    float: left; 
    overflow: hidden;   
    height: 20px;
    padding: 0px;
    margin: 0px;
}
.epggridcolumnheadertext
{
    margin: 0px;
    margin-top: 2px;
    margin-left: 5px;
}
.epggridcolumnheadertext img
{
    border: none;
}
/* COLUMN HEADERS ALTERNATE BETWEEN THESE TWO COLOURS */
.epggridcolumnheader.maincolor
{
    background-color: #000000 !important;
    color : #ffffff !important;
}
.epggridcolumnheader.altcolor
{
   background-color: #ffffff !important;
    color : #000000 !important;
}
/* NEXT COMES ROWS OF CHANNELS, EACH ONE IS ENCLOSED BY THIS DIV CLASS */
.epggridchannelrow
{
    border: none;
    clear: both;
    overflow: hidden; 
    margin: 0px;
    padding: 0px;
    /*width: 800px; */
    height: 62px;
}
/* EACH CHANNEL ROW BEGINS WITH ONE OF THESE ON THE LEFT */
.epggridchannelheading
{
    width: 70px;
    border: none;
    clear: both;
    float: left;
    overflow: hidden;   
    font-size: 0.85em;
    margin-right: 4px;
    height: 60px;
    padding: 0px;
}
/* CHANNEL LOGO/CALLSIGN:
    There is no css2 style to vertically centre an image, so a table hack is the best cross-browser way.
*/
table.epggridchannelheadingcontents
{
    width: 100%;
    height: 100%;
}
table.epggridchannelheadingcontents tr
{
    vertical-align: middle;
}
table.epggridchannelheadingcontents td.epggridchannelheadingchannellogo
{
    text-align: right;
}
table.epggridchannelheadingcontents td.epggridchannelheadingchannelnumber
{
    color: #CCCCFF;
    text-align: left;
}
/* EACH ROW IS FILLED WITH CELLS, EACH CELL REPRESENTS A TV SHOW
   NB: THE WIDTH OF CELLS IS OVERRIDEN BY THE APPLICATION IN ORDER TO REPRESENT THEIR DURATION IN THE GRID
 */
.epggridcell
{
    background-image: none;
    background-position: top left;
    background-repeat: no-repeat;
    border: solid 1px white;
    font-size: 0.9em;
    float: left; 
    overflow: hidden;   
    height: 58px;
    padding: 0px;
    margin: 0px;
}
.epggridcellprogrammetypenews
{
    background-color: #ffffff !important;
}
.epggridcellprogrammetypedocumentary
{
    background-color: #fff !important;
}
.epggridcellprogrammetypesport
{
    background-color: #fff !important;
}
.epggridcellprogrammetypekids
{
    background-color: #fff !important;
}
.epggridcellprogrammetypemovie
{
    background-color: #fff !important;
}
/*  THIS SITS INSIDE AN EPG CELL, CONTAINING THE SHOW NAME AND START TIME */
.epggridcelltext
{
    margin-top: 8px;
    margin-left: 3px;
    margin-right: 3px;
    margin-bottom: 1px;
}
/* THE RED DOTS DESIGNATING WHEN A SHOW/SERIES IS RECORDING */
#epggridrecorddot
{
    background-position: top left;
    background-repeat: no-repeat;
    float:right;
    margin-left: 10px;
    margin-right: 5px;
    margin-top: 8px;
    width: 16px;
    height: 16px;
}
.recorddot_onetime
{
    background-image: none;
}
.recorddot_series
{
    background-image: none;
}
#epggridrecommendedstar
{
    background-image: none;
    background-position: top left;
    background-repeat: no-repeat;
    float:right;
    margin-left: 10px;
    margin-right: 5px;
    margin-top: 8px;
    width: 16px;
    height: 16px;
}
/* THIS STYLE IS APPLIED FOR A SINGLE CELL, USUALLY FILLING THE WHOLE ROW, DISPLAYED WHEN NO LISTINGS ARE FOUND FOR A CHANNEL */
.epggridcell.nolistings
{
    border: solid 0.1em gray;
    font-size: 1.3em;
    color: #CCCCCC;
    height: 40px;
    padding-top: 18px;
}
/* THIS TYPE OF CELL IS INSERTED WHEN THERE IS SPARE TIME IN THE SCHEDULE BEFORE A SHOW.  IT'S IMPORTANT THAT IT STILL HAS A 1PX BORDER TO AVOID MESSING UP THE ALIGNMENT OF THE GRID */
.epggridfiller
{
    border: solid 1px #101077;
}
/* THIS GOES AT THE START OF THE FINAL ROW, AND USUALLY CONTAINS THE 'DOWN ARROW' TO NAVIGATE THE EPG CHANNELS */
.epggridbottomleftspacer
{
    width: 70px;
    border: none;
    clear: both;
    float: left;
    overflow: hidden;   
    font-size: 1.0em;
    height: 15px;
    padding: 0px;
    text-align: center;
    margin-right: 4px;
}
#epgchanneldownarrow
{
    width: 14px;
    height: 14px;
    border: none;
}
#epgchanneluparrow
{
    width: 14px;
    height: 14px;
    border: none;
    margin-top: 5px;
}
/* THE LINKS AT THE VERY BOTTOM OF THE EPG (IN THIS THEME) */
#epggridskiplinks
{
    margin-left: 74px;
    font-size: 0.8em;
}
#epggridhint
{
    margin-left: 74px;
    font-size: 0.8em;
}
body ul.epgprogrammes li {
    margin : 20px 20px 20px 0px;
    background : #eeeeee;
    display : block;
    height : 30px;
}

body ul.epgprogrammes {
    list-style : none;
    margin-left : -10px;
}


body ul.epgprogrammes li a {
    padding : 5px 5px 5px 30px;  
    border : 1px solid #666666;
    display : block;
    
}

body ul.epgprogrammes li.upcomingwillrecord a {
    color : #822f2f !important;
}


body ul.epgprogrammes li img {
    display : block;
    margin-top : -25px;
}

a {
	text-decoration: none !important;
	  color : #666666 !important;
}

}
